UPDATE locales_npc_text SET entry = 13019, Text0_0_loc2 = 'Bienvenue à notre campement, $r.$B$BC\'est pas un château, mais c\'est notre foyer du moment.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13019;
UPDATE locales_npc_text SET entry = 13022, Text0_0_loc2 = 'Que puis-je faire pour vous aider, mon enfant ?', Text0_1_loc2 = 'Que puis-je faire pour vous aider, mon enfant ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13022;
UPDATE locales_npc_text SET entry = 13023, Text0_0_loc2 = null, Text0_1_loc2 = 'Monsieur Toumou et moi on est partis se promener, mais ensuite on s\'est perdus, et j\'avais vraiment très peur à cause des loups, et ils couraient après nous, mais ensuite j\'ai vu la tour et puis monsieur Toumou et moi on s\'est cachés dedans, et les loups ils sont partis mais c\'était horrible, et maintenant on sait plus comment on peut rentrer chez nous.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13023;
