UPDATE locales_npc_text SET entry = 13025, Text0_0_loc2 = 'Soyez $gle bienvenu:la bienvenue; au temple du Repos du ver, $r. Veuillez nous pardonner cette agitation qui règne dehors. Suite aux récentes actions de Malygos, nous nous retrouvons assiégés. Vous pouvez toujours vous reposer ici sous notre protection, en cas de besoin.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13025;
