UPDATE locales_npc_text SET entry = 13030, Text0_0_loc2 = 'Salut, $c !$B$BJ\'espère que le travail ne vous fait pas peur. On aurait bien besoin d\'un coup de main.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13030;
UPDATE locales_npc_text SET entry = 13031, Text0_0_loc2 = null, Text0_1_loc2 = 'Le vent et les arbres parlent. Leurs murmures évoquent des dangers invisibles. La terre semble en bonne santé, mais nos yeux ne voient pas tout.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13031;
UPDATE locales_npc_text SET entry = 13032, Text0_0_loc2 = null, Text0_1_loc2 = '<Thayreen a le regard vide de toute expression. Purger Vordrassil de sa corruption lui a laissé des traces.>', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13032;
UPDATE locales_npc_text SET entry = 13033, Text0_0_loc2 = 'Des peaux pour la Horde, mec ! Rien que le mieux du mieux pour nos hommes !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13033;
UPDATE locales_npc_text SET entry = 13034, Text0_0_loc2 = 'Besoin de ravitaillement pour la route ? Un petit rafraîchissement à siroter en regardant les combats, peut-être ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13034;
UPDATE locales_npc_text SET entry = 13035, Text0_0_loc2 = 'Nos ennemis approchent de toute part, et les anciens parlent d\'abandonner ce poste. Je dis que nous devrions rester et combattre comme les guerriers que nous sommes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13035;
UPDATE locales_npc_text SET entry = 13042, Text0_0_loc2 = 'Je l\'ai appelée l\'Esprit de Gnomeregan. L\'est-y pas belle ? Sans votre aide, elle n\'aurait jamais pu être remise en état.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13042;
UPDATE locales_npc_text SET entry = 13043, Text0_0_loc2 = 'Bienvenue au séjour d\'Argent, $c.$B$BFaites comme chez vous, mais ne vous installez pas trop confortablement. Nous ne sommes pas encore sortis de l\'auberge - et loin de là.$B$BSi nous voulons sortir d\'ici en un seul morceau, il va falloir que tout le monde participe... y compris vous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13043;
