UPDATE locales_npc_text SET entry = 13046, Text0_0_loc2 = 'La terre est souillée par la présence du Fléau d\'un côté et les rituels des Drakkari de l\'autre.$B$BNous sommes là, entre les deux, bien déterminés.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13046;
UPDATE locales_npc_text SET entry = 13047, Text0_0_loc2 = 'Louée soit la Lumière de vous avoir $genvoyé:envoyée; !', Text0_1_loc2 = 'Louée soit la Lumière de vous avoir $genvoyé:envoyée; !', Text1_0_loc2 = 'Bonjour, $c. Nous sommes en pleine bataille.', Text1_1_loc2 = 'Bonjour, $c. Nous sommes en pleine bataille.', Text2_0_loc2 = 'Que faites-vous dans le coin ?', Text2_1_loc2 = 'Que faites-vous dans le coin ?', Text3_0_loc2 = 'Vous êtes $N, n\'est-ce pas ? J\'avais entendu dire que vous étiez $gmort:morte;.', Text3_1_loc2 = 'Vous êtes $N, n\'est-ce pas ? J\'avais entendu dire que vous étiez $gmort:morte;.', Text4_0_loc2 = 'Faites vite… Le combat est acharné, j\'ai besoin de rester concentré.', Text4_1_loc2 = 'Faites vite… Le combat est acharné, j\'ai besoin de rester concentrée.', Text5_0_loc2 = 'Z\'êtes une nouvelle recrue ? C\'est pas une armure réglementaire que vous avez là.', Text5_1_loc2 = 'Z\'êtes une nouvelle recrue ? C\'est pas une armure réglementaire que vous avez là.', Text6_0_loc2 = 'Ah, bien, des renforts !', Text6_1_loc2 = 'Ah, bien, des renforts !', Text7_0_loc2 = 'Vous avez un message pour moi ?', Text7_1_loc2 = 'Vous avez un message pour moi ?' WHERE entry = 13047;
UPDATE locales_npc_text SET entry = 13048, Text0_0_loc2 = 'Ça chauffe un poil plus que ce à quoi vous vous attendiez, hein, $r ?$B$BOn peut pas dire que ça me surprenne. C\'est ce que disent la plupart des gens qui arrivent jusqu\'ici.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13048;
