UPDATE locales_npc_text SET entry = 13069, Text0_0_loc2 = 'Je ne tiens plus…$B$BIls vous tuent, puis ils vous mangent ! Et encore, ça c\'est si vous avez de la chance !', Text0_1_loc2 = 'Je ne tiens plus…$B$BIls vous tuent, puis ils vous mangent ! Et encore, ça c\'est si vous avez de la chance !', Text1_0_loc2 = 'Faut m\'aider à partir d\'ici, $r !$B$BJe veux pas devenir comme eux !', Text1_1_loc2 = 'Faut m\'aider à partir d\'ici, $r !$B$BJe veux pas devenir comme eux !', Text2_0_loc2 = 'Depuis que je suis ici, je n\'arrive plus à manger ou dormir.$B$BLa Croisade, c\'est vraiment pas pour moi…', Text2_1_loc2 = 'Depuis que je suis ici, je n\'arrive plus à manger ou dormir.$B$BLa Croisade, c\'est vraiment pas pour moi…', Text3_0_loc2 = '<pleurniche> Mon oncle servait dans l\'Aube d\'argent.$B$BMais comment est-ce qu\'il faisait pour affronter des horreurs pareilles ?', Text3_1_loc2 = '<pleurniche> Mon oncle servait dans l\'Aube d\'argent.$B$BMais comment est-ce qu\'il faisait pour affronter des horreurs pareilles ?', Text4_0_loc2 = 'Chaque fois que je ferme les yeux, je vois le Fléau m\'encercler.$B$BIl faut que je me tire d\'ici…', Text4_1_loc2 = 'Chaque fois que je ferme les yeux, je vois le Fléau m\'encercler.$B$BIl faut que je me tire d\'ici…', Text5_0_loc2 = 'Je vais mourir ici… Je le sais.', Text5_1_loc2 = 'Je vais mourir ici… Je le sais.', Text6_0_loc2 = 'Nous sommes condamnéééés !', Text6_1_loc2 = 'Nous sommes condamnéééés !', Text7_0_loc2 = 'Dégagez d\'ici tant que vous le pouvez, $c !$B$BOn ne gagnera jamais…', Text7_1_loc2 = 'Dégagez d\'ici tant que vous le pouvez, $c !$B$BOn ne gagnera jamais…' WHERE entry = 13069;
