UPDATE locales_npc_text SET entry = 13100, Text0_0_loc2 = 'Salut ! Je suis surpris de croiser d\'autres aventuriers ici. L\'endroit est vraiment dangereux ! Si j\'avais su que vous veniez, j\'vous aurais suivi au lieu de prendre cette machine volante gnome infernale. Mais bon, maintenant que vous êtes là, vous allez pouvoir m\'aider à atteindre le Tribunal ! Si mes théories sur cet endroit sont correctes, il doit se trouver ici des informations sur la création même de toute la race naine !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13100;
UPDATE locales_npc_text SET entry = 13101, Text0_0_loc2 = 'Contemplez, camarades ! Quelle scène merveilleuse ! Si seulement mes chers frères pouvaient être ici, avec moi, en ce moment précis. Une telle découverte, ils n\'en croiraient pas leurs yeux ! Le Tribunal est l\'ensemble des bibliothécaires qui conservent les disques des titans, qui sont entreposés ici, dans cette salle. Regardez ! On aperçoit les disques sous le sol ! Il faut faire vite, la plus grande découverte de l\'histoire naine nous attend !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13101;
UPDATE locales_npc_text SET entry = 13117, Text0_0_loc2 = 'Voir ces cinglés de trolls drakkari abattre une nécropolis du Fléau droit dans notre jardin, c\'est vraiment la dernière chose qu\'il nous fallait.$B$BMais comment est-ce qu\'ils ont fait leur affaire ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13117;
