UPDATE locales_npc_text SET entry = 13119, Text0_0_loc2 = 'Vite, $c !$B$BVersez les ingrédients directement dans la marmite. Il faut absolument faire vite !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13119;
