UPDATE locales_npc_text SET entry = 13125, Text0_0_loc2 = 'Vous devez être l\'un des courageux soldats qui ont fait face aux créatures du Fléau contrôlées par Mal\'Ganis. Bravo.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13125;
UPDATE locales_npc_text SET entry = 13126, Text0_0_loc2 = 'En effet. Mal\'Ganis commande les troupes du Fléau depuis la place du Croisé, mais les portes qui y mènent sont fermées. Ce qu\'il ignore, c\'est qu\'un petit groupe peut quand même atteindre la place en passant par l\'hôtel de ville. Vous joindrez-vous à moi pour cette attaque ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13126;
