UPDATE locales_npc_text SET entry = 13135, Text0_0_loc2 = 'Faites-moi sortir d\'ici, $r !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13135;
UPDATE locales_npc_text SET entry = 13137, Text0_0_loc2 = '$GAmi dégoûtant:Amie dégoûtante; des grosses-langues, vous êtes pas $gbienvenu:bienvenue; ici. Quittez les terres frénécœur, ou j\'appelle plein des choses magiques pour vous tuer !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13137;
UPDATE locales_npc_text SET entry = 13138, Text0_0_loc2 = 'Frénécœur sera jamais ami des grosses-langues. Revenir ici c\'était bête, créature-traître !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13138;
UPDATE locales_npc_text SET entry = 13139, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous serez mon salut.$B$BVous pouvez aussi, cependant, être ma perte.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13139;
UPDATE locales_npc_text SET entry = 13147, Text0_0_loc2 = 'Avez-vous déjà vu des drakes comme ceux qu\'on trouve ici ? Ils font un gibier parfait.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13147;
UPDATE locales_npc_text SET entry = 13155, Text0_0_loc2 = 'Désolé, nos bières sont réservées aux membres du club de la Bière du mois.', Text0_1_loc2 = 'Désolée, nos bières sont réservées aux membres du club de la Bière du mois.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13155;
UPDATE locales_npc_text SET entry = 13156, Text0_0_loc2 = 'Cet avant-poste est sécurisé, $r.$B$BNous avons chassé l\'ennemi de la zone pour quelque temps.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13156;
UPDATE locales_npc_text SET entry = 13157, Text0_0_loc2 = null, Text0_1_loc2 = 'Merci de m\'avoir libérée, $r.$B$BJe me sens encore un peu vaseuse, à cause du venin. Je crois que je vais rester un peu ici en attendant de regagner mes forces.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13157;
UPDATE locales_npc_text SET entry = 13172, Text0_0_loc2 = null, Text0_1_loc2 = 'C\'est par le pouvoir de tout ce qui est impie que nous commandons à la mort même !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13172;
UPDATE locales_npc_text SET entry = 13173, Text0_0_loc2 = 'Le sang est la vie… Et tout aussi facilement que nous contrôlons l\'afflux et le reflux du sang contrôlons-nous la vie de nos ennemis.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13173;
UPDATE locales_npc_text SET entry = 13174, Text0_0_loc2 = 'Nous sommes ce que charrie le vent gelé. Le frisson glacé du désespoir… La froide étreinte de la mort…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13174;
UPDATE locales_npc_text SET entry = 13176, Text0_0_loc2 = 'Voir les Drakkari se détruire eux-mêmes ainsi nous attriste tous.$B$BLe pouvoir qu\'ils tirent de l\'assassinat des dieux naturels de la terre n\'est qu\'éphémère.$B$BCe qu\'ils gagnent à court terme leur sera éternellement perdu.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13176;
UPDATE locales_npc_text SET entry = 13177, Text0_0_loc2 = 'Reprenez vite vos esprits, nous devons avancer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13177;
UPDATE locales_npc_text SET entry = 13179, Text0_0_loc2 = 'La situation ne s\'arrange pas. Stratholme brûle. Pour atteindre Mal\'Ganis, nous devons braver le Fléau en même temps que les flammes. Préparez-vous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13179;
UPDATE locales_npc_text SET entry = 13180, Text0_0_loc2 = null, Text0_1_loc2 = 'Zim\'Torga réussit à nous offrir une bonne protection, mais la présence du Fléau et les actes des Drakkari l\'ont très affaiblie.$B$BC\'est à Ahunae et moi qu\'il revient de gérer le reste de nos mesures de défense.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13180;
UPDATE locales_npc_text SET entry = 13234, Text0_0_loc2 = 'Ceux qui nous attaquent, c\'est ma hache qui va s\'en occuper.$B$BEt puis même si c\'est pas ma hache, chuis sûr que z\'allez finir le boulot.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13234;
UPDATE locales_npc_text SET entry = 13237, Text0_0_loc2 = 'C\'est un seigneur-mage, Urom l\'entourloupeur, qui protège le troisième anneau. Il semblera seul et sans défense, mais ne vous laissez pas abuser par les apparences ! Urom est un puissant invocateur qui commande à toute une ménagerie de créatures fantomatiques. Vous le trouverez plus haut.

Je peux vous donner le pouvoir de faire appel à un drake du Vol rouge. Allez voir Eternos ou Verdisa si vous préférez en appeler aux pouvoirs du Vol de bronze ou du Vol vert.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13237;
UPDATE locales_npc_text SET entry = 13238, Text0_0_loc2 = 'Votre plus grand défi vous attend. Le gardien-tellurique Eregos est un dragon bleu incroyablement puissant. Il survole l\'anneau supérieur.
Vos drakes peuvent maintenant combattre à pleine puissance. Utilisez leurs pouvoirs pour vaincre Eregos !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13238;
