UPDATE locales_npc_text SET entry = 13241, Text0_0_loc2 = null, Text0_1_loc2 = 'Le Vol vert a une dette envers vous.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13241;
UPDATE locales_npc_text SET entry = 13254, Text0_0_loc2 = 'Les drakes rubis excellent dans l\'art de réduire les dégâts reçus et de protéger leurs alliés.
Grâce à leur Colère incendiaire, ils peuvent souffler un trait de feu liquide qui saute de cible en cible et inflige des dégâts augmentés à chaque saut.
Quand un drake rubis est victime d\'une attaque, il gagne une Charge d\'évasion. Les charges peuvent être utilisées pour lancer une série de manœuvres d\'évitement qui permettent au drake d\'esquiver les attaques et sorts pendant quelque temps.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13254;
UPDATE locales_npc_text SET entry = 13255, Text0_0_loc2 = 'Un drake rubis à pleine puissance peut lancer Martyr, qui protège ses cibles amies en redirigeant toute magie hostile vers lui pendant un court instant. Le drake rubis peut continuer ses Manœuvres d\'évasion pendant son Martyr.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13255;
UPDATE locales_npc_text SET entry = 13256, Text0_0_loc2 = 'Les drakes ambre excellent dans l\'art d\'infliger des dégâts en manipulant le temps.
Grâce à leur Javelot de choc, ils peuvent perturber l\'écoulement du temps autour de leur cible et provoquer des dégâts instantanés. La puissance de cette technique est très fortement amplifiée si la cible porte des Charges de choc.
Lorsqu\'il est menacé, un drake ambre peut interrompre brièvement l\'écoulement du temps pour ses adversaires. Cette suspension place une grande quantité d\'Énergie de choc sur les cibles touchées, sous la forme de Charges de choc.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13256;
UPDATE locales_npc_text SET entry = 13257, Text0_0_loc2 = 'Un drake ambre à pleine puissance peut créer une Faille temporelle, qui génère une bulle de temps compressé autour d\'un adversaire. Les sorts qui traversent cette bulle infligent des dégâts doubles et peuvent poser des Charges de choc sur la cible.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13257;
UPDATE locales_npc_text SET entry = 13258, Text0_0_loc2 = null, Text0_1_loc2 = 'Les drakes émeraude excellent dans l\'art de se soigner tout en empoisonnant leurs ennemis.

Grâce à leur Poison sangsue, il peuvent voler l\'énergie vitale d\'un adversaire. Une application répétée amplifiera l\'effet du poison.

Toucher le cauchemar permet aux drakes émeraudes de puiser dans le Rêve pour convertir une partie de leur énergie vitale en une attaque dévastatrice qui affaiblit la cible et réduit brièvement les dégâts qu\'elle inflige.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13258;
