UPDATE locales_npc_text SET entry = 13262, Text0_0_loc2 = 'Qu\'est-ce que Corvus peut faire pour vous ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13262;
UPDATE locales_npc_text SET entry = 13265, Text0_0_loc2 = 'C\'que vous avez vu aujourd\'hui, $N, faut bien y réfléchir.$B$BLe futur, il est déjà écrit. La seule chose qui vous reste à décider, c\'est quel rôle que vous voulez y jouer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13265;
UPDATE locales_npc_text SET entry = 13267, Text0_0_loc2 = 'Les anneaux supérieurs sont protégés par les plus puissants agents de Malygos. Seuls, vous ne pouvez espérer les vaincre. Nous pouvons vous donner le pouvoir de faire appel à des drakes de nos propres vols. Montés sur le dos d\'un drake, vous pourrez voler dans l\'Oculus et déchaîner la puissante magie des dragons.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13267;
