UPDATE locales_npc_text SET entry = 13270, Text0_0_loc2 = 'Personne ne connaît mieux le Fléau que nous autres chevaliers de la mort.$B$BCroyez-moi, $r, il n\'y a rien de plus efficace que de combattre le feu par le feu.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13270;
