UPDATE locales_npc_text SET entry = 13282, Text0_0_loc2 = 'Même si mes compatriotes aimeraient vous faire jouer un rôle actif dans ce qui se passe à Zul\'Drak, nous, les Zandalari, sommes surtout ici pour être témoins de tous ces événements et chroniquer la fin d\'un empire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13282;
