UPDATE locales_npc_text SET entry = 13290, Text0_0_loc2 = null, Text0_1_loc2 = '<Rose-sang vous examine avec curiosité.>$B$BJe ne peux pas m\'empêcher de me demander ce qui vous rend digne d\'une telle attention.$B$BSans vouloir vous vexer…', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13290;
