UPDATE locales_npc_text SET entry = 13300, Text0_0_loc2 = 'Ooohh… R\'gadez-moi voère la zoulie ch\'tite goule.$B$BAllez, ma goule, saute-z-y voère un peu ! Saute !$B$BHahaha… Gentille ch\'tite goule.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13300;
