UPDATE locales_npc_text SET entry = 13303, Text0_0_loc2 = null, Text0_1_loc2 = 'Le Fléau est une souillure sur la texture immaculée de ces terres. Par la puissance de mes créateurs, je vais broyer de mon talon jusqu\'au dernier des plus petits os de la dernière de ses créatures.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13303;
UPDATE locales_npc_text SET entry = 13304, Text0_0_loc2 = null, Text0_1_loc2 = 'Les titans n\'ont pas créé la vie en une journée. Ce fut un processus long et progressif, et qui demanda beaucoup d\'expérimentation et de répétition.$B$BC\'est pour cette raison que des endroits hautement protégés comme le bassin de Sholazar existent.$B$BPour garantir la sécurité de leurs sites expérimentaux, les titans créèrent un mécanisme de défense. Mais la puissance de destruction de ce mécanisme est sans égale, et je ne vous en dirai pas plus tant que vous n\'aurez pas juré fidélité à ma cause.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13304;
UPDATE locales_npc_text SET entry = 13305, Text0_0_loc2 = null, Text0_1_loc2 = 'Révéler ces secrets à $gun mortel tel:une mortelle telle; que vous confine au sacrilège, mais je n\'ai pas le choix.$B$BDepuis que Freya, fille des titans, m\'a laissée ici, je n\'ai plus qu\'une mission, et je ne dois pas échouer.$B$BIl existe un portail qui relie cet endroit à un autre, très similaire. C\'est là-bas que l\'arme que nous cherchons se trouve.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13305;
UPDATE locales_npc_text SET entry = 13306, Text0_0_loc2 = 'Cet avant-poste est non-boulonneur. Toute personne qui utiliserait des boulons pour construire un engin mécanique deviendra l\'objet de moqueries incessantes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13306;
UPDATE locales_npc_text SET entry = 13307, Text0_0_loc2 = 'On dirait que vous et moi, on a eu la même idée. Vous cherchez des fruits, c\'est ça ?', Text0_1_loc2 = 'On dirait que vous et moi, on a eu la même idée. Vous cherchez des fruits, c\'est ça ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13307;
UPDATE locales_npc_text SET entry = 13308, Text0_0_loc2 = 'Loken ?! Ouh, c\'est carrément embêtant… On a p\'tet neutralisé les nains de fer, mais j\'suis prêt à parier qu\'il y a une autre machine quelque part qui déballe du vrykul de fer à toute blinde !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13308;
UPDATE locales_npc_text SET entry = 13310, Text0_0_loc2 = 'Il y a deux tours des zeppelins devant Orgrimmar. La tour est dessert les clairières de Tirisfal et la vallée de Strangleronce, tandis que la tour ouest dessert le Norfendre.', Text0_1_loc2 = 'Il y a deux tours des zeppelins devant Orgrimmar. La tour est dessert les clairières de Tirisfal et la vallée de Strangleronce, tandis que la tour ouest dessert le Norfendre.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13310;
