UPDATE locales_npc_text SET entry = 13321, Text0_0_loc2 = null, Text0_1_loc2 = 'Depuis ce quai, la Bravoure fait l\'aller-retour entre Hurlevent et Auberdine.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13321;
UPDATE locales_npc_text SET entry = 13322, Text0_0_loc2 = 'Ma pauvre machine volante. Elle n\'a pas survécu à l\'accident et, si je ne trouve pas de pièces de rechange, je ne réussirai jamais à la refaire marcher !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13322;
UPDATE locales_npc_text SET entry = 13324, Text0_0_loc2 = 'Bientôt, $N !$B$BBientôt z\'aurez le goût d\'la vraie puissance. Bientôt, z\'aurez la récompense pour vos efforts et votre service.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13324;
