UPDATE locales_npc_text SET entry = 13328, Text0_0_loc2 = 'Bienvenue. Puis-je vous proposer de quoi vous reposer ou vous sustenter ?', Text0_1_loc2 = 'Bienvenue. Puis-je vous proposer de quoi vous reposer ou vous sustenter ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13328;
UPDATE locales_npc_text SET entry = 13331, Text0_0_loc2 = null, Text0_1_loc2 = 'Aimez-vous le nouveau foyer que j\'ai taillé pour mes prétendus adorateurs ?$B$BLivrée aux affres de la mort, je les ai tous maudits et les ai traînés avec moi vers cet abîme de ma création.$B$BJe pense que je vais me régaler de leur tourment éternel… Mais ce que je veux vraiment, c\'est exercer ma vengeance sur mon ancien prophète !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13331;
UPDATE locales_npc_text SET entry = 13342, Text0_0_loc2 = 'Vous venez acheter ce que vend Hargus, hein ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13342;
UPDATE locales_npc_text SET entry = 13348, Text0_0_loc2 = 'Encore un suppliant qui veut la faveur du roi-liche ?$B$BLe don des ténèbres, y a que les dignes qui le reçoivent…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13348;
UPDATE locales_npc_text SET entry = 13349, Text0_0_loc2 = 'Bienvenue au Bureau d\'enregistrement des guildes de Dalaran. Comment puis-vous vous être utile ?', Text0_1_loc2 = 'Bienvenue au Bureau d\'enregistrement des guildes de Dalaran. Comment puis-vous vous être utile ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13349;
UPDATE locales_npc_text SET entry = 13352, Text0_0_loc2 = 'J\'espère que l\'archimage apprécie tout ce que j\'ai traversé pour tenir la part d\'Hemet du marché.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13352;
UPDATE locales_npc_text SET entry = 13353, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous devriez suivre Arthas de près maintenant !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13353;
UPDATE locales_npc_text SET entry = 13355, Text0_0_loc2 = 'Nous allons tout leur prendre, chevalier de la mort.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13355;
UPDATE locales_npc_text SET entry = 13359, Text0_0_loc2 = 'Frénécœur va prendre cette terre pour eux. Les grosses-langues ils vont partir ou ils vont mourir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13359;
UPDATE locales_npc_text SET entry = 13360, Text0_0_loc2 = 'Frénécœur, des fois, c\'est brutal, mais c\'est un bon peuple. Si vous restez longtemps avec nous, vous allez voir. Nous on est toujours amis avec les gens qui sont forts et intelligents.$B$BC\'est juste qu\'on aime pas les idiots.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13360;
UPDATE locales_npc_text SET entry = 13361, Text0_0_loc2 = 'C\'est la pierre à Pluie toute-puissante. On la protège, et on lui apporte des trucs qui brillent et elle, elle s\'occupe de nous.$B$BPeut-être vous avez encore des trucs qui brillent pour la pierre ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13361;
UPDATE locales_npc_text SET entry = 13362, Text0_0_loc2 = 'J\'ai pas la grande taille, mais j\'ai la grande magie. Je m\'occupe bien de la pierre à Brume toute-puissante… En tout cas le plus souvent.$B$BIls sont idiots les hommes-chiots.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13362;
UPDATE locales_npc_text SET entry = 13363, Text0_0_loc2 = 'Les hommes chiots, ça fait des jours qu\'ils nous attaquent maintenant. Mais la Pierre à foudre toute-puissante elle est bien protégée.$B$BSoo-palonion il est venu nous aider comme il avait fait souvent… Les hommes-chiots ils ont aucune chance.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13363;
UPDATE locales_npc_text SET entry = 13364, Text0_0_loc2 = 'Kartak vient chercher la viande de grosse-langue. Frénécœur, il fait attention qu\'il y en a toujours beaucoup.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13364;
UPDATE locales_npc_text SET entry = 13365, Text0_0_loc2 = 'Bonjour, peau-sèche.', Text0_1_loc2 = null, Text1_0_loc2 = 'Qu\'est-ce que vous regardez ?', Text1_1_loc2 = null, Text2_0_loc2 = 'Est-ce qu\'il y a une raison pour qu\'on reste ici ?', Text2_1_loc2 = null, Text3_0_loc2 = 'Je suppose que vous cherchez quelque chose.', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13365;
UPDATE locales_npc_text SET entry = 13366, Text0_0_loc2 = 'Tout va bien ?', Text0_1_loc2 = null, Text1_0_loc2 = 'Oui ?', Text1_1_loc2 = null, Text2_0_loc2 = 'C\'est bien d\'avoir $gun bon:une bonne; partenaire pour la chasse.', Text2_1_loc2 = null, Text3_0_loc2 = 'Quelque chose va pas ?', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13366;
UPDATE locales_npc_text SET entry = 13367, Text0_0_loc2 = 'Salut ! Ça va ? Nous on est gentils… Oui, vraiment gentils. Salut !', Text0_1_loc2 = null, Text1_0_loc2 = 'Vous avez déjà trébuché sur vos pieds à vous ? Nous on l\'a fait. Ça fait un peu mal. C\'est pas très marrant, en fait.', Text1_1_loc2 = null, Text2_0_loc2 = 'Bonjour ! Vous êtes vraiment $gfort:forte; et bien et sympa et tout. Moi je vous aime bien. On est copains, d\'accord ?', Text2_1_loc2 = null, Text3_0_loc2 = 'Vous voulez qu\'on aide pour quelque chose ? Pour quoi on peut aider ? Nous on est bien pour aider pour des trucs.', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13367;
UPDATE locales_npc_text SET entry = 13368, Text0_0_loc2 = null, Text0_1_loc2 = 'J\'ai une dette éternelle envers vous, $N.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13368;
UPDATE locales_npc_text SET entry = 13375, Text0_0_loc2 = 'Belle journée pour voler, n\'est-ce pas ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13375;
UPDATE locales_npc_text SET entry = 13376, Text0_0_loc2 = 'Je sais reconnaître un vrai as quand j\'en ai un sous les yeux. Merci encore de m\'avoir sauvé la vie !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13376;
UPDATE locales_npc_text SET entry = 13382, Text0_0_loc2 = 'Regardez ce que vous avez fait, chevalier de la mort ! Vous avez apporté la gloire au Fléau !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13382;
UPDATE locales_npc_text SET entry = 13390, Text0_0_loc2 = 'Laissez-moi m\'barrer de là, mec !', Text0_1_loc2 = null, Text1_0_loc2 = 'Hé, z\'êtes pas du Fléau. Caisse vous fabriquez ici, mec ?', Text1_1_loc2 = null, Text2_0_loc2 = 'Pourquoi que le Fléau il vous a $glaissé:laissée; arriver ici, mec ?', Text2_1_loc2 = null, Text3_0_loc2 = 'J\'crois que z\'êtes $gperdu:perdue;, mec…', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13390;
UPDATE locales_npc_text SET entry = 13391, Text0_0_loc2 = 'Le Fléau, il est pas le bienvenu à Zul\'Drak !', Text0_1_loc2 = null, Text1_0_loc2 = 'Zul\'Drak, ça appartient aux Drakkari, mec ! Ça changera jamais !', Text1_1_loc2 = null, Text2_0_loc2 = 'Le Fléau, il va jamais gagner ici, mec.', Text2_1_loc2 = null, Text3_0_loc2 = 'Notre peuple, il va tuer tous les monstres du Fléau, mec !', Text3_1_loc2 = null, Text4_0_loc2 = 'Approche pas de moi, sale démon !', Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13391;
UPDATE locales_npc_text SET entry = 13395, Text0_0_loc2 = 'Pour l\'instant, ça ne ressemble peut-être qu\'à un champ de citrouilles ordinaire, mais une fois que le chaudron à peste sera en place, ça deviendra une source de mort, pourriture et corruption !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13395;
UPDATE locales_npc_text SET entry = 13396, Text0_0_loc2 = 'Comment osez-vous ! Hors de ma vue !', Text0_1_loc2 = null, Text1_0_loc2 = 'Ce bon à rien de lèche-bottes est sans doute en train de répéter ses entrechats dans ses quartiers.', Text1_1_loc2 = null, Text2_0_loc2 = 'Ne répétez JAMAIS ce nom en ma présence ! Considérez ceci comme un premier et dernier avertissement !', Text2_1_loc2 = null, Text3_0_loc2 = 'Je peux vous dire où il n\'est pas…', Text3_1_loc2 = null, Text4_0_loc2 = 'Heigan est sans doute en train de travailler sur le tas de fange qu\'il appelle un studio de danse.', Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13396;
