UPDATE locales_npc_text SET entry = 13402, Text0_0_loc2 = 'Fineous a tout ce qu\'il vous faut, $gl\'ami:l\'amie;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13402;
UPDATE locales_npc_text SET entry = 13419, Text0_0_loc2 = 'Les croisés…$B$B<Vaillefendre secoue la tête.>$B$BJe suis convaincu qu\'ils constituent une branche unique de l\'humanité. Ma théorie est qu\'ils souffrent d\'une déficience des fonctions avancées du cerveau qui les empêche d\'évaluer logiquement une situation et d\'en calculer l\'issue. Il suffit de mentionner la Lumière pour qu\'ils soient prêts à se battre, la bouche pleine d\'écume. Puis ils fonceront tête baissée dans n\'importe quelle situation sans s\'arrêter une seconde pour réfléchir à leur propre sécurité.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13419;
UPDATE locales_npc_text SET entry = 13422, Text0_0_loc2 = '$N ! Moi j\'attendais que vous reveniez. Qu\'est-ce qui a pris si longtemps, hein, mec ?$B$BLa conversion des chancre-sang, c\'est l\'heure de la finir.$B$BVenez assister à la création de notre armée !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13422;
UPDATE locales_npc_text SET entry = 13425, Text0_0_loc2 = 'Je suis salement blessé, mais je ferai ce que je peux pour aider. Essayez de rester derrière ma barrière anti-magie, ou les sortilèges des acolytes vont vous mettre en charpie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13425;
