UPDATE locales_npc_text SET entry = 13466, Text0_0_loc2 = 'Varos Arpentenuée et ses gardiens des anneaux protègent le second anneau. Vos drakes sont largement de taille à affronter les gardiens, mais Varos se tient derrière un bouclier impénétrable créé directement à partir de l\'énergie de l\'Oculus. Dix assemblages centifruges alimentent le bouclier en énergie depuis l\'anneau et les plates-formes qui le surplombent. Détruisez-les, et Varos deviendra vulnérable.', Text0_1_loc2 = 'Varos Arpentenuée et ses gardiens des anneaux protègent le second anneau. Vos drakes sont largement de taille à affronter les gardiens, mais Varos se tient derrière un bouclier impénétrable créé directement à partir de l\'énergie de l\'Oculus. Dix assemblages centifruges alimentent le bouclier en énergie depuis l\'anneau et les plates-formes qui le surplombent. Détruisez-les, et Varos deviendra vulnérable.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13466;
