UPDATE locales_npc_text SET entry = 13503, Text0_0_loc2 = 'Grâce à vous, nous avons récupéré quelques provisions et je peux me remettre à la cuisine. Alors, que puis-je faire pour vous ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13503;
UPDATE locales_npc_text SET entry = 13504, Text0_0_loc2 = null, Text0_1_loc2 = 'Soyez sur vos gardes, $r.$B$BIci, toute faiblesse peut vous coûter la vie.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13504;
UPDATE locales_npc_text SET entry = 13506, Text0_0_loc2 = null, Text0_1_loc2 = 'Pourquoi tout le monde me regarde comme si le vaisseau s\'était écrasé à cause de moi ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13506;
UPDATE locales_npc_text SET entry = 13510, Text0_0_loc2 = 'Mmm… Votre visage me dit quelque chose, $r… Donnez-moi une seconde…$B$BHa ! La marche de l\'Ouest, c\'est de là que je vous connais. Vous êtes $N ! Je suis content de vous revoir. Espérons que, cette fois, vous pourrez nous aider à en finir.$B$BBien sûr, nous sommes loin de la marche, ici. Mais si nous ne gagnons pas cette bataille, nous n\'aurons peut-être plus de chez-nous où rentrer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13510;
UPDATE locales_npc_text SET entry = 13511, Text0_0_loc2 = '$N ! Je vous ai à peine $greconnu:reconnue; avec votre nouvelle tenue. Vous avez encore la tunique… ou est-ce que vous aviez pris les hauts-de-chausses, ou le bâton ? Je ne me souviens plus, c\'était il y a si longtemps…$B$BEn tout cas, je suis content que vous soyez $grevenu:revenue; nous aider. Nous avons besoin de toute l\'aide possible. $N, si vous faites aussi bien qu\'à l\'époque avec la milice du peuple de la marche de l\'Ouest, nous avons notre chance.$B$BLes temps sont durs. Si nous ne gagnons pas cette bataille, il n\'y aura peut-être plus jamais de marche de l\'Ouest.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13511;
UPDATE locales_npc_text SET entry = 13512, Text0_0_loc2 = 'Par la grâce de la Lumière, et grâce à votre aide, $N, les Défias ne sont plus une menace.$B$BMais nous sommes loin de la marche de l\'Ouest, et, si nous ne gagnons pas cette bataille, il n\'y aura peut-être plus jamais de marche de l\'Ouest.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13512;
UPDATE locales_npc_text SET entry = 13517, Text0_0_loc2 = 'Qu\'est-ce que Korg peut faire pour vous aujourd\'hui ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13517;
UPDATE locales_npc_text SET entry = 13519, Text0_0_loc2 = 'Bonjour, mon $gami:amie;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13519;
UPDATE locales_npc_text SET entry = 13525, Text0_0_loc2 = 'Oui, le roi-liche succombera à notre puissance. Nous aurons notre ultime vengeance.$B$BMais d\'abord, nous nous assurerons que cette plaie qu\'est l\'Assaut écarlate disparaisse une bonne fois pour toute !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13525;
UPDATE locales_npc_text SET entry = 13526, Text0_0_loc2 = 'Très bien. Mais si j\'étais vous, j\'écouterais bien, au lieu de me contenter de vaguement hocher la tête à ce que j\'entends.$B$BCette histoire commence, comme toutes les bonnes histoires, par une trahison. Oh, ce n\'est pas nous qui avons été trahis… pas du tout. Le roi-liche nous a élevés, nous les chevaliers de la mort, au rang le plus élevé de son armée.$B$BNon, c\'est en fait nous qui l\'avons trahi, avec l\'aide du généralissime Darion Mograine.$B$BLors de l\'affrontement final à la chapelle de l\'Espoir de Lumière, alors que ces simples d\'esprit étaient à deux doigts d\'être balayés, Tirion Fordring a aidé Mograine à briser l\'emprise que le roi-liche avait sur lui et sur tous ceux d\'entre nous qui étaient présents.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13526;
UPDATE locales_npc_text SET entry = 13527, Text0_0_loc2 = 'Le roi-liche a fui, terrorisé par Fordring et Porte-cendres, ce qui nous a permis d\'arracher le fort d\'Ébène aux créatures du Fléau restantes.$B$BCela n\'a pas arrangé les choses entre nous et la Croisade écarlate, cependant.$B$BLes Réprouvés l\'ont appris dans la douleur : les écarlates ne semblent pas être capables de faire la différence entre nous et le Fléau.$B$BNous pensons par nous-mêmes, nous avons nos motivations à nous, nous avons presque toutes les qualités des vivants, et pourtant, pour eux, nous ne sommes pas différents des esclaves décérébrés du roi-liche.$B$BIls croient à tort que nous allons nous coucher sur le dos et faire les morts.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13527;
UPDATE locales_npc_text SET entry = 13528, Text0_0_loc2 = '<Le seigneur-commandant vous fixe du regard avant de poursuivre.>$B$BJ\'y venais, j\'y venais. Effectivement, quand le grand général Abbendis est arrivée en Norfendre avec ses troupes, elle leur a donné le nom d\'Assaut écarlate.$B$BIl n\'a pas fallu longtemps pour que l\'amiral Barean Ponevent se présente à elle à la Nouvelle-Âtreval dans la Désolation des dragons. D\'après toutes mes sources, elle ne s\'attendait pas à sa venue. Elle ne s\'attendait pas non plus à ce qu\'il rabaisse son autorité et la prive du contrôle de l\'Assaut.$B$BIl y a de bonnes chances pour que vous ayez joué un rôle dans sa disgrâce, mais peu importe. Une fois Abbendis morte, l\'amiral a fait une sortie étrange, et nous avons perdu sa trace.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13528;
UPDATE locales_npc_text SET entry = 13529, Text0_0_loc2 = 'Comme je le disais, nous avons perdu sa trace, mais je suis sûr que vous arriverez à la retrouver pour nous. Je soupçonne qu\'il se terre quelque part là-bas, mais mes espions n\'ont pas trouvé le moindre indice sur l\'île.$B$BLes Réprouvés de Vexevenin ont réussi leur sabotage de la Nouvelle-Âtreval, mais les troupes de l\'Assaut stationnées là-bas sont les plus endurcies qu\'il reste. Malheureusement, ils ont trouvé de quoi améliorer leur équipement en chemin, ainsi que quelques griffons pris quelque part à des imbéciles.$B$BCe que j\'essaie de vous dire, c\'est que tout ça ne va pas être une promenade de santé, $N. Pas même pour vous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13529;
UPDATE locales_npc_text SET entry = 13530, Text0_0_loc2 = 'Nous allons commencer dans quelques instants.$B$BJe veux juste que ce soit bien clair : nous ne sommes pas là pour les affaiblir. Nous ne sommes pas là pour ébrécher leur cuirasse. Nous sommes là pour mettre un terme définitif à l\'infection que l\'on appelle Assaut écarlate.$B$BSi vous ne voulez pas vous lancer là-dedans, si vous n\'avez pas les tripes pour, repartez maintenant. Il me faut quelqu\'un qui sera présent tous les jours jusqu\'à ce que ce soit fait.$B$BÊtes-vous $gcelui:celle; qu\'il me faut, $N ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13530;
UPDATE locales_npc_text SET entry = 13531, Text0_0_loc2 = '<Le seigneur-commandant vous adresse un sourire carnassier, en vous examinant des pieds à la tête.>$B$BVoilà ce que je voulais entendre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13531;
UPDATE locales_npc_text SET entry = 13534, Text0_0_loc2 = null, Text0_1_loc2 = 'Pensez à toute la souffrance qu\'ont répandue les ordres écarlates, toutes ces années. La torture, l\'intégrisme, le meurtre.$B$BJe ne permettrai pas que ça continue. Le permettrez-vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13534;
UPDATE locales_npc_text SET entry = 13540, Text0_0_loc2 = null, Text0_1_loc2 = 'Le Hyldsmeet est sacré. Quiconque s\'y empare de la victoire se voit assuré d\'une vie aux côtés de Thorim.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13540;
UPDATE locales_npc_text SET entry = 13548, Text0_0_loc2 = 'Salut, $N.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13548;
UPDATE locales_npc_text SET entry = 13557, Text0_0_loc2 = 'Bienvenue, $c. Je peux mettre vos familiers à l\'écurie ou vous aider à retrouver un compagnon perdu.', Text0_1_loc2 = 'Bienvenue, $c. Je peux mettre vos familiers à l\'écurie ou vous aider à retrouver un compagnon perdu.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13557;
UPDATE locales_npc_text SET entry = 13565, Text0_0_loc2 = null, Text0_1_loc2 = 'Croc-de-glace m\'a aidée à traverser d\'innombrables batailles.$B$BNe vous laissez pas abuser par l\'absence d\'armure. C\'est l\'ourse la plus solide que j\'ai jamais vue.', Text1_0_loc2 = null, Text1_1_loc2 = 'Ne traînez pas ici, donzelle !$B$BIl y a un combat à livrer !', Text2_0_loc2 = null, Text2_1_loc2 = 'Nous combattons pour la gloire de Brunnhildar !', Text3_0_loc2 = null, Text3_1_loc2 = 'Croc-de-glace fait montre d\'un courage incroyable en partant au combat sans armure.$B$BPeut-être que son exemple pourrait vous inspirer de la bravoure, $N.', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13565;
UPDATE locales_npc_text SET entry = 13572, Text0_0_loc2 = 'Hé, $c, si vous avez perdu une de vos clés, je peux peut-être vous aider.', Text0_1_loc2 = 'Hé, $c, si vous avez perdu une de vos clés, je peux peut-être vous aider.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13572;
UPDATE locales_npc_text SET entry = 13573, Text0_0_loc2 = 'Vous vous trouvez devant un maître de l\'Arcane. Me défier serait imprudent. J\'ai laissé beaucoup de corps calcinés dans mon sillage.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13573;
UPDATE locales_npc_text SET entry = 13574, Text0_0_loc2 = null, Text0_1_loc2 = 'Ma vie et mon esprit sont pleins d\'images de cette insupportable Horde. Des heures misérables et désespérées, des heures de sacrifice arrivent. La rage du combat ne quittera jamais mon cœur !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13574;
