UPDATE locales_npc_text SET entry = 13581, Text0_0_loc2 = 'Caisse qui s\'passe, mec ?$B$BZ\'êtes là pour manger, mec ? Ou vous voulez apprendre la cuisine auprès des meilleurs, hein ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13581;
