UPDATE locales_npc_text SET entry = 13591, Text0_0_loc2 = null, Text0_1_loc2 = 'Salut, vous ! Vous voulez acheter du matériel de pêche, ou peut-être prendre une leçon ou deux ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13591;
UPDATE locales_npc_text SET entry = 13593, Text0_0_loc2 = 'Je suis resté trop longtemps loin de vous. J\'ai dû me battre dans tout Azeroth pour redécouvrir mon passé et reprendre mon trône. Après tous les défis qu\'a dû affronter l\'Alliance en mon absence, voir des héros comme vous défendre notre coalition me réchauffe le cœur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13593;
UPDATE locales_npc_text SET entry = 13607, Text0_0_loc2 = 'Vous perdez votre temps. Allez donc tuer les laquais du roi-liche, $c !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13607;
