UPDATE locales_npc_text SET entry = 13611, Text0_0_loc2 = '<L\'éclaireur givre-né tente de se relever à votre approche, mais il retombe par terre, les mains sur une blessure au flanc visiblement douloureuse.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13611;
UPDATE locales_npc_text SET entry = 13612, Text0_0_loc2 = 'Je crois que ça ne sera pas possible, $ggamin:gamine;. J\'ai perdu beaucoup de sang… Je n\'arriverai jamais à rentrer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13612;
UPDATE locales_npc_text SET entry = 13613, Text0_0_loc2 = 'Un vol de harpies, venues de la vallée. Menées par une harpie gigantesque, Sirana… Aucun moyen de m\'enfuir. Ça fait longtemps qu\'elles n\'avaient pas été agressives comme ça… Ça m\'a surpris… J\'ai baissé ma garde… C\'est ma faute.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13613;
UPDATE locales_npc_text SET entry = 13614, Text0_0_loc2 = 'Un peu tard pour ça, $ggamin:gamine;…$B$B<L\'éclaireur givre-né crache du sang sur la neige.>$B$BOccupez-vous… de cette furie… pour moi.$B$B<L\'éclaireur laisse échapper son dernier souffle et s\'effondre.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13614;
UPDATE locales_npc_text SET entry = 13615, Text0_0_loc2 = 'Regardez autour de vous, digérez bien tout cette dévastation, $c. Il n\'y a pas un des trolls qui vivaient ici qui ne soit mort ou en fuite dans les niveaux supérieurs.$B$BEt ce n\'est pas le pire.$B$BDans un geste désespéré, les trolls drakkari encore vivants ont sacrifié leurs propres dieux animaux pour pouvoir en boire le sang et y gagner une puissance incommensurable. Même si cette nouvelle force leur a permis de repousser le Fléau, les conséquences en sont catastrophiques. Les ruines de l\'antique empire de Zul\'Drak en sont bien la preuve.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13615;
UPDATE locales_npc_text SET entry = 13616, Text0_0_loc2 = 'Est-ce que vous voulez bien rester hors de vue, s\'il vous plaît ? J\'essaie de faire semblant d\'être encore sous le contrôle du roi-liche !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13616;
UPDATE locales_npc_text SET entry = 13619, Text0_0_loc2 = 'Gerk, Burr, Dargath : tous morts… Il ne reste plus que MacKellar et moi ici. J\'espère que vous apportez de bonnes nouvelles.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13619;
UPDATE locales_npc_text SET entry = 13628, Text0_0_loc2 = 'Pas faire de bruit ! Y a les petits qui dorment !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13628;
UPDATE locales_npc_text SET entry = 13637, Text0_0_loc2 = null, Text0_1_loc2 = 'Soyez $gle bienvenu:la bienvenue; parmi nous, $c.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13637;
UPDATE locales_npc_text SET entry = 13639, Text0_0_loc2 = 'Avec ma liberté vient la vengeance de l\'orage !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13639;
UPDATE locales_npc_text SET entry = 13640, Text0_0_loc2 = 'Écoutez ce que j\'ai à vous dire sur l\'ennemi avant que nous y allions. Vous amuser à aller casser du Fléau sur le champ de bataille sans être bien $gpréparé:préparée; ne serait pas une bonne idée.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13640;
UPDATE locales_npc_text SET entry = 13641, Text0_0_loc2 = '<Brann vous lance un regard plein d\'espoir.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13641;
UPDATE locales_npc_text SET entry = 13642, Text0_0_loc2 = 'Imaginez toute la sagesse, toutes les connaissances enfermées dans Ulduar. C\'est le secret même de la vie qui pourrait y être gardé !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13642;
UPDATE locales_npc_text SET entry = 13646, Text0_0_loc2 = 'Boktar trouve que ce désastre est un accident fâcheux, mais je pense que nous sommes censés en retirer quelque chose.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13646;
UPDATE locales_npc_text SET entry = 13647, Text0_0_loc2 = 'Le seigneur de bataille ymirjar, Algar l\'Élu, mobilise ses armées vargules au Reliquaire de souffrance, au nord-est d\'ici.$B$BLe prince Navarius se trouve de l\'autre côté des champs des Morts, à l\'est, près des grandes marches qui mènent au deuxième étage de Zul\'Drak.$B$BLa position actuelle de Thrym n\'est pas connue, ce qui est étrange considérant sa taille.$B$BIl faut nous débarrasser d\'eux !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13647;
UPDATE locales_npc_text SET entry = 13650, Text0_0_loc2 = 'Du… poison… Mes entrailles brûlent…', Text0_1_loc2 = 'Du… poison… Mes entrailles brûlent…', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13650;
UPDATE locales_npc_text SET entry = 13651, Text0_0_loc2 = 'Dites-moi quand vous serez $gprêt:prête;. Tôt, ce serait mieux que tard… Je suis un petit peu pressé, avec ces histoires de poison et d\'agonie atroce.', Text0_1_loc2 = 'Dites-moi quand vous serez $gprêt:prête;. Tôt, ce serait mieux que tard… Je suis un petit peu pressée, avec ces histoires de poison et d\'agonie atroce.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13651;
UPDATE locales_npc_text SET entry = 13654, Text0_0_loc2 = 'Il faut frapper Jotunheim sans arrêt. Ne jamais leur donner une chance de reprendre leur souffle ou de dormir. Et ne jamais accepter la moindre reddition !$B$BTirion Fordring, armé de Porte-cendres et soutenu par toute la puissance de la Croisade d\'argent, ne pourrait jamais espérer arriver à ce que vous êtes en train d\'accomplir !$B$BDites ce que vous avez à dire, $N, mais faites vite. La situation n\'est pas propice aux petits bavardages.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13654;
UPDATE locales_npc_text SET entry = 13656, Text0_0_loc2 = 'Quand vous serez $gprêt:prête;, je vous poserai sur ma tête et nous marcherons jusqu\'au cœur du Fléau à Zul\'Drak. Vous devez me diriger, ensemble nous allons broyer nos ennemis !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13656;
UPDATE locales_npc_text SET entry = 13658, Text0_0_loc2 = 'On est encerclés, $r ! Attention derrière vous !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13658;
UPDATE locales_npc_text SET entry = 13659, Text0_0_loc2 = 'La Lame d\'ébène vous est redevable pour l\'aide que vous nous avez apportée dans la prise du caveau des Ombres. Nous avons de la chance de vous avoir.$B$BPendant que l\'Alliance et la Horde se battent entre elles et font le travail du roi-liche, nous allons rester concentrés sur notre tâche.$B$BLa Croisade d\'argent hésiterait et se demanderait longuement quelle est la meilleure manière d\'écraser son ennemi, mais nous, nous ne nous posons pas ce genre de problèmes moraux.$B$BVous serez notre instrument de vengeance, $N.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13659;
UPDATE locales_npc_text SET entry = 13660, Text0_0_loc2 = null, Text0_1_loc2 = 'Je vais vous arracher les membres.', Text1_0_loc2 = null, Text1_1_loc2 = 'Vous me défiez ?', Text2_0_loc2 = null, Text2_1_loc2 = 'Vous n\'avez pas l\'air si solide.', Text3_0_loc2 = null, Text3_1_loc2 = 'Vous pensez que vous en avez assez dans la tunique ?', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13660;
