UPDATE locales_npc_text SET entry = 13673, Text0_0_loc2 = 'Je vais m\'en sortir tout seul, $N.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13673;
UPDATE locales_npc_text SET entry = 13674, Text0_0_loc2 = 'Mais que faites-vous ici, VOUS ?$B$BQuoi qu\'il arrive, attention à ne pas vous mettre sur mon chemin. Je n\'ai pas quitté la Croisade écarlate pour que des moustiques comme vous viennent me déranger !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13674;
UPDATE locales_npc_text SET entry = 13678, Text0_0_loc2 = null, Text0_1_loc2 = 'Cessez de m\'embêter, insecte. J\'étudie les techniques ineptes de ces soi-disant combattants.$B$BJe vais tous les terrasser, puis je serai élevée au rang de servante du roi-liche.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13678;
UPDATE locales_npc_text SET entry = 13679, Text0_0_loc2 = 'Hé, on dirait que vous pourriez nous rapporter un peu d\'argent. Qu\'est-ce que vous diriez d\'un petit tour dans l\'amphithéâtre de l\'Angoisse, $gmon gars:ma vieille; ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13679;
UPDATE locales_npc_text SET entry = 13680, Text0_0_loc2 = 'Vous êtes $gchampion:championne;, maintenant, $gmon pote:ma grande; ! Mais vous pensez avoir ce qu\'il faut pour défendre votre titre ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13680;
