UPDATE locales_npc_text SET entry = 13682, Text0_0_loc2 = 'Retournez au travail avant que le surveillant ne nous voie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13682;
UPDATE locales_npc_text SET entry = 13685, Text0_0_loc2 = 'Cette statue de la corne de Hodir est un monument au roi Arngrim et à tous les guerriers tombés à la bataille de Chute-tonnerre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13685;
UPDATE locales_npc_text SET entry = 13690, Text0_0_loc2 = 'Que puis-je faire pour vous ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13690;
UPDATE locales_npc_text SET entry = 13691, Text0_0_loc2 = null, Text0_1_loc2 = 'Ce petit insecte vert ne servait à rien dans les mines. Ce n\'est pas grave, il fera un bon échauffement au combat.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13691;
UPDATE locales_npc_text SET entry = 13694, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous venez faire vos preuves au roi-liche, vous aussi ?$B$BC\'est un peu archaïque, vous ne trouvez pas, de devoir combattre pour survivre ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13694;
UPDATE locales_npc_text SET entry = 13695, Text0_0_loc2 = null, Text0_1_loc2 = 'Hé, je vous reconnais ! J\'imagine que c\'est vous que je dois remercier pour mon arrivée ici. Cet accident de téléporteur est la meilleure chose qui me soit jamais arrivée.$B$BSi jamais vous repassez dans la toundra Boréenne, dites à mon bon à rien de double que j\'ai fait mon petit bout de chemin dans le monde !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13695;
UPDATE locales_npc_text SET entry = 13697, Text0_0_loc2 = 'L\'enclume de Fjorn se dresse désormais fièrement à Dun Niffelem, symbole de la victoire sur Fjorn et ses armées.$B$BVous ne pouvez réprimer un sourire à l\'idée que vos actes ont eu un impact aussi marquant sur le monde.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13697;
UPDATE locales_npc_text SET entry = 13699, Text0_0_loc2 = null, Text0_1_loc2 = 'Je n\'entraîne plus les chevaliers de la mort, ces crétins d\'initiés et leurs incessantes questions me fatiguaient.$B$BMaintenant, dégagez.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13699;
UPDATE locales_npc_text SET entry = 13701, Text0_0_loc2 = 'Onu\'zun est là pour défier vrykuls. Onu\'zun veut monter dans hiérarchie.$B$BEnsuite, Onu\'zun va combattre dans Valhalas et gagner, et Onu\'zun va travailler pour roi-liche !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13701;
UPDATE locales_npc_text SET entry = 13702, Text0_0_loc2 = 'Comment puis-je vous aider ?', Text0_1_loc2 = 'Comment puis-je vous aider ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13702;
UPDATE locales_npc_text SET entry = 13703, Text0_0_loc2 = 'Salutations, $N. Voici l\'atelier du gardien Mimir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13703;
UPDATE locales_npc_text SET entry = 13707, Text0_0_loc2 = 'Avant, j\'avais tout. La gloire, la fortune, toutes les pièces de la meilleure armure de gladiateur… Et j\'ai tout foutu en l\'air.$B$BQuand le cartel m\'a pris à truquer des combats… à arranger des victoires… tout a été terminé.$B$BMaintenant, je n\'ai plus que ces haillons et un mauvais foie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13707;
UPDATE locales_npc_text SET entry = 13709, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13709;
UPDATE locales_npc_text SET entry = 13715, Text0_0_loc2 = null, Text0_1_loc2 = 'Jadis, j\'avais une maison dans les vertes forêts de Quel\'Thalas. J\'avais une famille, un mari… des enfants. Jadis, j\'étais vivante, en harmonie avec le monde. Le roi-liche m\'a arraché tout cela, en un éclair.$B$BMême si je n\'ai pas le pouvoir de le frapper directement, je lui ferai payer au centuple !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13715;
UPDATE locales_npc_text SET entry = 13731, Text0_0_loc2 = 'C\'est une découverte révolutionnaire ! La Société royale des apothicaires travaille depuis des années à briser les secrets de la peste du roi-liche, et j\'ai enfin réussi !$B$BMalgré ce grand tournant de l\'Histoire, il reste encore tant à découvrir, j\'ai encore des expériences à faire... des sujets à trouver...$B$BAu moins, je n\'ai pas à m\'inquiéter des nécropoles du Fléau. Ce problème-là, c\'est l\'Alliance qui s\'en occupe. Cela me laisse tout le temps pour me plonger dans les secrets de la peste et découvrir ce que je pourrai.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13731;
UPDATE locales_npc_text SET entry = 13733, Text0_0_loc2 = 'Où il ya des givre-nés, il y a des aigles. Ce sont nos alliés depuis plus longtemps que je ne foule cette terre... Et on ne peut pas dire que je suis de la première jeunesse, $Gmon garçon:jeune fille;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13733;
UPDATE locales_npc_text SET entry = 13734, Text0_0_loc2 = 'Que puis-je faire pour vous ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13734;
UPDATE locales_npc_text SET entry = 13735, Text0_0_loc2 = 'Comment que ça va, mec ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13735;
