UPDATE locales_npc_text SET entry = 13738, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13738;
UPDATE locales_npc_text SET entry = 13740, Text0_0_loc2 = 'Vous êtes $gle bienvenu:la bienvenue; ici, en tant qu\'$gami:amie; et $gallié:alliée; des terrestres.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13740;
UPDATE locales_npc_text SET entry = 13747, Text0_0_loc2 = 'Vous avez la permission de visiter notre bastion, $r.$b$bNe nous apportez pas le déshonneur !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13747;
UPDATE locales_npc_text SET entry = 13748, Text0_0_loc2 = 'Je ne vous fais pas assez confiance pour parler de ces choses.$b$bVous devez respecter les épreuves que notre peuple a endurées, les fardeaux que nous avons portés...$b$bPeut-être que si vous faisiez grandement honneur à Dun Niffelem, je serais enclin à partager avec vous le savoir de notre peuple.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13748;
UPDATE locales_npc_text SET entry = 13749, Text0_0_loc2 = 'Très bien, $gpetit:petite:c; $c.$b$bBien qu\'il vous reste encore beaucoup à faire pour améliorer nos relations avec Thorim, je me sens obligé de partager avec vous ce secret.$b$bRevenez me parler quand vous serez $gprêt:prête; à découvrir ce que vous cherchez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13749;
UPDATE locales_npc_text SET entry = 13759, Text0_0_loc2 = 'Bonjour, $r. Comment puis-je vous aider aujourd\'hui ?', Text0_1_loc2 = 'Bonjour, $r. Comment puis-je vous aider aujourd\'hui ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13759;
UPDATE locales_npc_text SET entry = 13761, Text0_0_loc2 = 'Désolé, $gl\'ami:l\'amie;. Seuls les officiers accrédités de la Horde peuvent autoriser l\'achat d\'un véhicule.', Text0_1_loc2 = 'Désolé, $gl\'ami:l\'amie;. Seuls les officiers accrédités de la Horde peuvent autoriser l\'achat d\'un véhicule.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13761;
UPDATE locales_npc_text SET entry = 13764, Text0_0_loc2 = 'C\'est bon d\'vous revoir !$B$BLe roi Foudrecoeur m\'a fait savoir qu\'il s\'absentait quelque temps… Un noir souvenir qui lui est revenu lui a rappelé une vieille dette qu\'il avait, semble-t-il. Il a fortement insisté pour que nous ne l\'accompagnions pas, et, comme toujours, nous avons respecté sa volonté.$B$BEn attendant, je suis responsable du clan. Vous êtes toujours $gle bienvenu:la bienvenue; parmi nous, bien sûr.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13764;
UPDATE locales_npc_text SET entry = 13776, Text0_0_loc2 = 'Vous n\'êtes pas $gun:une; taunka. Pourquoi venez-vous ici, au cimetière sacré de nos guerriers ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13776;
UPDATE locales_npc_text SET entry = 13777, Text0_0_loc2 = null, Text0_1_loc2 = 'Regardez-les… Tout ce qui les intéresse, c\'est de surpasser l\'autre.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13777;
UPDATE locales_npc_text SET entry = 13778, Text0_0_loc2 = null, Text0_1_loc2 = 'Thorim n\'est pas comme ces harpies, voyez-vous. C\'est un être de valeur, de stature quasi-divine.$B$BIl a servi les titans, comme gardien d\'Ulduar et de la terrasse des Faiseurs.$B$BEnfin, jusqu\'à ce que sa femme, Sif, soit traîtreusement tuée par son frère.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13778;
UPDATE locales_npc_text SET entry = 13779, Text0_0_loc2 = null, Text0_1_loc2 = 'Thorim a perdu pied. Il s\'en est pris à ses plus proches alliés, les géants du givre.$B$BC\'est une des raisons pour lesquelles, aujourd\'hui, cette terre est déchirée par la guerre entre les Hyldnir et les fils d\'Hodir… et pour lesquelles Thorim s\'est exilé dans le temple des Tempêtes.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13779;
UPDATE locales_npc_text SET entry = 13780, Text0_0_loc2 = null, Text0_1_loc2 = 'Qu\'est-ce que j\'en ai à faire que les louves et les géants du givre se massacrent mutuellement jour après jour ?$B$BNon, non… Ce que je veux, c\'est que Thorim sorte de sa torpeur et rassemble ses alliés pour reprendre Ulduar.$B$BLe monde sera pulvérisé s\'il ne le fait pas !$B$BEt, bien sûr, je saurai vous récompenser.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13780;
UPDATE locales_npc_text SET entry = 13782, Text0_0_loc2 = 'Laissez-moi à ma honte, $r.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13782;
UPDATE locales_npc_text SET entry = 13783, Text0_0_loc2 = null, Text0_1_loc2 = 'Ah, les hommes. On ne peut pas vivre avec, et on ne peut pas vivre sans. Enfin si, on peut… Mais on s\'ennuie.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13783;
UPDATE locales_npc_text SET entry = 13784, Text0_0_loc2 = null, Text0_1_loc2 = 'Ces mines sont vraiment un endroit horrible. Heureusement, je ne compte pas rester ici très longtemps.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13784;
UPDATE locales_npc_text SET entry = 13785, Text0_0_loc2 = null, Text0_1_loc2 = 'Les ours sont des créatures magnifiques. Sauvages, puissants, féroces… tout comme nous, ma sœur.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13785;
