UPDATE locales_npc_text SET entry = 13797, Text0_0_loc2 = 'Nous avons découvert ces proto-drakes au sanctuaire draconique bronze dans la Désolation des dragons. Au premier regard, nous avons su qu\'ils étaient de lointains cousins. Du même sang que nous, loin dans le temps.$B$BMes excuses, $c. Tout cela est sans doute perturbant. Je suis Penumbrius, du Vol de bronze. J\'ai pris cette forme pour plus de facilité. Évoluer parmi les mortels et communiquer avec eux est bien plus aisé avec une telle apparence.$B$BComment puis-je vous aider ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13797;
UPDATE locales_npc_text SET entry = 13798, Text0_0_loc2 = 'Bonjour, $N ! Que puis-je faire pour vous aujourd\'hui ?', Text0_1_loc2 = 'Bonjour, $N ! Que puis-je faire pour vous aujourd\'hui ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13798;
UPDATE locales_npc_text SET entry = 13799, Text0_0_loc2 = 'Parlez, $c.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13799;
UPDATE locales_npc_text SET entry = 13800, Text0_0_loc2 = 'J\'ai fui ma destinée bien assez longtemps. Ce que je dois faire n\'a jamais été aussi clair.$B$BC\'est à moi qu\'il incombe de détruire Loken.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13800;
UPDATE locales_npc_text SET entry = 13801, Text0_0_loc2 = 'Pendant un moment, j\'ai cru que vous étiez ma chère Sif… cette chevelure… Mais non ! C\'est impossible… Sif est morte…$B$BMon frère… l\'a tuée !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13801;
UPDATE locales_npc_text SET entry = 13802, Text0_0_loc2 = 'Ulduar… J\'ai jadis fait serment de protéger Ulduar.$B$BFait serment de protéger la terrasse des Faiseurs.$B$BFait serment de protéger le pacte entre les races de givre.$B$BMais j\'ai échoué ! J\'ai laissé mon frère me tromper. Je l\'ai laissé me retourner contre mes propres alliés. Il m\'a abusé, m\'a fait tenir un ami cher, Arngrim, roi des géants du givre, pour responsable de la mort de Sif.$B$BQuand j\'ai enfin compris ce que j\'avais fait... j\'ai cédé au désespoir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13802;
UPDATE locales_npc_text SET entry = 13803, Text0_0_loc2 = 'Qui êtes vous, petite créature ? Pour venir murmurer ces mots au puissant Thorim…$B$BJe devrais… vous écraser !$B$BCependant, vous m\'avez délivré de ma torpeur. Votre présence ici est un signe !$B$BCe n’est pas un accident si je vous ai $gpris:prise; pour Sif. Qu\'est-ce qu\'elle penserait de moi ? Je dois me préparer au combat.$B$BJe dois venger sa mort et délivrer la terre sainte des titans de la folie de mon frère !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13803;
