UPDATE locales_npc_text SET entry = 13846, Text0_0_loc2 = 'Enchanté, $c. Voici un conseil pour vous : dans vos voyages à travers le monde, défiez-vous de la magie, car elle consume les non-initiés.', Text0_1_loc2 = 'Enchanté, $c. Voici un conseil pour vous : dans vos voyages à travers le monde, défiez-vous de la magie, car elle consume les non-initiés.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13846;
UPDATE locales_npc_text SET entry = 13847, Text0_0_loc2 = null, Text0_1_loc2 = 'Bien sûr ! Allez avec la bénédiction d\'Elune.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13847;
UPDATE locales_npc_text SET entry = 13848, Text0_0_loc2 = 'Ce jour, il est sombre dans l\'histoire des trolls, mec.', Text0_1_loc2 = null, Text1_0_loc2 = 'C\'est horrible, mec. J\'peux pas encadrer de voir ça.', Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13848;
UPDATE locales_npc_text SET entry = 13850, Text0_0_loc2 = null, Text0_1_loc2 = 'Les événements tragiques, c\'est mon devoir de les consigner, pour le bien des générations futures.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13850;
UPDATE locales_npc_text SET entry = 13851, Text0_0_loc2 = 'Il ne faut pas relâcher notre attention. Se laisser dépasser par l\'instant présent comporte bien des dangers, qui pourraient nous causer un tort incroyable. Mais, hélas, les soldats ont aussi besoin d\'un peu de répit au milieu d\'une telle folie.$B$BSavourez-bien cette victoire, $N. Si vous n\'aviez pas été là, nous aurions pu perdre l\'avant-garde.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13851;
UPDATE locales_npc_text SET entry = 13853, Text0_0_loc2 = null, Text0_1_loc2 = 'Des renforts, enfin ! Les gardes de la prison ne tiendront plus très longtemps contre cette invasion ! Il va falloir que vous releviez mes hommes et que vous essayiez de tenir le plus longtemps possible. Le système de défense a encore un peu d\'énergie en réserve. Utilisez les cristaux d\'activation répartis dans la prison pour lancer une décharge si la situation tourne mal.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13853;
UPDATE locales_npc_text SET entry = 13854, Text0_0_loc2 = null, Text0_1_loc2 = 'Est-ce que tout votre groupe est prêt à continuer ? Je vais tirer une décharge avec ce cristal pour couvrir la retraite de mes gardes.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13854;
UPDATE locales_npc_text SET entry = 13856, Text0_0_loc2 = 'Ce que je fais pour l\'Aube d\'argent aujourd\'hui ne doit être vu que comme l\'acquittement d\'une obligation. Un paiement, si vous voulez. Le généralissime m\'a libéré des griffes du roi-liche et nous a ouvert à tous le chemin de la rédemption. Cela dit, il faut bien comprendre que nos méthodes diffèrent, et diffèrent profondément. Les chevaliers de la Lame d\'ébène et moi aiderons l\'Aube d\'argent à poser les fondations de leur présence dans la Couronne de glace, puis nos chemins se sépareront. Le paiement aura été honoré.$B$BUne destinée différente nous attend.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 13856;
