UPDATE locales_npc_text SET entry = 14052, Text0_0_loc2 = null, Text0_1_loc2 = 'Mais bon sang ! Baissez la voix, $N ! Quoi ? Vous essayez de nous faire massacrer ?$B$BBien sûr que je sais qui vous êtes. Qui ne connaît pas le nom $N, dans l\'Alliance ?!$B$BAlors, qu\'est-ce que vous pensez de mon sautomate ? Je me suis inspirée des mécagnomes. Élancé et aérodynamique !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14052;
UPDATE locales_npc_text SET entry = 14053, Text0_0_loc2 = 'Houlà, houlà là ! Mettez-la en veilleuse, $N. Pas si fort !$B$B<Exploz regarde autour de lui.>$B$BIl ne faudrait pas que les vrykuls-à-glaçons nous remarquent, hein ?$B$BQu\'est-ce que vous pensez de mon sautomate ? Je me suis inspiré des mécagnomes. Joli, non ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14053;
UPDATE locales_npc_text SET entry = 14055, Text0_0_loc2 = 'Ça ne s\'annonce pas comme une partie de rigolade. Si vous savez envoyer la sauce, restez dans le coin jusqu\'à ce qu\'un de mes bataillons soit prêt à faire l\'ascension jusqu\'à Ymirheim. Ils auront besoin de toute l\'aide possible.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14055;
UPDATE locales_npc_text SET entry = 14056, Text0_0_loc2 = 'L\'Assurance a subi une infestation de vermine qui nous empêche de partir pour le Norfendre.', Text0_1_loc2 = null, Text1_0_loc2 = 'On était tous supposés appareiller pour le Norfendre, mais on a trouvé un nombre alarmant de parasites sur le navire.', Text1_1_loc2 = null, Text2_0_loc2 = 'Avant de pouvoir lever les voiles pour le Norfendre, il faut qu\'on se débarrasse du problème d\'infestation sur l\'Assurance.', Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14056;
