UPDATE locales_npc_text SET entry = 14064, Text0_0_loc2 = 'Il y a des vies qui dépendent de ce qu\'on fait ici. Si vous avez quelque chose à dire, dites-le vite, $c.', Text0_1_loc2 = null, Text1_0_loc2 = 'Apportez-vous des nouvelles du combat, $r ?', Text1_1_loc2 = null, Text2_0_loc2 = 'La meilleure guerre est une guerre rapide. C\'est aussi vrai pour les conversations. Qu\'avez-vous à dire, $r ?', Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14064;
