UPDATE locales_npc_text SET entry = 14067, Text0_0_loc2 = null, Text0_1_loc2 = 'L\'odeur de poudre de tous les missiles et les bombes qui explosent arrive jusqu\'ici. Vous voulez vous joindre aux réjouissances ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14067;
UPDATE locales_npc_text SET entry = 14068, Text0_0_loc2 = 'Les voix me parlent.', Text0_1_loc2 = null, Text1_0_loc2 = 'Je dois obéir.', Text1_1_loc2 = null, Text2_0_loc2 = 'Il faut que vous sortiez d\'ici avant d\'être $gpris:prise; à votre tour !', Text2_1_loc2 = null, Text3_0_loc2 = 'C\'est tout autour de nous. C\'est dans ma tête !', Text3_1_loc2 = null, Text4_0_loc2 = 'La saronite… Il y a quelque chose dedans !', Text4_1_loc2 = null, Text5_0_loc2 = 'Il y a quelque chose ici. C\'est dans les pierres et la terre !', Text5_1_loc2 = null, Text6_0_loc2 = 'Il faut que je sorte d\'ici ! Est-ce que vous pouvez me libérer ?', Text6_1_loc2 = null, Text7_0_loc2 = 'Vous l\'entendez parler dans votre esprit ?', Text7_1_loc2 = null WHERE entry = 14068;
