UPDATE locales_npc_text SET entry = 14072, Text0_0_loc2 = 'Au cours de mes voyages, j\'ai appris comment confectionner une cape aérienne.$B$BC\'est un manteau épique pour lanceur de sorts, échangeable et qui apporte endurance, puissance des sorts, intelligence et récupération du mana à qui le porte. Je recherche un élève digne d\'apprendre à le fabriquer.$B$BQuelqu\'un qui a écumé tous les donjons des terres du Nord et acquis par ses hauts faits le titre de maître des donjons du Norfendre serait digne de cet enseignement.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14072;
UPDATE locales_npc_text SET entry = 14074, Text0_0_loc2 = 'J\'ai exploré les endroits les plus ténébreux et appris l\'art de confectionner une cape de Froidemort.$B$BC\'est un manteau épique pour lanceur de sorts, échangeable et qui apporte puissance des sorts, hâte et frappes critiques à qui le porte. Je recherche un élève digne d\'apprendre à le fabriquer.$B$BQuelqu\'un qui a accompli la plupart des quêtes des terres du Nord et acquis par ses hauts faits le titre de maître des traditions du Norfendre serait digne de cet enseignement.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14074;
