UPDATE locales_npc_text SET entry = 14089, Text0_0_loc2 = 'J\'ai des renseignements sur la pierre-clé de Norgannon, qui ouvre l\'accès aux archives d\'Ulduar, mais les Titans l\'ont divisée en deux morceaux qu\'ils ont cachés.$B$BUne des deux pièces, la coque, est gardée dans la bibliothèque de l\'Inventeur, sur la côte Nord, au sud d\'Ulduar. La première chose à faire, ce sera de récupérer les fragments d\'un disque d\'accès auprès des gardiens de la bibliothèque.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14089;
UPDATE locales_npc_text SET entry = 14090, Text0_0_loc2 = null, Text0_1_loc2 = 'Quand c\'est d\'trésors qu\'on parle, le fourbi des autres, faut jamais dédaigner d\'le piller. Les objets volés, ils font plus plaisir que le reste, hein ? Gratuit c\'est gratuit, eh. Pourquoi c\'était gratuit, on s\'en fiche !', Text1_0_loc2 = null, Text1_1_loc2 = 'Pour une p\'tite réduction négociée à trois doigts, moi chuis toujours d\'accord. Le vol, ça se passe tout dans le coup d\'poignet… Et si jamais ça marche pas, alors vous y collez un truc pointu entre les côtes, et ça aussi c\'est tout dans le coup d\'poignet.$B$BDes doigts de fée, ça vaut plus qu\'un salaire régulier, qu\'il paraît.', Text2_0_loc2 = null, Text2_1_loc2 = 'Moi, le roi-liche, j\'m\'en inquiète pas. Des grosses pointures, j\'en ai vu passer des caisses, à chaque fois faut qu\'elles s\'débrouillent pour semer la pagaille. Et là, faut pas longtemps pour qu\'une bande de gars débarque et les envoie vite fait vers l\'livre d\'histoire le plus proche. Çui-là, il sera pas différent.', Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14090;
UPDATE locales_npc_text SET entry = 14091, Text0_0_loc2 = 'Seul un imbécile raillerait l\'apparence de mes atours de bataille.$B$BCette tenue d\'acier bien trempé vient d\'un antique lieu de savoir !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14091;
