UPDATE locales_npc_text SET entry = 14364, Text0_0_loc2 = 'Mes bébés ont peut-être l\'air de tas de ferraille, mais ils peuvent charger pour culbuter vos ennemis. Le bélier avant est équipé d\'un hyper-condensateur électroscopique qui lui permet de lâcher des décharges électriques.$B$BMême la tourelle a été dotée de fusées anti-aériennes, ce qui donne un avantage décisif sur ces satanés démolisseurs.', Text0_1_loc2 = 'Mes bébés ont peut-être l\'air de tas de ferraille, mais ils peuvent charger pour culbuter vos ennemis. Le bélier avant est équipé d\'un hyper-condensateur électroscopique qui lui permet de lâcher des décharges électriques.$B$BMême la tourelle a été dotée de fusées anti-aériennes, ce qui donne un avantage décisif sur ces satanés démolisseurs.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14364;
UPDATE locales_npc_text SET entry = 14365, Text0_0_loc2 = 'J\'ai rassemblé ici ce qui pourrait bien être la plus grande collection de connaissances de toute l\'Outreterre. Même les grandes bibliothèques de Lune-d\'argent font pâle figure en comparaison.$B$BJe vous en prie… jetez un œil, lisez, parcourez tout ce que vous voudrez. Vous verrez, tout est trié et indiqué clairement.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14365;
