UPDATE locales_npc_text SET entry = 14534, Text0_0_loc2 = null, Text0_1_loc2 = 'Le cruel joug du roi-liche a causé une grande souffrance aux habitants du Norfendre, et en particulier aux enfants. Si vous êtes l\'$gami:amie; des Oracles ou des Frénécœur de Sholazar, vous pourriez peut-être vous occuper d\'un orphelin pendant une semaine.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14534;
UPDATE locales_npc_text SET entry = 14535, Text0_0_loc2 = 'Nous on aime bien le vieux jaune d\'œuf. On peut avoir ?', Text0_1_loc2 = null, Text1_0_loc2 = 'Tout-puissants ils ont jamais les sacs pleins, avec tous les qui-brille qu\'on leur donne ?', Text1_1_loc2 = null, Text2_0_loc2 = 'Pourquoi les hommes-chiots ils disent toujours que Lafoo taise ? Lafoo il parle beaucoup mais c\'est copain quand même.', Text2_1_loc2 = null, Text3_0_loc2 = 'Soo-$n, peut-être vous pouvez nous donner pour nous un sifflet et comme ça nous on vous appelle si on a besoin ?', Text3_1_loc2 = null, Text4_0_loc2 = 'Peut-être nous on sera $gun:une:r; $r quand on sera grand. Vous pensez ?', Text4_1_loc2 = null, Text5_0_loc2 = 'Hommes-chiots ils ont tué vos parents de vous aussi ?', Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14535;
