UPDATE locales_npc_text SET entry = 14712, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue dans notre magasin. Nous avons tout ce qu\'il vous faut… S\'il vous faut des fournitures d\'alchimie.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14712;
UPDATE locales_npc_text SET entry = 14713, Text0_0_loc2 = null, Text0_1_loc2 = 'Non, moi je suis l\'intellectuelle, et il ne faut pas me prendre à la légère. Je pourrais vous concocter une potion de soin à partir d\'eau de vaisselle et de pissenlits, mais je ne vous montrerai pas comment !$B$BEnfin bon, si vous voulez acheter quelque chose, veuillez vous adresser à Patricia.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14713;
