UPDATE locales_npc_text SET entry = 14751, Text0_0_loc2 = 'Ces falaises sont battues par un vent féroce, et toute vie a depuis longtemps fui ce coin de la Couronne de glace. Cependant, il n\'y a besoin que de quelques encouragements pour la faire revenir, et c\'est pour cela que j\'arpente ce site. Un jour, ces arbres se dresseront haut au-dessus de nos têtes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 14751;
