UPDATE locales_npc_text SET entry = 15081, Text0_0_loc2 = null, Text0_1_loc2 = 'Je ne pourrai pas le retenir longtemps, il faut vite partir d\'ici !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15081;
UPDATE locales_npc_text SET entry = 15101, Text0_0_loc2 = 'Les vieilles blessures sont encore là, $g:petit:petite;. J\'les sens au fond de mes os, même maintenant. Ouaip, j\'ai un compte à régler avec Arthas, et j\'peux vous dire que je vais lui coller la raclée que j\'aurais dû lui ficher quand il est était encore tout petit, au gamin. J\'ai toujours su qu\'il y avait queq\'chose qui clochait chez lui.$B$BDites-moi quand votre bande sera prête, et on s\'envolera vers le sommet d\'la citadelle.$B$BArthas s\'attendra jamais à un coup pareil !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15101;
UPDATE locales_npc_text SET entry = 15103, Text0_0_loc2 = 'Voici venu l\'ultime combat. Les échos des évènements d’aujourd’hui résonneront dans l’histoire. Quelle qu’en soit l’issue, le monde saura que nous avons combattu avec honneur. Que nous avons combattu pour la liberté et la survie de nos peuples.$B$BSouvenez-vous, héros, la peur est votre plus terrible ennemi en ces salles viciées. Armez vos cœurs, et vos âmes brilleront plus qu’un millier de soleils. L’adversaire vacillera à votre vue. Il succombera enveloppé par la lumière de la vertu !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15103;
