UPDATE locales_npc_text SET entry = 15158, Text0_0_loc2 = 'Préparez-vous, $c.$B$BLe temps est venu de mettre fin au règne du roi-liche.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15158;
UPDATE locales_npc_text SET entry = 15159, Text0_0_loc2 = 'Il s\'agit d\'un regroupement des artisans les plus talentueux de la Lame d\'ébène et de la Croisade d\'argent. Sa création symbolise l\'unité de nos troupes au moment de monter l\'ultime offensive contre le roi-liche et ses serviteurs.$B$BC\'est grâce aux efforts du Verdict des cendres que nous avons pu découvrir comment plier la saronite à notre volonté.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15159;
UPDATE locales_npc_text SET entry = 15160, Text0_0_loc2 = 'Allez voir Scott le Miséricordieux, $c. Il peut vous mettre sur la voie.$B$BFaites la preuve de votre loyauté, et ils vous révéleront les secrets de leur art.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15160;
