UPDATE locales_npc_text SET entry = 15170, Text0_0_loc2 = 'Salut ! Je représente la Compagnie de Chimie La Royale.$B$BVous cherchez un joli cadeau ? Ou du boulot ? Ou alors juste à vous faire un petit plaisir ? En tout cas, vous êtes au bon endroit, $gl\'ami:l\'amie;.$B$BSi je n\'ai pas ce qu\'il vous faut, notre mascotte Kalin ou la marchande ravissante l\'aura.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15170;
UPDATE locales_npc_text SET entry = 15171, Text0_0_loc2 = 'Nous allons utiliser tout ce matériel pour nous préparer au combat.$B$BContinuez à avancer. Nous vous retrouverons plus loin.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15171;
UPDATE locales_npc_text SET entry = 15172, Text0_0_loc2 = 'Je vais rassembler le reste des esclaves et les préparer au combat. Nous vous rattraperons avant que vous n\'attaquiez le seigneur du Fléau.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15172;
UPDATE locales_npc_text SET entry = 15174, Text0_0_loc2 = 'Nous vous suivons, héros. Le seigneur du Fléau mourra ce soir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15174;
