UPDATE locales_npc_text SET entry = 15188, Text0_0_loc2 = 'Écoute, trésor, je ne sais pas qui tu es ni pourquoi tu me suis partout, mais je vais embarquer et je veux qu\'on me fiche la paix, compris ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15188;
UPDATE locales_npc_text SET entry = 15189, Text0_0_loc2 = null, Text0_1_loc2 = 'Pardonnez-moi, héros. J’aurais dû écouter Uther. Mais… Je devais le voir par moi-même. Regarder une dernière fois dans ses yeux. Pardon.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15189;
