UPDATE locales_npc_text SET entry = 15194, Text0_0_loc2 = 'C\'était une sacrée bataille, $N ! Prenez un moment pour reprendre des forces. Qui sait c\'qui nous attend derrière cette porte, hein ?$B$BDites-moi quand vous serez prêts, et on commencera l\'ascension vers le haut d\'la citadelle.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15194;
