UPDATE locales_npc_text SET entry = 15206, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous n\'avons pas de temps à perdre, $c. Pourquoi retardez-vous votre départ ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15206;
UPDATE locales_npc_text SET entry = 15207, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous n\'avons pas beaucoup de temps. Qu\'est-ce qu\'il vous faut ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15207;
