UPDATE locales_npc_text SET entry = 15209, Text0_0_loc2 = '<Il lance un coup d\'œil aux fusées et arbore un rictus nerveux.>$B$BOui bon d\'accord, peut-être que j\'ai bricolé deux-trois fusées pour des gens. Et alors ? Ça payait bien, et mon travail m\'a rapporté un peu. C\'est pas comme si j\'avais tué quelqu\'un. C\'est aussi proche d\'un boulot honnête que possible. Alors qu\'est-ce que ça peut vous faire ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15209;
UPDATE locales_npc_text SET entry = 15210, Text0_0_loc2 = 'Écoutez, je n\'en sais rien, d\'accord ? Rien du tout !$B$BMoi, on m\'a juste payé pour fabriquer des fusées qui transportent un chargement de nature chimique d\'un point A à un point B, et c\'est ce que j\'ai fait. J\'ai fait mon boulot. Tout a été construit, assemblé et envoyé. J\'ai reçu mon argent, et là j\'ESSAYAIS de prendre un peu de bon temps quand vous vous êtes mis en tête de me pourchasser dans toute la ville. Ce produit, je n\'en ai plus, et je ne sais pas ce que c\'était.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15210;
UPDATE locales_npc_text SET entry = 15211, Text0_0_loc2 = 'Vous voudriez que je me souvienne de ça comme ça, de tête ? Écoutez. J\'ai pas été assez bien payé pour avoir à supporter ça, et tout ce que j\'ai promis, c\'est de me taire. Alors je vais laisser tomber le bon de commande là, comme ça, sur le sol, et vous allez le ramasser et me laisser tranquille.$B$BPigé ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15211;
UPDATE locales_npc_text SET entry = 15212, Text0_0_loc2 = 'Avez-vous entendu parler de notre opération « Les breloques du cœur » ?$B$BLes mascottes officielles de La Royale comme moi ont l\'autorisation de distribuer des trousses de collectionneur de breloques ravissantes.$B$BVous pourrez alors utiliser vos breloques pour fabriquer des bracelets pour ceux que vous aimez, ou les transformer en marchandes ravissantes pour obtenir des gages d\'amour !$B$BBeaucoup de gens s\'amusent à confectionner des bracelets pour leurs chefs de faction favoris, pour leur montrer qu\'ils sont appréciés et aimés...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 15212;
