SET NAMES utf8;
set names latin1;

/*#############################################
#Parsing quêtes de Wowhead 
#############################################*/

UPDATE `locales_quest` SET `Title_loc2` = 'Les champs de mort' ,`Details_loc2` = 'Regardez ce qu\'il s\'est passé ici ! Ces terres étaient autrefois habitées par de braves fermiers. Mais ces maudits voleurs les ont tous chassés. Pas moi, pour sûr ! Mais il semble que quelques Gardiens des moissons ont pris possession des champs.$B$BSi vous êtes d\'accord, j\'aimerais que vous y alliez et que vous en détruisiez une vingtaine. Revenez quand vous aurez fini pour recevoir votre dû. Une fois que vous aurez débarrassé ceux de mon champ, occupez-vous des champs alentours.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Good work, friend. &nbsp;You have earned your pay well. &nbsp;Who knows, perhaps Westfall will prosper once again.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 9;
UPDATE `locales_quest` SET `Title_loc2` = 'Rechercher les composants à Uldaman' ,`Details_loc2` = 'On a découvert récemment une nouvelle plante : les champignons magenta. Ils poussent en grappes au site de fouilles d\'Uldaman ; on les trouve dans les premiers tunnels et loin dans des zones moins explorées. Je veux étudier leur utilité éventuelle en alchimie, et c\'est là que vous entrez en scène.$B$BTrouvez les champignons magenta et rapportez-m\'en une douzaine. Attention, les grappes peuvent projeter des spores vénéneuses quand on les manipule !$B$BRendez-moi ce service et je sortirai pour vous un petit élixir revigorant de derrière les fagots.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Well done &lt;name&gt;, you\'ve proven your worth once again! &nbsp;The caps will be an interesting study, yon verily!$B$BAnd now, as promised, enjoy a taste of one of my more potent restorative brews from my personal stock...$B$BIf you are a skilled alchemist, then perhaps we might have some additional business to conduct. &nbsp;Regardless, I thank you for your assistance!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 17;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'épreuve du lion de mer' ,`Details_loc2` = 'Pour la seconde épreuve, vous aurez besoin des deux moitiés qui constitueront le Pendentif du lion de mer. L\'une des moitiés emprunte au lion de mer son agilité à glisser à travers les eaux. L\'autre moitié emprunte à l\'animal son incroyable endurance. Les druides doivent revêtir ces deux aspects pour pouvoir adopter leur forme aquatique.$B$BAllez parler aux résidents de Reflet-de-Lune pour apprendre où les deux parties se trouvent, puis rapportez-les ici, afin de pouvoir les joindre ensemble. Quand vous aurez formé le pendentif, apportez-le à Dendrite Brasétoile.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You\'ve completed the Trial of the Sea Lion, $N - congratulations.$B$BEach task in acquiring the pendant shows that both agility and endurance are necessary to act in harmony with what you desire to do underwater. &nbsp;Neither can exist without the other, and both cannot be done without your willingness to embrace the aspect of the sea lion.$B$BRemember these lessons well, and count on them once you have gained your aquatic form.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 30;
UPDATE `locales_quest` SET `Title_loc2` = 'Doux whisky' ,`Details_loc2` = 'Fluide suave et clair, comme des larmes d’enfant$BQui une belle teinte ambrée acquiert avec le temps$BAvant que nous puissions te boire à satiété$BUn fût de chêne noirci il nous faut fabriquer.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Aged in the barrel, filtered through coal$BIt passes your lips and right through your soul.$BSo lets us drink now, a toast to you$BAnd me while we\'re at it, Grimbooze Thunderbrew!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 53;
UPDATE `locales_quest` SET `Title_loc2` = 'La loi de Salomon' ,`Details_loc2` = 'Un ennemi de Hurlevent s\'est installé dans les collines à l’est des Carmines. D’infâmes voleurs utilisent les Arcanes pour répandre la terreur et la misère sur notre ville et ses habitants. Derrière cela, il y a une personne, Morganth ; c\'est lui qui dirige cette entreprise maléfique.$B$BMorganth contrôle maintenant le clan des gnolls Sombrepoil, qu’il utilise pour ses exactions. De par la loi de Comté-du-Lac, aider un ennemi du royaume est passible de la peine de mort.$B$BCes infâmes gnolls doivent être tués !$B$BRapportez-moi 10 pendentifs sombrepoil, et vous serez $Grécompensé:récompensée.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'The Court of Lakeshire recognizes your commitment to justice, &lt;name&gt;. &nbsp;By enforcing the laws of the Kingdom you have helped uphold the justice of the land.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 91;
UPDATE `locales_quest` SET `Title_loc2` = 'La vengeance de Sven' ,`Details_loc2` = 'Je travaillais dans une ferme, au sud-est... Des cavaliers noirs du défilé de Deuillevent sont venus dans ma ferme et ont massacré toute ma famille alors que j\'étais absent !$B$BQuand je suis rentré, j\'ai vu une silhouette indistincte rôder près de ma grange, en train d\'enterrer quelque chose. Il s\'est enfui avant que j\'aie pu l\'attraper, et je n\'ai pas pu m\'attarder, car je suis parti aussitôt à sa poursuite. Du coup, je n\'ai pas vu ce qu\'il avait caché.$B$BSi vous pouviez découvrir ce qu\'a enterré le cavalier noir, je vous en serais reconnaissant. L\'endroit exact est derrière la vieille souche d\'arbre près de la grange.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'A mound of dirt is here, hastily piled up as if someone had need of hiding something...without much time to do it.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 95;
UPDATE `locales_quest` SET `Title_loc2` = 'La folie d\'Arugal' ,`Details_loc2` = 'Mon mépris pour ce malheureux fou d\'Arugal augmente à mesure que je déchiffre son enchantement. J\'ai presque fini mes recherches pour y parvenir.$B$BMa compréhension sera totale quand j\'aurai compris ce qui cause l\'étrange comportement des habitants de Bois-du-Bûcher. Le jour, les paysans sont des gens normaux. Mais la nuit venue ils se transforment en worgens Ragelune.$B$BJe dois puiser l\'énergie des chaînes qu\'a enchantées Arugal. Rapportez-moi six chaînes enchantées de Bois-du-Bûcher, $N.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Your persistence is commendable. &nbsp;With the knowledge you helped me gather, Arugal will fall and his reckless magic shall be undone. &nbsp;Cheers to you, &lt;name&gt;.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 99;
UPDATE `locales_quest` SET `Title_loc2` = 'Le Totem d\'affliction' ,`Details_loc2` = 'À mesure que la corruption mystique rampe à travers la forêt, la nécessité d’une protection devient indispensable, $N. Les vents m\'ont murmuré qu\'un grand danger vous guette dans un avenir proche.$B$BSi vous voulez vous garder de ce danger, noble $C, apportez-moi 10 Crocs de goule, 10 Doigts de Squelette et 5 Fioles de venin d\'araignée. J’enchanterai alors un Totem d\'affliction qui blessera ceux qui tenteront de vous agresser.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Very wise of you, &lt;name&gt;. &nbsp;This Totem of Infliction will help to protect you against the evil which lurks throughout Azeroth and beyond. &nbsp;When you feel threatened, hold this totem and take comfort in its magical aura. &nbsp;Be brave, fair &lt;class&gt;. &nbsp;You will need all the courage you can muster for I see much bloodshed in your future.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 101;
UPDATE `locales_quest` SET `Title_loc2` = 'Menace sur la côte' ,`Details_loc2` = 'Quand ma vie a pris fin sur ces rochers, je ne savais pas que je pouvais espérer quelque chose après la mort. Cette nuit-là, le phare était éteint parce que le Vieux Troubloeil avait fait fuir la famille qui s\'en occupait. Mais les gens sont revenus et ont rallumé la flamme. Alors le Vieux Troubloeil a contraint ces faibles d\'esprit de murlocs à attaquer le phare. Cette fois-ci, la famille n\'a pas eu le temps de fuir, et elle a été massacrée par ces ignobles créatures, sans que personne ne puisse intervenir.$B$BTuez le Vieux Troubloeil si vous le rencontrez le long de la plage, et rapportez-moi une de ses écailles, je vous récompenserai.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'So the foul heathen, Murk-Eye is dead. &nbsp;Well done, &lt;name&gt;. &nbsp;By your hands one life was laid to rest but perhaps many more were saved. &nbsp;The Great Sea, wrought with danger as it is, shall be a wee bit safer this night thanks to your heroics.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 104;
UPDATE `locales_quest` SET `Title_loc2` = 'Régime sec' ,`Details_loc2` = 'Je suis dans une situation désespérée, $N. Mes réserves sont presque à sec. La dernière cargaison de boisson aurait dû arriver il y a bien longtemps. L\'invasion des orcs a été un enfer.$B$BPeut-être pouvez-vous travailler pour moi ?$B$BJ\'ai besoin que vous récupériez un tonneau de Tonnebière de chez Tristevin Tonnebière dans les collines de la marche de l\'Ouest, un tonneau de merlot de Hurlevent, une bouteille de tord-boyaux de Sombre-Comté et une outre de rhum doux de Comté-de-l’Or. Rapportez-les-moi et je ferai en sorte que vous soyez $Grécompensé:récompensée.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'A job well done, &lt;name&gt;! &nbsp;I know well the roads you had to journey to procure this fine array of liquor. &nbsp;But my patrons will be happy once again! $B$BMy wife is quite a seamstress. &nbsp;Please accept this cloak she made in return for your services.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 116;
UPDATE `locales_quest` SET `Title_loc2` = 'Les écailles de dragonnet' ,`Details_loc2` = 'J\'ai besoin d\'écailles ventrales de dragonnet noir pour payer les fers qu\'Argus m\'a envoyés de Comté-de-l’Or. Si vous pouviez m\'en trouver six, je réglerais ma dette... et il m\'en resterait suffisamment pour vous façonner quelque chose.$B$BLes dragonnets noirs errent souvent au sud de Comté-du-Lac, mais ils aiment aussi se balader. Vous devrez vous embusquer et les chasser quand vous les verrez.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Thanks! &nbsp;These will get me out of debt to Argus...'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 122;
UPDATE `locales_quest` SET `Title_loc2` = 'Le hurlement des gnolls' ,`Details_loc2` = 'Comme si les attaques des orcs ne suffisaient pas ! Maintenant j\'ai des brutes et des mystiques gnolls qui rôdent au nord de mon écurie et qui massacrent mes chevaux dès qu\'ils s\'éloignent...$B$BSi vous pouviez nous débarrasser de ces gnolls, mes chevaux et moi vous en serions très reconnaissants.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Thank you for all your help, &lt;name&gt;.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 124;
UPDATE `locales_quest` SET `Title_loc2` = 'Les outils perdus' ,`Details_loc2` = 'J\'apprécierais vraiment votre aide, $N. La ville est assiégée, l\'approvisionnement devient difficile. On devait me livrer des outils par chariot depuis Comté-de-l’or, mais le pont a sauté. Nous avons alors mis les outils dans un bateau, mais les orcs l\'ont touché avec une catapulte. Évidemment, ma boîte d\'outils a coulé tout droit au fond du lac.$B$BRécupérez-la, $N, et je ferai en sorte que vous ne le regrettiez pas.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Excellent recovery, &lt;name&gt;! &nbsp;I never thought I\'d see these tools again.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 125;
UPDATE `locales_quest` SET `Title_loc2` = 'Criée aux poissons' ,`Details_loc2` = 'Le lac Placide est connu pour son poisson-lune. C\'est un poisson qui se vend bien et j\'ai justement besoin d\'argent !$B$BRapportez-m\'en 10 ; je paierai bien. Si vous ne savez pas pêcher, peut-être pourrez-vous en voler aux murlocs !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Pleasure doin\' business with you, matey!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 127;
UPDATE `locales_quest` SET `Title_loc2` = 'La Confrérie défias' ,`Details_loc2` = 'Ce que je vais vous dire peut me coûter la vie. Le gang défias est en train de préparer un gros coup. Il paraît qu\'ils se sont acoquinés avec des gnolls, des kobolds et même des gobelins.$B$BApportez ce message à Roidemantel. Il donne tous les détails de ce que je sais à ce sujet.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Bah! &nbsp;I should have let that scoundrel rot when I had the chance. &nbsp;But this information is crucial. &nbsp;Good work.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 132;
UPDATE `locales_quest` SET `Title_loc2` = 'Le trésor caché du capitaine Sanders' ,`Details_loc2` = 'Si vous lisez ceci, c\'est que le vieux capitaine Sanders repose sous six pieds de flotte. Mon trésor est à vous. Il vous suffit de suivre le jeu de piste.$B$BD\'abord, trouvez mon coffre. Il doit être à moitié enterré dans le sable, depuis le temps, sur la côte de la marche de l\'Ouest, à côté de l\'épave. Il y a des tas d\'épaves sur la côte, mais une seule ancre rouillée. Qui trouve cette ancre trouve mon coffre ! Et avec le coffre, l\'indice suivant !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'The footlocker slowly creaks open. &nbsp;Sand and water seem to be its only contents. But wait! &nbsp;A small crab scurries out with a clue to the treasure in his claws!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 136;
UPDATE `locales_quest` SET `Title_loc2` = 'Le trésor caché du capitaine Sanders' ,`Details_loc2` = 'L\'indice que vous trouvez est le suivant : « Bon travail, matelot ! Maintenant il va falloir cingler vers l\'est. À l\'est, vers le haut des falaises, à l\'est vers la route. Cherchez les ruines des vieilles cheminées, au bord de la route. Là, vous trouverez un vieux tonneau qui contient l\'indice suivant. »' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Good work, treasure hunter!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 138;
UPDATE `locales_quest` SET `Title_loc2` = 'La Confrérie défias' ,`Details_loc2` = 'La Guilde des Maçons est dirigée par un homme du nom d\'Edwin VanCleef. C\'est lui qui a rebâti Hurlevent après sa destruction par les orcs au cours de la Première Guerre. Apparemment, VanCleef et ses équipes n\'ont pas apprécié la façon dont ils ont été traités par le roi à la fin de la reconstruction. Ça pourrait expliquer une ou deux petites choses.$B$BJ\'ai rédigé un rapport détaillé pour ton maître, dans la marche de l\'Ouest. Apporte-le-lui tout de suite !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Edwin VanCleef. . .I know the name well. &nbsp;To think that a man so industrious and talented could turn into such a scoundrel unnerves me. &nbsp;I\'ll need further proof before I believe this.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 141;
UPDATE `locales_quest` SET `Title_loc2` = 'La Confrérie défias' ,`Details_loc2` = 'Nous devons débusquer les Défias de leur cache, $N. Mes éclaireurs ont rendu compte qu\'un Messager Défias a été vu sur les routes entre Ruisselune, la carrière de la côte de l\'Or et la mine Veine-de-Jango. Je veux que vous le capturiez. S\'il résiste, tuez-le, et apportez-moi tout ce que vous trouverez sur lui.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'This is indeed firm proof that VanCleef is in charge. &nbsp;Now all we need to know is  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  the Defias gang is hiding out.$B$BWe\'ve had a stroke of luck while you were gone. &nbsp;We captured a thief trying to steal Saldean\'s wagon. &nbsp;He has promised to lead us to the hideout in exchange for his life. &nbsp;I want you to defend the traitor so he can reveal the hideout. &nbsp;Return to me once you have uncovered the location.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 142;
UPDATE `locales_quest` SET `Title_loc2` = 'Un message pour Sombre-Comté' ,`Details_loc2` = 'Si je ne connaissais pas personnellement le magistrat Salomon, je penserais qu\'il a perdu la tête ! Envoyer les Veilleurs à Comté-du-Lac simplement parce que Hurlevent lui refuse son aide ? Nous avons dû créer le corps des Veilleurs justement parce que Hurlevent nous avait abandonnés au moment où cette horrible magie a commencé à se répandre. Si j\'envoie mes Veilleurs, notre région sera la proie du Mal avant même que les soldats n’aient atteint la lisière de la forêt.$B$BPortez cette lettre en réponse à votre maître à Comté-du-Lac.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'By the Light! &nbsp;The Stormwind Army retreated from Westfall. &nbsp;And the Guard no longer protects Darkshire? &nbsp;There is some treachery abound. &nbsp;How could this be so? $B$BOh I forgot to reward your service, &lt;class&gt;. &nbsp;Here are some coins. &nbsp;Now excuse me. &nbsp;Something is amiss in Stormwind and I must get to the bottom of it.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 146;
UPDATE `locales_quest` SET `Title_loc2` = 'La côte n\'est pas sûre' ,`Details_loc2` = 'Vous avez probablement remarqué toutes les épaves le long de la côte. La Grande Mer est une maîtresse cruelle, en vérité. La côte de la marche de l\'Ouest doit être nettoyée, afin de permettre aux marins de trouver leur chemin jusqu\'à nos plages en toute sécurité. Les murlocs sont déjà assez dangereux !$B$BTuez 7 Chasse-marées, 7 Guerriers, 7 Oracles et 7 Cours-la-côte et vous recevrez une récompense.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Well done, &lt;name&gt;. &nbsp;You have quite a knack for combat. &nbsp;Thanks to you the Coast of Westfall is a safer place.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 152;
UPDATE `locales_quest` SET `Title_loc2` = 'Adieu mon frère…' ,`Details_loc2` = 'Nous étions au plus profond d\'une mine dans la marche de l\'Ouest, cachés derrière un baraquement à Ruisselune. Impossible de savoir d\'où viennent ces voleurs puants... Bref, la mine s\'est effondrée sur nous. Je m\'en suis sorti mais les autres... Enfin... On n\'en a plus entendu parler.$B$BVous aurez peut-être plus de chance. Si vous arrivez à avoir des nouvelles de mon frère, je vous en serai reconnaissant. Il portait toujours son insigne de la Ligue des Explorateurs. Si vous ne le trouvez pas, mais que vous me rapportez cet insigne, cela me rendrait un peu de paix.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'My suspicions were correct. &nbsp;My poor brother.... Well, thank you for bringing some conclusion to this mystery, even if the conclusion was grim.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 167;
UPDATE `locales_quest` SET `Title_loc2` = 'À la recherche de cartes du syndicat des mineurs' ,`Details_loc2` = 'Nombre de mes amis ont péri ce jour terrible de l\'effondrement de la mine. Si vos pas vous conduisent dans cette partie de la mine de Ruisselune, voyez s’il existe des indices qui permettraient de les identifier. Si vous trouvez leurs cartes du syndicat des mineurs, rapportez-les-moi et je préviendrai leurs familles.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Thank you for collecting these, &lt;name&gt;. &nbsp;Your efforts will help bring peace to the dead in the wake of this tragedy. &nbsp;I will see to it that the families are notified.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 168;
UPDATE `locales_quest` SET `Title_loc2` = 'Worgens dans les bois' ,`Details_loc2` = 'Les ténèbres s\'étendent inexorablement sur le bois de la Pénombre. La quête de maître Carevin est l\'expulsion du mal et de l\'hérésie. Nos efforts protègent le peuple de Sombre-Comté.$B$BVous sentez-vous digne de nous rejoindre ?$B$BJe pensais comme vous, autrefois. Mais la suffisance des Veilleurs était pénible et j\'ai rejoint maître Carevin. Si vous voulez prouver votre valeur, les mots ne suffiront pas.$B$BTestez votre habileté contre les tisseurs d\'ombre Plaie-de-nuit, des worgens qui rôdent dans le verger Pourrissant et dans le bosquet de Clairbois (clair, mon œil !).' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Impressive, &lt;name&gt;. It would seem that you are capable enough to handle yourself. Perhaps a more suitable challenge could be found for one of your abilities.$B$BIt is curious though... The worgen seemed to just appear out of thin air. But then, with all the other strange creatures that have moved across our borders, I suppose it\'s only one of many concerns.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 173;
UPDATE `locales_quest` SET `Title_loc2` = 'Theocritus récupère son Pendentif' ,`Details_loc2` = '$GVous entendez une voix désincarnée...&gt;$B$BNe soyez pas &lt;effrayé:effrayée. Je suis Theocritus, grand mage de la tour d\'Azora de la forêt d\'Elwynn. Le pendentif que vous avez en main est un moyen de communication entre les gnolls Sombrepoil et leur maître Morganth.$B$BAprès des mois de recherches, j\'ai cru que moi aussi je pourrais communiquer grâce à ces pendentifs. Si vous pouvez entendre ce message, c\'est que mon sortilège est un succès.$B$BApportez-moi ce pendentif et je vous récompenserai.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You have my gratitude, &lt;name&gt;. &nbsp;I sent that message to the Shadowhide Pendants weeks ago and had believed it a failure. &nbsp;But it seems the spell did reach at least some pendants, after a significant delay.$B$BIn time I hope to learn how to send an attack through the pendants, to immobilize any Shadowhide Gnolls who wear them.$B$BBut before that is possible, I\'m afraid that much more research is required.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 178;
UPDATE `locales_quest` SET `Title_loc2` = 'La seconde rébellion' ,`Details_loc2` = 'Nous combattons désespérément le colonel Kurzen. Non seulement c’est un habile tacticien, mais nous nous battons à 1 contre 10. Pour survivre, nous devons retourner contre lui ses propres tactiques sournoises.$B$BInvestissez son camp, à l\'est, et tuez ses hommes. Le plus possible. Et ressortez avant que les renforts ne vous attrapent.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Bien joué, la mort de ces combattants ôte du poids de nos épaules.$B$BMais maintenant que vous avez une idée des forces contre lesquelles nous nous battons, voulez-vous toujours rejoindre nos rangs ?'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 203;
UPDATE `locales_quest` SET `Title_loc2` = 'Les masques rouges en soie' ,`Details_loc2` = 'Ce n\'est un secret pour personne que le signe de reconnaissance du gang des Défias est un masque rouge. Mais nous avons appris que la qualité du tissu dont est fait le masque détermine le rang de celui qui le porte au sein du gang.$B$BCela étant dit, je veux que vous éliminiez autant de Défias haut placés que possible. Vous en trouverez à coup sûr dans la cachette de VanCleef. Lorsque vous en aurez terminé, rapportez-moi 10 masques rouges en soie pour me prouver leur mort, je vous remettrai alors une récompense. Bonne chance, $N.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Well done, &lt;name&gt;! &nbsp;On behalf of The People\'s Militia I salute you for your efforts. &nbsp;Soon we will return this land to the people with help from brave souls like you.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 214;
UPDATE `locales_quest` SET `Title_loc2` = 'À la défense des terres du roi' ,`Details_loc2` = 'Nous devons protéger le Loch, $N ! Avec tant de soldats du roi combattant vaillamment sur des champs de bataille lointains, les forces manquent à l\'intérieur. Les troggs se déversent de la moindre brèche et constituent la menace principale qui pèse sur Forgefer. Ces répugnants mutants doivent être annihilés.$B$BNous avons besoin de vous, $Gbrave aventurier:courageuse aventurière, pour mener une expédition et semer le chaos dans leurs rangs. Tuez six troggs et six éclaireurs brisepierre, puis revenez me voir. Vous trouverez cette vermine dans les passes entre les montagnes, au sud.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Well done, &lt;name&gt;! &nbsp;You demonstrated uncanny courage on the battlefield. &nbsp;Our war against the Troggs will be won with efforts such as yours. &nbsp;You have served King Magni well.$B$BIf you fancy yourself well-suited for this kind of adventuring, I suggest you speak with Mountaineer Gravelgaw. &nbsp;The Captain has him assigned to a rough patrol that I am sure he can use some help with. &nbsp;Check with Gravelgaw just inside the tower.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 224;
UPDATE `locales_quest` SET `Title_loc2` = 'La Chasse d\'Orneval' ,`Details_loc2` = 'Votre attention ! Les terres sauvages d\'Orneval vous attendent tous !$B$BLa Horde s\'est établie dans les terres au nord des Tarides. Nos deux avant-postes : le Poste de Bois-Brisé et le Poste de la Grève de Zoram s\'efforcent bravement d\'apporter la gloire à la Horde ! Ceux et celles qui veulent faire montre de leur courage devraient y trouver conseil. De plus, Senani Cœur-de-tonnerre, au Poste de Bois-Brisé, directement au nord des Tarides, cherche en ce moment des candidats prêts à prendre part à une grande chasse en Orneval !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Welcome to the new frontier, $N. Ashenvale is a land of opportunity, one  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  a young &lt;class&gt; like yourself is able to find boundless chances to prove their mettle. Look around the outpost here, and be sure to travel out to the Zoram Strand, as the Horde has another outpost there as well.$B$BYour presence here tells me that you\'ve come to learn more about the hunt. Listen close, and I will gladly share with you what you need to know.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 235;
UPDATE `locales_quest` SET `Title_loc2` = 'Retour vers la Tremblote' ,`Details_loc2` = 'Et nous y voilà. Des gâteaux au « crabe » façon Grual.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Aha! &nbsp;Thank you, &lt;name&gt;. &nbsp;Those really hit the spot!$B$BAnd here, please let me pay you for these!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 240;
UPDATE `locales_quest` SET `Title_loc2` = 'À la défense des terres du roi' ,`Details_loc2` = 'Les montagnards disent que vous êtes &lt;courageux/courageuse&gt; et &lt;compétent/compétente&gt;, $N. Nous avons besoin de gens tels que vous ici. Le problème avec les troggs ne s\'arrange pas. Les réservistes ont été envoyés au front et nous sommes seuls. Mais à présent que nous avons &lt;un/une:c&gt; $C &lt;expérimenté/expérimentée:c&gt; comme vous, $N, nous allons voir de quoi vous êtes capable.$B$BAllez tuer huit chamans et huit briseurs d\'os brisepierre. Vous en trouverez à proximité des cavernes, dans les montagnes du sud. Nous allons voir si votre réputation est fondée, &lt;tueur/tueuse de troggs.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'I knew the second I laid eyes on you, $Gname&gt;, that you would make Ironforge proud. &nbsp;You have served King Magni well. &nbsp;Now that you\'ve proven yourself to be such a loyal aid to the Kingdom, perhaps you should talk with the Captain to see if he\'ll entrust you with a higher detail.$B<br :>I salute you, &lt;name.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 263;
UPDATE `locales_quest` SET `Title_loc2` = 'Piétinements de pattes' ,`Details_loc2` = 'Les gnolls poils-moussus ont vécu paisiblement dans les Paluns pendant longtemps, mais aujourd’hui leur nombre augmente. Les buissons et les fleurs du marais ne leur suffisent plus, et pour alimenter leurs feux, leurs haches crasseuses coupent trop de bois.$B$BL\'équilibre naturel n\'est plus respecté. Pour la survie des Paluns, le nombre de gnolls doit être réduit.$B$BTuez quinze gnolls poils-moussus et dix bâtards poils-moussus. Chassez-les au sud, près des nains de la Porte d\'Algaz. Lorsque vous aurez terminé, revenez me voir.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'By reducing the Gnolls\' number, you have tended to the health of the Wetlands. &nbsp;Although my faith in your people has been taxed in the past, $R, it may yet be restored.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 276;
UPDATE `locales_quest` SET `Title_loc2` = 'Feu interdit' ,`Details_loc2` = 'Les Poils-moussus coupent les arbres des Paluns et les brûlent pour se chauffer. Je ne le supporte plus !$B$BAllez leur prendre leurs silex bruts, les outils qu\'ils utilisent pour faire du feu. À l\'exception des simples gnolls et des bâtards, tous les Poils-moussus portent des silex.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = '... thank you. &nbsp;I will smash these flints and cast them into the sea, so that they may never again be used to burn living wood.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 277;
UPDATE `locales_quest` SET `Title_loc2` = 'Bas les pattes !' ,`Details_loc2` = 'La mine du Ru d\'argent à l\'est est épuisée depuis longtemps. Elle a été convertie en dépôt de stockage par la Ligue des mineurs, mais aujourd’hui les kobolds s\'y sont installés, et ils mettent leurs sales pattes sur nos bons vieux outils !$B$BNous chasserons bientôt cette vermine de là, mais avant d\'envoyer des guerriers qui feront forcément un peu de casse, la Ligue veut que quelqu\'un aille chercher son équipement. Ce ne sera pas facile, vous pourriez avoir besoin de l\'aide d\'alliés.$B$BL\'équipement de mineur est stocké dans les caisses de la Ligue des mineurs un peu partout dans la mine. Bonne chance !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Did you see a lot of Kobolds in there? &nbsp;I can\'t wait for the order to clear them out of the mine. &nbsp;The thought of their grimy hands clawing around our mine makes my stomach rumble!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 307;
UPDATE `locales_quest` SET `Title_loc2` = 'La clé cachée' ,`Details_loc2` = 'Bien qu\'il ne soit jamais fait mention du soldat Thorsen dans ces rapports, une petite clé en cuivre était cachée dans un des registres d\'Hérode.$B$BEt en poussant plus loin l\'étude de ces notes, on trouve au milieu de divers papiers des références à un « coffre-fort sécurisé ». Il est quelque part dans la Réserve, l\'ensemble de cavernes qui se trouve derrière la base de Kurzen.$B$BSi nous retrouvons ce coffre-fort et que la petite clé peut l\'ouvrir, nous résoudrons peut-être le mystère de Thorsen.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Opening the strongbox, it\'s clear that this is  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  Herod keeps his more valuable coins and items.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 328;
UPDATE `locales_quest` SET `Title_loc2` = 'Frère Paxton' ,`Details_loc2` = '« Les Tensions du fer » ? Oui, je connais ce livre. Excellent ouvrage, écrit par le mineur nain Margulf Blaggon il y a quelques décennies. Il a passé l\'essentiel de sa vie au cœur des montagnes de Khaz Modan, à creuser pour trouver du minerai. Chic camarade !$B$BMais où en étais-je ? Ah oui ! « Les Tensions du fer » ! J\'ai bien peur que le livre ait été envoyé à l\'abbaye de Comté-du-Nord. Le bibliothécaire de l\'abbaye, le frère Paxton, voulait faire une copie de l’ouvrage pour sa propre collection.$B$BMais c\'était il y a plusieurs mois. Je me demande pourquoi ça lui prend si longtemps...' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Mr. Sheaf is wondering  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  his book is? &nbsp;Well this is a tad embarrassing. &nbsp;I have the book, but I\'m still making my copy and I\'m not quite ready to give the original up!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 344;
UPDATE `locales_quest` SET `Title_loc2` = 'Minerai de rethban' ,`Details_loc2` = 'Le minerai de rethban a été découvert par Bart Rethban. Lorsqu’il est fondu, ce minerai est moins dur que le fer, mais il possède une essence que certains scribes utilisent comme encre. Il peut être trouvé dans les Cavernes de Rethban, plus au nord.$B$BNous en avons normalement en réserve, mais devant nous préparer aux attaques des orcs, nous n\'avons pas eu le temps d\'en collecter dernièrement. De plus, les gnolls des Carmines occupent maintenant les cavernes de Rethban et leurs manouvriers y exploitent le minerai eux-mêmes.$B$BSi vous voulez en collecter, vous devrez le faire par vos propres moyens.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Oh, splendid! &nbsp;I can use this ore to make my dye, and then can finish copying The Stresses of Iron.$B$BThank you, &lt;name&gt;!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 347;
UPDATE `locales_quest` SET `Title_loc2` = 'Le point de vue d\'un vieil ami' ,`Details_loc2` = 'Ce Marzon va contacter un Défias et il est lié à Lescovar...$B$BEn ayant tué VanCleef et Thredd, il va être difficile de traîner en justice Lescovar, vu que vos preuves ne valent rien sans leurs témoignages. Sans compter que Lescovar est un noble et qu\'il a de nombreux appuis. Il est ainsi au-dessus des lois.$B$BVous avez besoin d\'une autre aide. Je répugne à le faire, mais voici quelqu\'un que je connais et qui peut nous aider. Vous trouverez Trias dans le Quartier commerçant de la cité.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Vous plaisantez ? Shaw veut encore que je l\'aide ? Quel culot...$B$BLa confrérie Défias ? Eh bien, vous êtes sûrement porteur de nouvelles merveilleuses ? Bientôt vous allez me dire que l\'Aile de mort est toujours en vie et en train d\'attaquer la cité.$B$BBon, ça va, racontez-moi tout depuis le début. Et surtout n\'omettez aucun détail. Je dois tout savoir.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 350;
UPDATE `locales_quest` SET `Title_loc2` = 'La lettre non envoyée' ,`Details_loc2` = 'En fouillant le corps d\'Edwin VanCleef, vous trouvez parmi d\'autres objets une lettre non envoyée. Elle est adressée à Baros Alexston, architecte de la cité de Hurlevent, Hôtel de ville, place de la Cathédrale.$B$BElle a l\'air récemment écrite et scellée.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Edwin VanCleef, you say? I would as much expect a letter from my dead grandmother... So you\'ve killed him then? Pardon me in saying, but I\'m somewhat surprised. He was a peerless fighter in his younger years. Let\'s see what he finds the need to write to me about after so many years.$B$B$GBaros peruses the letter.&gt;$B<br :>Edwin... I see the years haven\'t changed you a bit, an idealist as ever, and a romantic. He doesn\'t care who he hurts, &lt;name. Revenge has consumed him. But then, I\'m not sure I can blame him.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 373;
UPDATE `locales_quest` SET `Title_loc2` = 'Une audience avec le roi' ,`Details_loc2` = 'J\'ai préparé un rapport pour le roi. Je crois que vous devriez le lui remettre, en même temps qu\'une explication de vos découvertes. N\'ayez crainte, il vous récompensera sûrement pour vos actions.$B$BVotre audience est prévue pour bientôt, vous devriez vous hâter, il y a du chemin jusqu\'au donjon de Hurlevent.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You are to be commended for the service you have done for Stormwind, $Gname&gt;. If VanCleef had been successful in his machinations, he would no doubt have done considerable harm to the kingdom--something we must obviously avoid.$B<br :>Take this, &lt;class, as a token of the my appreciation.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 396;
UPDATE `locales_quest` SET `Title_loc2` = 'Chasse au rat' ,`Details_loc2` = 'Nous avons vraiment un problème de vermine à l\'ouest. Les rats des tunnels kobolds sont venus dans les collines, ont creusé leurs terriers et contaminé notre terre avec leur pestilence !$B$BNous voulons les éliminer. Chassez-les et rapportez-moi leurs oreilles pour la récompense.$B$BVous trouverez les tunnels des kobolds et leurs terriers à l\'est, dans la mine du Ru d\'argent.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Ugh! &nbsp;When I asked for those ears I didn\'t realize they\'d smell so bad!$B$BHere\'s your reward, &lt;name&gt;. &nbsp;Well done.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 416;
UPDATE `locales_quest` SET `Title_loc2` = 'Les boudins de Thelsamar' ,`Details_loc2` = 'On n\'a jamais manqué d\'estomacs vides à Thelsamar, avec les gamins qui courent partout et les ouvriers qui reviennent de leur journée de travail après un dur labeur. Nos boudins sont réputés, vous les avez déjà essayés ?$B$BNon ? Eh bien, par ici, il faut travailler pour mériter sa pitance et ce n\'est pas parce que vous êtes bien $Ghabillé:habillée que l\'on fera une exception pour vous, $C.$B$BTout ce qu\'il me faut, c\'est de la viande d\'ours. La préparation fera le reste. Trouvez-moi cela et laissez la cuisine à Vidra !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Trust me, it seems a lot harder than it looks, but...$B$BOh, I\'ve never known someone to take much interest in the making of sausages, but I\'ll give you the recipe just in case. Here you go, mind you eat them fresh!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 418;
UPDATE `locales_quest` SET `Title_loc2` = 'Raleigh et Fossoyeuse' ,`Details_loc2` = 'Deliah avait un mari, Raleigh, qui maintenant habite à Fossoyeuse. Si la Deliah qui vivait à Brill est bien celle à qui appartient la bague, alors Raleigh le saura.$B$BRaleigh est dans le quartier des Marchands à Fossoyeuse.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Yes, this ring was my wife\'s. &nbsp;Her fate was the fate of all who fail to endure these harsh times. &nbsp;I feel nothing for her death, nor for the story you tell of her body\'s abduction.$B$BSuch feelings have long since grown cold within me. &nbsp;But desire does still burn in my dead heart, &lt;name&gt;.$B$BDesire for revenge.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 441;
UPDATE `locales_quest` SET `Title_loc2` = 'Reposer en… pièces' ,`Details_loc2` = 'Vous n\'avez pas l\'air d\'un Poil-putride. C\'est déjà ça. J\'avais perdu espoir...$B$BMon nom est Alaric, j\'étais naguère le serviteur et garde du corps de Thule. Il a tenté d\'utiliser sa magie pour me « protéger » du Fléau mort-vivant, mais en fait, son sort m’a donné une forme de mort-vivant. Après avoir créé les maudits Poil-putride, Thule m’a décapité et donné ma tête aux gnolls pour qu\'ils jouent avec !$B$BJe vous supplie de me ramener près de mon corps. Je suis sûr que les gnolls l\'ont enterré ici, au donjon de Fenris, près des écuries, dans une tombe anonyme.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Thank you, &lt;name&gt;. &nbsp;I had feared the Gnolls destroyed my body. &nbsp;It is good to know they did not.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 460;
UPDATE `locales_quest` SET `Title_loc2` = 'La manœuvre de Nek\'rosh' ,`Details_loc2` = 'Pendant que vous combattiez les Gueules-de-dragon, nous avons percé à jour certains de leurs plans.$B$BNek\'rosh a fait construire des catapultes et projette d\'assiéger Menethil ! Et avec les combats à Dun Algaz, nous sommes coupés des renforts !$B$BVous devez détruire ces catapultes. Elles sont au campement des Gueules-de-dragon.$B$BVoici, prenez ce combustible. Taillez-vous une route à coups d\'épée jusqu\'aux catapultes, puis brûlez-les à l\'aide du combustible.$B$BAprès, trouvez et tuez Nek\'rosh. Rien ne nous plairait plus que d\'avoir la tête de cette engeance Gueule-de-dragon au bout d\'une pique...' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You place some of the tinder on the catapult then strike a spark onto it.$B$BFlame erupts and quickly spreads!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 465;
UPDATE `locales_quest` SET `Title_loc2` = 'Enquête à Moulin-de-l\'Ambre' ,`Details_loc2` = 'Dalar essaie de localiser la source de la magie des sorciers. Pour l\'heure, nous devons nous efforcer de ralentir leurs progrès...$B$BIl ne fait aucun doute que les conjurateurs, les mages et les protecteurs portent ces pendentifs. Récupérez-en.$B$BSuivez la route principale au sud, puis prenez à l\'est à l\'embranchement qui conduit à Moulin-de-l\'Ambre.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Dalar has found that the weaving is quite far along, despite the setback.$B$BA wizard of great power is guiding the magic energy. He must be stopped quickly.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 479;
UPDATE `locales_quest` SET `Title_loc2` = 'Valik' ,`Details_loc2` = 'Eurrgh… Blessé et mourant, me voilà en face de l\'ennemi.$B$BVous me tuerez sans doute de toute façon, mais si vous m\'apportez de la bière brune d’Austrivage... Les gens de votre race ne boivent pas, non ? Je vous dirai ce que vous voulez savoir.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Given my impending death, I think I can safely say that this is the sweetest tasting thing ever... Here, take this, won\'t do me any good when I\'m dead.$B$BWell, get on with it!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 535;
UPDATE `locales_quest` SET `Title_loc2` = 'Les éclats vibrants' ,`Details_loc2` = 'Les légendes parlent d\'un bijou, caché loin au cœur de Strangleronce. Nous l\'appelons la vue de l\'esprit, et il détient de grands pouvoirs. Si vous trouvez ce bijou, alors, peut-être, nous pourrions ramener Yenniku des ténèbres.$B$BJe vais parler aux esprits de la jungle pour localiser la vue de l\'esprit. Tandis que je m\'en occupe, allez dans la Mine aux cristaux, au sud-est d\'ici, et récupérez des éclats vibrants bleus des basilics gueule-de-fer. Ces éclats sont rares, mais nous aurons besoin de leur magie pour faire plier la vue de l\'esprit à notre volonté.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You have done well, &lt;name&gt;. &nbsp;These shards are well formed. &nbsp;Already I hear them sing to me.$B$BAnd while you were gone, I spoke with the spirits of the jungle. &nbsp;I know  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  the Mind\'s Eye rests...$B$B...And who guards it.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 589;
UPDATE `locales_quest` SET `Title_loc2` = 'Un marché de voleur' ,`Details_loc2` = 'Quoi ?$B$BQuoi ?$B$BVous voulez de l\'argent ? Je ne vous dois pas un sou.$B$BUne lettre ? Quelle lettre ? Oh ! Vous voulez vous battre, alors ? Très bien, $Gmon pote:ma fille… Allons-y ! Je vous dois de l\'argent…' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Incredible! What skill!$B$BThought you\'d be easy prey, &lt;name&gt;. Got me good, you did.$B$BHere\'s the coin I promised... not as much as I said it\'d be, but that\'s on a count o\' me lying to ya in the first place.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 590;
UPDATE `locales_quest` SET `Title_loc2` = 'Les Colliers d\'os ensanglantés' ,`Details_loc2` = 'J\'ai été envoyé à Grom\'gol pour aider Nimboya dans sa quête, mais j\'ai aussi des raisons personnelles d\'être là.$B$BLa tribu des Scalps-rouges est l\'ennemie de la nôtre. Collecter les trophées de nos ennemis nous donne du pouvoir sur eux. Vous pouvez aider notre tribu en chassant nos ennemis.$B$BIls vivent au nord. Massacrez des trolls scalps-rouges et prenez leurs Colliers d\'os ensanglantés. Trouvez-en beaucoup, et grande sera votre récompense.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Ah, very good. &nbsp;I will boil these, and leech them, and the spirits of their owners will weaken as I render their necklaces to paste!$B$BHere, &lt;name&gt;. &nbsp;This will repay my debt to you.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 596;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'analyse magique' ,`Details_loc2` = 'D\'après ce que j\'ai entendu, les mages de Dalaran ont réussi à reconstruire leur ville magique et à la déplacer en Norfendre, laissant derrière eux un énorme cratère. Mais quelques mages sont tout de même restés, et ils seront ravis de prendre un peu de temps pour examiner les bracelets. S\'il y a une chose que je sais à propos des mages, c\'est qu\'ils ne peuvent résister à une énigme magique.$B$BPrenez le sac de bracelets d’élémentaires d\'eau et voyez s\'ils peuvent apporter quelque lumière sur la malédiction de la pierre des marées. Cherchez l\'archimage Ansirem Tisserune. J\'ai déjà eu affaire à lui par le passé, et grand est son savoir.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'The Stone of the Tides? Most interesting. It might surprise you to know that I wrote a tome--several years ago--pertaining to the legends of the Gurubashi Empire. I do know quite a bit about the Stone.$B$BAt least one of these bracers appears to have some magical residue I can analyze.$B$BLet\'s see what I can see...'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 602;
UPDATE `locales_quest` SET `Title_loc2` = 'Les boucaniers de la Voile sanglante' ,`Details_loc2` = 'Maudit soit le toupet de Firallon ! Il ne pourrait même pas diriger une yole sur le lac de Cristal, et encore moins mener une flotte entière contre nous.$B$BLes boucaniers de la Voile sanglante vont payer leur négligence. À présent que nous connaissons leur plan d\'attaque, nous pouvons les contrer avant même qu\'ils ne sachent ce qui les frappe.$B$BMais pourquoi envoyer la flotte entière faire le travail d\'un courageux groupe d\'aventuriers ? $N, dirigez certains de vos meilleurs camarades au mouillage de Firallon et assassinez les dirigeants de la Voile sanglante. Leur flotte coulera peu après.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Ah, what a great day for the Blackwater Raiders! &nbsp;Three cheers to you, &lt;name&gt;!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 608;
UPDATE `locales_quest` SET `Title_loc2` = 'Gages vaudou' ,`Details_loc2` = 'Ma liste se réduit, mais certains me doivent encore de l\'argent.$B$BAprès, il y a Maury « Pied bot » Wilkins, Jon-Jon le Corbeau et Chucky « Dix pouces ».$B$BLes dettes de ces chiens remontent à des mois et je pensais qu\'ils avaient quitté la ville pour éviter de payer ! Plus tard, j\'ai appris qu\'ils étaient maudits et ensorcelés. À présent, ils courent la jungle. Mais je me fiche de leur sort, je veux récupérer ce qui m\'appartient !$B$BIls se trouvent dans les ruines d\'Aboraz et dans les ruines de Jubuwai, au nord-est. Trouvez-les, et rapportez-moi mon dû.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Great! &nbsp;I can get a good price for these, especially for what\'s inside Maury\'s foot! &nbsp;Thanks much, &lt;name&gt;!$B$BI hope those three learned their lesson: don\'t bet something you can\'t afford to lose.$B$BAnd maybe it\'s too late for them, but...they should be careful of who they gamble with.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 609;
UPDATE `locales_quest` SET `Title_loc2` = 'Allécher Negolash' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You pile food and drink upon the raft...'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 619;
UPDATE `locales_quest` SET `Title_loc2` = 'Le récif infâme' ,`Details_loc2` = 'Il y a longtemps, un grand tremblement de terre engloutit une ancienne cité trolle sous les eaux de la Côte sauvage au nord-ouest. Nous appelons cet endroit le Récif infâme, car ce sont les murlocs qui à présent résident dans les ruines de la cité, attaquant tous ceux qui s\'en approchent.$B$BUne vieille tablette repose parmi ces ruines, racontant l’histoire du héros troll Gri\'lek. Cette légende est sacrée pour la tribu Sombrelance, et bien que la tablette soit trop lourde pour qu\'on puisse la bouger, j\'en veux un fragment pour le conserver dans notre nouvelle demeure, à Orgrimmar.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'This is good. &nbsp;When I return to Orgrimmar I will place this shard in a sacred place. &nbsp;It will remind us of the might and honor of Gri\'lek.$B$BThank you, &lt;name&gt;. &nbsp;Your deeds mark a growing friendship with the Darkspear tribe.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 629;
UPDATE `locales_quest` SET `Title_loc2` = 'Le viaduc de Thandol' ,`Details_loc2` = 'Une explosion monstrueuse a grondé à travers les collines. C\'tait horrible, j\'vous l\'dis !$B$BDe bons soldats sont morts en défendant le viaduc de Thandol. Tresse-longue dit que nous prendrons notre revanche sur les Sombrefers avant que tout cela ne soit fini.$B$BIci, il n’y a que des blessés, et je ne vois aucun renfort arriver à l\'horizon. Tresse-longue a envoyé le vieux Bouclerouille en reconnaissance au pont à l\'est, pour voir si nous pouvions le sécuriser.$B$BC\'était il y a plusieurs jours. Avec Dun Modr en état de siège, nous n\'avons pas les ressources d’aller le chercher.$B$BPeut-être pourriez-vous le trouver...' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You search the tiny, dwarven corpse. &nbsp;Engraved into the helmet you can clearly read: Ebenezer Rustlocke.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 631;
UPDATE `locales_quest` SET `Title_loc2` = 'Le viaduc de Thandol' ,`Details_loc2` = 'Il semblerait que le pauvre vieux Bouclerouille soit tombé dans une embuscade de Sombrefer. Vous remarquez un parchemin froissé dans son poing serré.$B$BApportez la triste nouvelle à Rhag Garmaçon et montrez-lui ce que vous avez trouvé.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Oh my, the news is grave....'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 632;
UPDATE `locales_quest` SET `Title_loc2` = 'Trol\'kalar' ,`Details_loc2` = 'La lumière s\'éteint, doucement, et l\'épée Trol\'kalar, reposant dans un fourreau de pierre, est libérée.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'For generations, the forces of Strom and Arathor ravaged the trolls. Now their greatest weapon is in our hands. Our inching progress in the steamy forests of Zul\'Gurub will be no more.$B$BYour assistance has been great, &lt;name&gt;, and the Darkspear trolls are indebted to you.$B$BI will be sure that the sword is delivered to Nimboya at Grom\'gol.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 646;
UPDATE `locales_quest` SET `Title_loc2` = 'La récupération de Houle' ,`Details_loc2` = 'L’elfe que vous devez consulter s\'appelle Gilveradin Courresoleil. Il peut identifier n\'importe quel vin ou cuvée, uniquement à l\'odorat ! Il peut emballer la Houle dont nous avons besoin, et c\'est lui que vous devrez voir. Nous avons besoin de six bouteilles pour la livraison, de deux pour papa, et d\'autant de bouteilles que l\'elfe en voudra... Et il en voudra, croyez-moi ! Lorsque tout sera prêt et que vous aurez deux bouteilles pour papa, revenez me voir.$B$BLe camp de Gilveradin est proche d\'un gîte de hauts-elfes dans les Hinterlands, et ils ne sont pas commodes. Croyez-moi.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Ah, so you know Malton? &nbsp;He is an old drinking companion of mine. &nbsp;You see, I have found something that transcends the boundaries of artificial treaties and alliances - alcohol. &nbsp;Were we all to slake our thirsts on its blissful qualities, we all would be in a much better place spiritually.$B$BAlas, something tells me that your visit here is not to learn of alcohol\'s redeeming values, now is it.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 650;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'éveil de l\'esprit' ,`Details_loc2` = 'Tenez, donnez cette amulette à Gor\'mul, et regardez si la force des raptors peut ranimer la flamme guerrière qui sommeille en lui.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'What is this? Polished bone. What use have I for polished bone? Will a trinket restore the Horde to its glory?'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 674;
UPDATE `locales_quest` SET `Title_loc2` = 'Menace sur le Trépas-d\'Orgrim' ,`Details_loc2` = 'Mauviettes... Tous autant qu\'ils sont. Vous, $C, qu\'est-ce que vous regardez comme ça ? Vous pensez être quelqu\'un de spécial, hein ? Pourquoi ne le prouvez-vous pas ?$B$BL\'avant-poste de Trépas-d\'Orgrim dans les hautes-terres Arathies est attaqué. À moins qu\'on leur envoie des renforts rapidement, leur défaite est imminente.$B$BMais avant de vous précipiter là-bas, j\'aimerais vous faire passer un test. Au nord-est de Stromgarde, dans les hautes-terres Arathies, il y a une colline habitée par la tribu Rochepoing. Si vous pouvez vous occuper de ces ogres et de leurs massacreurs, allez ensuite faire votre rapport à Drum Lagangrène, à l\'avant-poste du Trépas-d\'Orgrim.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'So, you think you\'re strong enough to help Hammerfall? So be it.$B$BTallow either sent you to that ogre mound in the hopes that you would kill yourself or he thinks you\'re strong enough to aid us. Either way, you\'re here now and I am in need of more soldiers in our fight against the constant attacks.$B$BIf you plan on staying in Hammerfall then get some rest, and come speak to me again when you\'re ready. There\'s no shortage of fighting to be had.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 676;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'appel du combat' ,`Details_loc2` = 'Les trolls fanécorces et les ogres rochepoings arathis se sont alliés pour nous éradiquer de cette terre, mais nous ne mourrons pas de leurs mains, ni des mains de personne. Nous allons les tuer et leur montrer que nous aussi possédons notre demeure ici.$B$BLes trolls nous ont refusé leur aide et les ogres, si nous les laissions faire, se serviraient de nous comme nourriture et comme literie. Cela ne sera pas !$B$BNous commençons notre attaque sur les trolls fanécorces, au sud. Tuez-les en masse, et ne revenez que lorsque leur camp sera rempli de leur sang.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'The signs of war are upon your garments, and sweat drips from your brow. Good, very good indeed.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 677;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'appel du combat' ,`Details_loc2` = 'La tête de la bête est prête à tomber... avec grand fracas, $N. Vous avez définitivement prouvé votre valeur au cours de cette dernière bataille. Les commanditaires de ces attaques sont installés dans les ruines de Stromgarde, symbole de la faiblesse humaine.$B$BTrouvez et tuez les seigneurs rochepoings et leurs laquais chamans, qui essayent de nous repousser hors de nos terres.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You do your people proud, &lt;name&gt;. The threat has been greatly lessened, and I am sure we will begin to thrive here in the Highlands.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 679;
UPDATE `locales_quest` SET `Title_loc2` = 'La réelle menace' ,`Details_loc2` = 'Vous avez bien servi mon mari, $C, mais il ne connaît pas la véritable cause des attaques contre le Trépas-d\'Orgrim.$B$BOr\'Kalar, le valet de Mug’Thol à Stromgarde, déteste mon mari, et souhaite sa mort depuis qu\'il a été vaincu par lui quelques années auparavant.$B$BJ\'avais le pressentiment qu\'Or\'Kalar tendait un piège à mon mari. Je lui ai donc caché les indices comme quoi il était à l\'origine de ces attaques.$B$BSinon mon mari aurait foncé tête baissée dans le piège… Je voudrais que vous tuiez cette créature et que vous me rameniez sa tête.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You have done me a great service, &lt;name&gt;. Know that my husband\'s life and the safety of Hammerfall has been protected for another day because of your deeds. Please take this as a reward for your courage.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 680;
UPDATE `locales_quest` SET `Title_loc2` = 'On recherche : Marez Capuche' ,`Details_loc2` = 'La tête de Marez Capuche a été mise à prix. La dernière fois que cette conseillère de haut niveau du Syndicat a été aperçue, c\'était dans la zone de Stromgarde contrôlée par le Syndicat.$B$BLa prime peut être réclamée au capitaine Nials.$B$BAttention: Marez est une démoniste rusée et très adroite, et il est de notoriété publique qu\'elle fricote avec les démons. Débusquez-la avec une extrême prudence.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You took down Marez Cowl? &nbsp;Well done, &lt;name&gt;. &nbsp;Well done indeed!$B$BMarez menaced our Stromgarde defenders, and it is rumored she was an emissary between the Syndicate and some other, darker power. &nbsp;We are glad to see that she\'ll no longer ply her powers against us.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 684;
UPDATE `locales_quest` SET `Title_loc2` = 'Hommage royal' ,`Details_loc2` = 'Donc mon bon roi veut construire un monument en l\'honneur du soldat Sully Balloo ? Et vous me dites que le pauvre homme est mort lors de la tragédie du viaduc de Thandol ?$B$BN\'y a-t-il jamais eu la paix en Azeroth ?$B$BQuoi qu\'il en soit, les ordres du roi sont prioritaires. Pour construire un monument grandiose et durable, j\'ai besoin d\'une pierre spéciale, d\'une grande finesse. Allez sur la colline de Darrow à Hautebrande et rapportez-moi du granit d\'Alterac.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Perfect! Better than I could have imagined!$B$BAnd I have quite an imagination you know...'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 689;
UPDATE `locales_quest` SET `Title_loc2` = 'La demande de Malin' ,`Details_loc2` = 'Bonjour, $N. Vous avez l\'air d\'être $Gun aventurier:une aventurière, et j\'ai un petit travail qui doit être fait.$B$BL\'archimage Trelane, mon ancien maître, a fui depuis peu Stromgarde dans les hautes-terres Arathies, et il m\'a demandé de récupérer quelques objets qu\'il n\'avait pas pu emporter.$B$BLa cité assiégée est tombée, bien qu\'il ait essayé de la défendre. J\'ai déjà des agents sur les Hautes-terres, mais ils ont besoin de renfort. Cela vous intéresse ?' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'So, Malin sent ye? I don\'t care if Muradin rose up from the dead and whisked ye here on a dragon. I\'m busy! Talk to Kryten if you need somethin\'.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 690;
UPDATE `locales_quest` SET `Title_loc2` = 'Valoir son poids en or' ,`Details_loc2` = 'L’archimage Malin nous a envoyés, Skuerto et moi, collecter des ingrédients. Maintenant que vous êtes là, je sais que je n’aurai pas de problèmes pour trouver ces objets.$B$BJ’ai besoin des objets suivants : des défenses de Fanécorce, des bourses-médecine fanécorces et un couteau de chasseur des ombres.$B$BPourriez-vous me rapporter ces objets ? Les Fanécorces vivent au sud-est d’Arathi.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Incredible! I can see why Malin asked you to come join us--very well done indeed!$B$BAt this rate, Skuerto and I will be headed back to Stormwind in no time at all.$B$BI believe my dwarven companion is back from scouting Boulderfist Hall; go speak to him when you\'re ready.$B$BIt looked as though he might have had a bit of a scuffle with the ogres. Don\'t mention that to him, though; he\'s very sensitive about criticisms to his skills at scouting.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 691;
UPDATE `locales_quest` SET `Title_loc2` = 'La ruse du raptor' ,`Details_loc2` = 'Prenez cet orbe et utilisez sa magie pour insuffler à Gor\'mul l\'essence des raptors. Cela va revigorer son esprit et lui redonner goût à la vie. Lorsqu\'il sera comme avant, nous lui ferons un résumé de l\'activité de la Horde, et il comprendra que pour nos incessantes batailles nous avons besoin d\'un grand guerrier.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'At least it is something interesting you bring to me this time. A strange power it emits...'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 702;
UPDATE `locales_quest` SET `Title_loc2` = 'Enchantements d\'éclats flamboyants' ,`Details_loc2` = 'En tant qu\'apprenti de Feranor Orteil-d\'acier, j\'ai appris à créer des armes dotées d\'enchantements flamboyants. La dernière fois que j\'ai entendu parler de lui, il travaillait dans une forge à Lordaeron.$B$BJe ne peux pas vous donner beaucoup de détails quant au processus, mais je sais qu\'il requiert du sang d\'un cœur de drake noir qui bat encore. Je n\'ai pas grand-chose à faire en attendant les ordres de Forgefer. Si vous me rapportez un tel cœur du ravin de Lethlor, je vous montrerai mes talents !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Ah yes, this will do excellently. This will enable your weapons to strike with searing heat!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 706;
UPDATE `locales_quest` SET `Title_loc2` = 'Le savoir perdu' ,`Details_loc2` = 'J\'ai le livre pour vous, $N. Je n\'ai pas besoin de vous demander d\'en prendre le plus grand soin. Et de le garder hors de portée de nos ennemis.$B$BCe livre contient de nombreuses vérités...$B$B... Et la vérité peut vous consumer.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Vous l\'avez ! Oh, l\'espoir revient !$B$BVos efforts vont peut-être nous sauver, $N. Ils dresseront peut-être un monument pour vous ! Et c\'est un autel qu’il faudrait ! Que votre nom soit loué durant des générations et des générations !$B$BSauf si... ILS sont libérés et reviennent à la surface. Si cela se produit vous pouvez oublier le monument et les louanges.$B$BS\'ILS sont libérés, nous serons uniquement bons à nourrir les vers.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 737;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'eau empoisonnée' ,`Details_loc2` = 'Des gobelins et leurs serviteurs ont pollué nos puits sacrés ! Nous ne pouvons tolérer cela.$B$BPour assainir les puits, je dois créer un totem de purification que vous devrez porter au puits, afin de pratiquer le rituel de purification.$B$BD\'abord nous allons fabriquer le totem de purification pour le puits Sabot-d\'hiver. Pour ce faire, le pays doit offrir son aide. Chassez des loups des prairies pour leurs pattes et des trotteurs des plaines adultes pour leurs serres. Vous les trouverez au sud-ouest.$B$BRevenez avec les pattes et les serres, $N.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You have done well, &lt;name&gt;. &nbsp;I will take these and bind their power into a totem of cleansing.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 748;
UPDATE `locales_quest` SET `Title_loc2` = 'Le totem de Corne-tonnerre' ,`Details_loc2` = 'Pour créer le Totem de purification de Corne-tonnerre, vous devez collecter les griffes des prédateurs qui hantent les plaines de Mulgore. Chassez les traqueurs des prairies et les pumas des basses plaines pour leurs griffes, puis revenez me voir.$B$BVous trouverez les traqueurs des prairies et les pumas des basses plaines à l\'est et à l\'ouest.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Thank you, &lt;name&gt;. &nbsp;The purity with which these beasts hunt is vital in the creation of the next cleansing totem.$B$BThe goblins will never learn the difference between contending with the land, and living in harmony with it.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 756;
UPDATE `locales_quest` SET `Title_loc2` = 'Le rite de force' ,`Details_loc2` = 'Les rites de la Terre-mère sont un passage obligé pour tout jeune tauren qui se respecte, aux Pitons-du-Tonnerre.$B$BLa première épreuve est le rite de Force. Vous devrez y démontrer votre bravoure en tuant des ennemis de la tribu.$B$BLes Dos-hirsutes du ravin de Roncelame à l\'est, empiètent sur nos terres tribales. Ils nous tendent des embuscades lors de nos parties de chasse et volent dans les villages, à la nuit tombée.$B$BProuvez votre valeur en massacrant ces vauriens et retournez voir le Chef, au Camp Narache, avec leurs ceintures pour prouver que vous les avez tués.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You have passed the first test of the Rites of the Earthmother. The tribe will be proud.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 757;
UPDATE `locales_quest` SET `Title_loc2` = 'Le rite de vision' ,`Details_loc2` = 'Pour passer le Rite de vision, vous devez collecter les ingrédients nécessaires à la réalisation de l\'Eau des Prophètes.$B$BJ\'aurai besoin de Pierres de puits, qui se trouvent autour des puits, en Mulgore. Je les combinerai aux pommes de pin, qui tombent des arbres majestueux de notre pays.$B$BCes ingrédients sont porteurs de grands principes magiques ; quand vous en consommerez les décoctions devant le feu tribal, leurs essences vous donneront une vision. Suivez-la et vous accéderez à l\'étape suivante de votre quête sacrée.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'I see you have learned to gather from our sacred land, &lt;name&gt;. It will take but a moment for me to brew the Water of the Seers.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 771;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'attaque du camp Narache' ,`Details_loc2` = 'Après une analyse attentive de la carte, vous comprenez que vous avez découvert les plans du chef de guerre dos-hirsute pour assiéger le camp Narache.$B$BLe chef Vent-du-faucon pourrait certainement tirer profit de ces informations !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 781;
UPDATE `locales_quest` SET `Title_loc2` = 'Vaincre les traîtres' ,`Details_loc2` = 'Guidés par l\'amiral Portvaillant, les humains de Kul Tiras prirent pied en Durotar, en violation du pacte signé des années auparavant entre le Chef de guerre et Jaina Portvaillant pour vaincre Archimonde.$B$BL\'attaque humaine fut repoussée et le donjon de Tiragarde tomba. Mais, récemment, les troupes de réserves de l\'amiral, menées par le lieutenant Benedict, ont repris le donjon et menacent nos terres, une fois de plus. Ces humains ne montrent décidément aucun respect pour la diplomatie.$B$BDémontrez votre bravoure et voyagez vers le sud, jusqu\'au donjon de Tiragarde, pour éliminer ses envahisseurs humains.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'J\'ai déjà eu vent de votre bravoure, $C. La geste de votre victoire, au donjon de Tiragarde, sera chantée longtemps à Orgrimmar.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 784;
UPDATE `locales_quest` SET `Title_loc2` = 'Assumer ses responsabilités' ,`Details_loc2` = 'Mon âge me rend inapte au maniement des armes, mais j\'ai appris à me rendre utile par d\'autres moyens.$B$BDe ce poste d\'observation, je guette les envahisseurs. Nos forces s\'accroissant de jour en jour, je me trouve de moins en moins souvent à devoir donner l\'alarme en soufflant dans le cor.$B$BAussi, pour passer le temps, je confectionne du matériel et aide de plus jeunes guerriers que moi à défendre leur pays.$B$BJe peux fabriquer un sac pour vos affaires. Si cela vous intéresse, apportez-moi du canevas, un matériau qu\'on trouve communément chez les humains et les centaures.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Most excellent, &lt;name&gt;. Any good &lt;class&gt; will surely find a use for this bag on the battlefield.$B$BI salute your vigor and willingness to die in the name of the Horde!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 791;
UPDATE `locales_quest` SET `Title_loc2` = 'Les vils quasits' ,`Details_loc2` = 'Je suis sûre que la vallée des Épreuves vous enseignera beaucoup, jeune $C.$B$BJ\'ai été envoyée dans la vallée pour vous guider, mais j\'y ai découvert une menace croissante.$B$BUn groupe qui se fait appeler la Lame ardente a un convent, ici, dans la vallée des Épreuves. Ils se cachent dans une grotte, au nord-est, d\'où leurs vils quasits partent se répandre sur les terres pour les ravager.$B$BComme première action contre la Lame ardente, je vous demande de vaincre ces quasits. Massacrez-les en nombre et, si vous y avez survécu, revenez me voir.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Vous avez bien agi, $N.$B$BLes vils quasits ne sont que des marionnettes dirigées par des puissances agissant au sein de la Lame ardente, mais votre succès en présage de plus grands à l\'avenir.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 792;
UPDATE `locales_quest` SET `Title_loc2` = 'Le crâne de Minshina' ,`Details_loc2` = 'J\'entends la voix de mon frère Minshina qui m\'appelle dans mes rêves.$B$BIl a été enlevé par Zalazane, le démoniste des îles de l\'Écho, à l\'est. Et il est mort.$B$BMais la mort ne l’a pas libéré. Car Zalazane a emprisonné son esprit dans son propre crâne. Dans mes rêves, je peux le voir parmi d\'autres crânes, dans un cercle de puissance situé sur la plus grande des îles de l\'Écho. Tant qu\'il y reste, son esprit est condamné.$B$BJe vous en prie, $N. Trouvez le cercle et le Crâne de Minshina. Rapportez-le.$B$BLibérez-le !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Thank you, &lt;name&gt;. &nbsp;You have saved Minshina. &nbsp;You have saved my brother\'s spirit from slavery!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 808;
UPDATE `locales_quest` SET `Title_loc2` = 'Besoin d\'un remède' ,`Details_loc2` = '$N... votre minutage est parfait. J\'espère que je pourrai en dire autant de votre célérité.$B$BJ’ai manqué d\'attention en combattant des Queues-venin aux alentours, et j\'ai été piqué par l\'un d\'entre eux. Le poison m\'affaiblit au moment même où je parle. A ce rythme, il me reste peut-être une heure à vivre. Mais je vais avoir besoin de vous...$B$BKor\'ghan, à Orgrimmar, sait comment confectionner l\'antidote. Trouvez-le... et faites vite, $N. Je ne vais pas pouvoir tenir beaucoup plus longtemps. Il devrait se trouver dans la Faille de l\'Ombre.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = '&lt;name&gt;, you\'ve saved my life. Thank you.$B$BPlease take this; I hope it can at least help you in your travels, if not bring you some coin. From what you say, I won\'t be usin\' it anytime soon. Kor\'ghan is gonna march me through more of his rituals until I prove myself. More killin\' boars, more gatherin\' scorpid tails...'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 812;
UPDATE `locales_quest` SET `Title_loc2` = 'Un esprit parfaitement éduqué' ,`Details_loc2` = 'Même si mes yeux sont faibles, j\'y vois encore suffisamment clair. Je me repose souvent sur mes compétences en alchimie pour les magies qui autrefois m\'étaient aisées. Mais je refuse de prendre $Gun apprenti:une apprentie. Aucun orc ou troll assez digne ne s\'est jamais présenté.$B$BEt vous, en êtes-vous digne ? Bien sûr que vous en êtes digne... que vous croyez l\'être.$B$BJ\'ai besoin de certaines choses. Pouvez-vous les obtenir ?$B$BIl me faut des yeux de makruras intacts, et des fioles de mucosités de clampants. De n\'importe quels makruras ou clampants en Durotar. Nous nous reparlerons bientôt.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Bravo, ma jeune amie... Beau travail, en vérité.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 818;
UPDATE `locales_quest` SET `Title_loc2` = 'Des épaves' ,`Details_loc2` = 'L\'un de mes éclaireurs les plus observateurs me rapporte que les épaves de la flotte de Portvaillant sont toujours près de la côte de Durotar, juste à l\'est du donjon de Tiragarde.$B$BCe n\'est un secret pour personne que les humains, alliés à ces petites bêtes idiotes que sont les gnomes, possèdent des connaissances avancées en mécanique. Nous devons apprendre précisément les savoirs de tous nos ennemis potentiels. Et notre peuple bénéficiera de ces nouveaux savoirs, également.$B$BNagez à travers les épaves, $N, et trouvez pour moi les outils de l\'Alliance.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Votre mission de récupération a été un succès, $C. Je veillerai à ce que ces outils soient envoyés à Orgrimmar par la prochaine caravane.$B$BBon travail.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 825;
UPDATE `locales_quest` SET `Title_loc2` = 'La revanche de Gann' ,`Details_loc2` = 'Il est évident que le prospecteur dépendait des explosifs et des véhicules bruyants pour déchirer le sol. Nous pouvons nous attendre à ce qu\'un nouveau contremaître le remplace bientôt.$B$BJ\'aimerais faire goûter aux nains leur propre médecine. D\'après ces plans, si nous combinons de la nitroglycérine avec de la pulpe de bois et du nitrate de sodium, nous pouvons créer une charge explosive. Les fusiliers, les soldats et les officiers du donjon possèdent ces ingrédients.$B$BRécupérez ces objets et apportez-les-moi. Je confectionnerai une charge que les nains ne sont pas près d\'oublier.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'I see you are quite industrious, &lt;class&gt;.$B$BNo doubt we shall soon have a fitting surprise for those dwarves.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 846;
UPDATE `locales_quest` SET `Title_loc2` = 'Un voyage à la Croisée' ,`Details_loc2` = 'J\'avais prédit votre arrivée, jeune $C. Bien que peut-être vous ne soyez plus si jeune.$B$BJe crois qu\'il est temps de vous aventurer plus profondément dans ces terres. Je sens que vous êtes $Gdestiné:destinée à de grandes choses.$B$BC\'est la Croisée que vous devez chercher à présent, dans la mesure où nos vies se sont mêlées à celles des orcs et qu\'ils ont besoin de notre aide. Descendez cette route vers l\'est et suivez-la vers le nord en direction de la Croisée. Trouvez Thork dans ses murs, il est béni par la Terre-mère.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'It is good to see that more of our $R allies have come to assist us from as far away as Thunder Bluff. Again, I welcome you.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 854;
UPDATE `locales_quest` SET `Title_loc2` = 'La chasse aux œufs' ,`Details_loc2` = 'Hé $N ! On m\'a fait venir à la Croisée pour surveiller la zone et noter ce qui s\'y déroule, pour mes maîtres à Orgrimmar.$B$BL\'un de mes sujets d\'étude est cette créature, ressemblant à un insecte, et que nous avons découverte au sud dans le Champ des géants. Nous savons peu de choses sur ces bêtes, alors je me fais un point d\'honneur d\'en découvrir plus. Elles semblent posséder quelque intelligence, plus que la plupart des simples animaux.$B$BPrenez cette griffe d\'excavation et récupérez quelques œufs dans les tertres où vivent ces bêtes. Mais attention, si elles sont alertées par quoi que ce soit, elles vous attaqueront.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You please me, &lt;name&gt;. The eggs and the tool. Well done.$B$BAnd for your reward...'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 868;
UPDATE `locales_quest` SET `Title_loc2` = 'Tribus en guerre' ,`Details_loc2` = 'Vous devez frapper mes ennemis $Ggrogne&gt;. Vous devez aider Mutiledent et les Tranchecrins en affaiblissant les Dos-hirsutes. Les deux tribus accordent beaucoup d\'importance à la spiritualité et sont très liées à la terre et à ses pouvoirs &lt;grogne&gt;. Cela nous apporte de la force en tout, et pour la guerre en particulier.$B<br :>Si vous voulez découvrir qui a causé à Thork &lt;grogne et à la Horde un tel supplice, alors aidez Mutiledent ! Attaquez les chefs spirituels des Dos-hirsutes au nord. Affaiblissez-les afin que les Tranchecrins puissent les vaincre et régner sur les Tarides.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Yes, yes... filled with joy because finally &lt;snort&gt; the Bristleback know my tribe\'s pain, $R. That is how I should feel, but that &nbsp;is not the case.$B$BYou aid your people and they honor you. Mangletooth aids his people, and what do they do? They turn their backs on him. They forget him and leave him to rot in cage!$B$BI aid them even from locked behind bars and they send no one to save me!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 878;
UPDATE `locales_quest` SET `Title_loc2` = 'Samophlange' ,`Details_loc2` = 'La KapitalRisk dispose d’un petit centre de recherches à l’ouest d’ici, juste après la Videfange. Je ne sais pas vraiment ce qu’ils y font, mais j’ai pu découvrir qu’ils y menaient des expériences sur un appareil appelé « samophlange ».$B$BMais qu’est-ce que ça peut bien être, un samophlange ? Enfin, quoi qu’il en soit, je veux examiner cette machine, il faut donc que quelqu’un aille la récupérer pour moi.$B$BJ’ai pu me procurer un exemplaire de leur mode d’emploi du système de contrôle, vous devriez pouvoir trouver comment désengager le samophlange.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Une grande variété de boutons, de leviers et de lumières clignotantes sont placés d\'une manière quelque peu fantaisiste sur le devant de la console de contrôle. Une petite jauge indique que l\'appareil est en marche en ce moment, à ses niveaux optimaux, et que les valves de contrôle 1 à 3 sont ouvertes. Il y a une petite serrure située dans le coin inférieur droit du panneau de contrôle.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 894;
UPDATE `locales_quest` SET `Title_loc2` = 'La connaissance des profondeurs' ,`Details_loc2` = 'J’ai pour devoir de recueillir le savoir. Le savoir ancien. Le savoir puissant. Le savoir qui ouvre des portes et le savoir qui pourrait réveiller Ceux qui dorment, s’il est utilisé à mauvais escient.$B$BIl court des rumeurs sur un livre ancien, le manuscrit de Lorgalis, dissimulé dans les profondeurs de Brassenoire, à l\'ouest. Cet endroit est l’ancienne demeure de nos ancêtres, depuis longtemps disparus. Ils avaient réuni de grandes connaissances avant que leur cité ne tombe en ruine.$B$BFouillez les profondeurs de Brassenoire à la recherche du manuscrit. Faites-le, et je ne l\'oublierai pas. Pas même en ces temps apocalyptiques.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You found the manuscript! &nbsp;What a joyous day this is! &nbsp;Copies will be made and distributed, and the ancient knowledge of Lorgalis will finally be known to my brothers and sisters!$B$BYour service to us is more than you know, &lt;name&gt;. &nbsp;But I hope that this gift shows you the gratitude we extend to our allies.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 971;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;INUTILISÉ&gt;' ,`Details_loc2` = 'Si nous décidons de nous battre pour sortir, le plan est simple : nous tracerons notre route en tuant tous les furbolgs qui seront sur notre chemin... Ou tout ce qui voudrait essayer de nous arrêter.$B$BJ\'espère qu\'il y a un autre moyen, mais tôt ou tard il faudra affronter les furbolgs. Autant le faire nous-mêmes, nous serons sûrs que le travail a été fait.$B$BPrenez le temps de préparer vos armes ou vos enchantements, puis revenez me voir.' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 989;
UPDATE `locales_quest` SET `Title_loc2` = 'Évasion par la ruse' ,`Details_loc2` = 'D’accord... si vous le voulez, nous nous échapperons d’ici.$B$BRappelez vous, une fois que vous aurez quitté ce lieu, allez à Auberdine et rencontrez Terenthis. C’est là que je vais me rendre aussi, et j’y serai probablement avant vous.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = '&lt;name&gt;! I\'m pleased to see you\'ve returned successfully. Your assistance to Volcor has given me faith that we can overcome the challenges facing us here in Darkshore and beyond.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 995;
UPDATE `locales_quest` SET `Title_loc2` = 'Quand les rêves tournent au cauchemar' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 999;
UPDATE `locales_quest` SET `Title_loc2` = 'Ce qui se cache au-delà' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1005;
UPDATE `locales_quest` SET `Title_loc2` = 'Ce qui est au-delà' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1006;
UPDATE `locales_quest` SET `Title_loc2` = 'Les statuettes antiques' ,`Details_loc2` = 'Je vais vous charger d\'une mission, $N.$B$BJ\'étais sur mon bateau, voguant sur les ruines englouties de Zoram, quand les nagas ont émergé des eaux et m\'ont attaqué en me lacérant de leurs griffes ! Je me suis enfui, et le reste, comme on dit, appartient à l’histoire.$B$BJe suis un archéologue, en quelque sorte, et j’aimerais retrouver des reliques de la cité perdue.$B$BPourriez-vous aller sur la grève de Zoram, au sud-ouest, et m’apporter toutes les statuettes antiques que vous trouverez ? C\'est pour elles que j\'ai bravé les dangers de la grève de Zoram.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You found it! &nbsp;Thank you, &lt;name&gt;!$B$BThe old city of Zoram holds many secrets, and this statuette may be the key to many of them.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1007;
UPDATE `locales_quest` SET `Title_loc2` = 'Les cheveux de Bathran' ,`Details_loc2` = 'Une plante pousse dans les vieilles ruines du repaire de Bathran, par-delà la route de l’est. On l\'appelle les cheveux de Bathran et elle est réputée guérir les maux de l\'esprit.$B$BÀ présent, la Horde la récolte pour servir ses desseins maléfiques.$B$BIl y a une enfant malade à Astranaar, et nous pensons qu\'il ne s\'agit pas simplement d’une maladie physique. J’ai promis à mon maître de trouver un remède, mais je n’ai pas l’âme d’un guerrier.$B$BAccepteriez-vous d\'aller au repaire de Bathran et d’y cueillir cette plante ?' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Ah, you have the hair! &nbsp;Now I will mix a cure for the child, and pray my concoction works.$B$BAnd... Forsaken are at Bathran\'s Haunt? &nbsp;Very troubling to hear, that is. &nbsp;Very troubling indeed...'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1010;
UPDATE `locales_quest` SET `Title_loc2` = 'Mystral gagnant' ,`Details_loc2` = 'Nous aurions pu facilement empêcher les attaquants de la Horde d’envahir le refuge de Vent-d\'Argent s’ils n’avaient pas pris le contrôle de nos élémentaires d’eau : les défenseurs du lac Mystral.$B$BL’un de leurs chamans a invoqué Maréen, un esprit de l’eau sylvestre, âgé et aigri. Il a contaminé nos élémentaires et les a retournés contre nous. Sous la pression de la Horde et de ses nouveaux alliés, nous avons dû nous retirer ici, au pic.$B$BAllez à l’est. Reprenez le lac pour que nous ayons une chance de récupérer un jour le refuge.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Well done, &lt;name&gt;, this should give us the information we need to have the culprit discovered. How would you feel about taking out the mage who fills our pools with corrupt elementals? The corruption of Ashenvale and beyond makes my stomach turn on its own--to have a wizard actively attempting to destroy it further sickens me to no end. We shall make him pay.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1016;
UPDATE `locales_quest` SET `Title_loc2` = 'Mythologie des titans' ,`Details_loc2` = 'L\'avenir est inévitable ! Nous nous lançons vers lui comme les gouttes de pluie tombent dans la Grande mer.$B$BMais peut-être qu\'en étudiant le passé, nous pourrons savoir à quoi nous attendre pour mieux nous préparer.$B$BAvant que Lordaeron ne connaisse ces événements sinistres, il y avait un bastion de connaissance caché dans les Clairières de Tirisfal. La rumeur disait qu\'un livre appelé « la Mythologie des titans » avait été placé dans la bibliothèque du vieux Monastère.$B$BRetrouvez-le et peut-être qu\'ensemble nous pourrons découvrir les secrets du passé !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You have rescued the sacred text!$B$BYour journey was long and acquiring the book was undoubtedly no easy task. But &nbsp;the dwarves of Ironforge stand to benefit from your success.$B$BThank you, &lt;name&gt;, on behalf of the Explorers\' League.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1050;
UPDATE `locales_quest` SET `Title_loc2` = 'Diminution de la menace' ,`Details_loc2` = 'Les furbolgs crins-de-chardon sont au nord d\'Astranaar. Mes éclaireurs m\'ont appris que leur nombre a très fortement augmenté ces derniers mois. S\'ils deviennent trop nombreux, ils pourraient bien trouver le courage d\'attaquer Astranaar...$B$BTuez-en autant que possible, mais surtout apportez-moi le crâne de leur chef pour me prouver que vous les avez arrêtés... au moins pour l\'instant.$B$BJe vais rester à Astranaar pour attendre de vos nouvelles. Qu\'Élune soit avec vous !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Well done, &lt;name&gt;.$B$BWe are under constant threat from the furbolg. Hopefully this will keep them at bay for a bit longer.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1054;
UPDATE `locales_quest` SET `Title_loc2` = 'Voyage au pic des Serres-Rocheuses' ,`Details_loc2` = 'Les esprits de la forêt m\'ont dit que vous aviez du courage et que vous étiez $Gprêt:prête à voyager.$B$BAu sud, près du lac Mystral, il y a un tunnel nommé la Perce des Serres. Par ce tunnel, vous arriverez dans un secteur connu sous le nom de la Combe des Cisailles, dans les Serres-Rocheuses. Une fois là-bas, faites route vers le sud-ouest, devant le lac de la Combe et ensuite vers le nord, en prenant la montée jusqu\'au pic des Serres-Rocheuses.$B$BLe gardien Albagorm vous y attend. Faites ce qu\'il vous demande, $R.$B$BLe voyage sera périlleux.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Finally, you arrive. I see Faldreas has listened to the forest spirits....'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1056;
UPDATE `locales_quest` SET `Title_loc2` = 'À la reconquête du Val calciné' ,`Details_loc2` = 'En tant que protecteur des esprits de la forêt, vous pouvez sans aucun doute deviner pourquoi je suis alarmé.$B$BVous avez voyagé à travers ces montagnes pour atteindre ce pic, et avez sûrement vu les changements terrifiants qu\'a subis la terre. La cupidité a mené à la destruction de ces collines sacrées.$B$BLe Val calciné, au sud, a été consumée par une avide armée mécanique. Pire encore, les Rougefuries qui ont empiété sur ce territoire nous en interdisent l\'accès et nous empêchent d’y prospérer à nouveau.$B$BChassez ces affreuses harpies, $N !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Now that the Bloodfuries have suffered such a great loss, perhaps the true work of restoring the land can begin....'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1057;
UPDATE `locales_quest` SET `Title_loc2` = 'À la reconquête du Val calciné' ,`Details_loc2` = 'Les Rougefuries ont été maîtrisées. Mais elles se regrouperont bientôt si nous n\'agissons pas.$B$BLoin au sud des Tarides se trouve une terre connue sous le nom de Mille pointes. Si vous voyagez à l\'ouest des Mille pointes jusqu\'à la lisière de la luxuriante forêt de Féralas, vous trouverez un druide nommé Falfindel Gardevoie.$B$BCe grand et sage druide est capable d\'invoquer de puissants esprits de la forêt qui pourraient aider à la reconquête du Val calciné.$B$BFaites-lui connaître notre requête ! Partez à sa recherche. Maintenant !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Keeper Albagorm was wise to send you with such urgency.$B$BLet us see if we cannot call forth aid from the great forest to travel to the Charred Vale before it is too late.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1059;
UPDATE `locales_quest` SET `Title_loc2` = 'Inaptitude + Produits chimiques = Rigolo' ,`Details_loc2` = 'Ha ha ha ! Vous avez besoin de nitromirglyceronium pour empêcher les gobelins de détruire une forêt ?$B$BSavoir si vous connaissez Gaxim ou non ne m\'intéresse pas… Je vais vous donner le matos juste histoire de vous voir ou de les voir exploser en petits confettis.$B$BIl y a un problème quand même : je n\'en ai plus.$B$BMais je vais vous dire, vous m\'apportez les bonnes potions, et je vous en fais tout de suite.$B$BLe moins que vous puissiez faire c\'est de me montrer que vous avez des talents d\'alchimiste avant que je vous donne un échantillon de ma grande œuvre.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'This\'ll do nicely.$B$BIf I didn\'t know better, stranger, I\'d say you knew somethin\' about alchemy. Good, good... the world\'s not all about swords and sorcery, you know?'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1073;
UPDATE `locales_quest` SET `Title_loc2` = 'Opérations spéciales – Plan A' ,`Details_loc2` = 'Ça y est, $N. Faites très attention ! Les dispositifs sont montés et prêts à fonctionner.$B$BPrenez ces explicatifs. Ils résument tout ce qu\'il faut savoir sur les détonateurs et les explosifs, et indiquent également où vous devez les placer.$B$BRappelez-vous, le succès de votre mission ne dépend pas de la somme de destruction que vous provoquerez. Le succès du plan A dépend du fait que vous rapportiez les plans d\'ingénierie de la KapitalRisk de l\'intérieur de la scierie au sud du lac de la Combe.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Truly amazing! This is a huge blow to the Venture Co.$B$BWe\'ll be able to find weaknesses in their machines and also incorporate their designs into our own creations. This is a wonderful day, indeed.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1079;
UPDATE `locales_quest` SET `Title_loc2` = 'Réception par Tyrande' ,`Details_loc2` = '$N, il est sûr que Tyrande Murmevent en personne voudrait vous remercier de prendre part à cette mission.$B$BIl n’y paraît peut-être pas, mais votre aide pourrait renforcer notre position dans les Serres-Rocheuses.$B$BSi vous vous considérez comme notre $Gallié:alliée face à ces atrocités, allez voir Tyrande au temple de la Lune de Darnassus.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Hello, &lt;name&gt;. I have indeed heard your name before, and although you are still full of youth, I am pleased to see the path you have set for yourself.$B$BYou would do me a great honor if you would accept this as a gift of appreciation.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1081;
UPDATE `locales_quest` SET `Title_loc2` = 'Les gobelins gagnent !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1099;
UPDATE `locales_quest` SET `Title_loc2` = 'Journal de Lonebrow' ,`Details_loc2` = 'Vous ouvrez lentement le journal décoloré de Henrig Lonebrow…' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You say you found this journal on a deceased dwarf? Give me but a moment to peruse the pages...'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1100;
UPDATE `locales_quest` SET `Title_loc2` = 'La mégère du Kraal' ,`Details_loc2` = 'Pauvre Heralath ! Et ce nain... Lonebrow. Quel esprit courageux il avait.$B$BNous ne devons pas laisser leur vaillance périr pour rien. Cette mégère… Charlga Trancheflanc… doit être arrêtée.$B$BIl ne fait pas de doute qu\'infiltrer le kraal sera périlleux. Nous n\'avons pas le temps d\'envoyer un mot à Darnassus. $N, rassemblez un groupe et tuez la mégère.$B$BApportez-moi le médaillon de Trancheflanc comme preuve de son décès.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'The crone has been laid to rest. This is fantastic news, &lt;name&gt;.$B$BIn these times of peril, it is refreshing to see one as brave as yourself.$B$BWith Razorflank\'s minions taken care of, our studies in the area can continue. Perhaps now we can gain further knowledge of exactly what happened to corrupt the resting place of Agamaggan.$B$BHowever, I fear the answer to that question lies in treachery as well...'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1101;
UPDATE `locales_quest` SET `Title_loc2` = 'Un destin funeste' ,`Details_loc2` = 'Cairne est un noble chef qui a unifié notre peuple, ici, aux Pitons-du-Tonnerre.$B$BMais je ne peux pas pardonner aussi facilement à ceux qui nous ont éloignés de nos terres ancestrales. Mon peuple a habité les Tarides du Sud pendant des décennies. Cette terre est sacrée pour nous. Mais nous en avons été chassés par de nombreux ennemis.$B$BUn destin funeste attend la sinistre mégère, Charlga Trancheflanc, qui rameute une ignoble armée depuis le kraal de Tranchebauge.$B$BApportez-moi le cœur de Trancheflanc et je pourrai mourir en paix, $N.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'I see the nasty tribe of Razorfen Kraul now knows the pain of my people. Serves the foul beasts right.$B$BTo drive one from a holy land is a sin worthy of the most severe revenge.$B$BThank you, &lt;name&gt;, for aiding me in my plight.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1102;
UPDATE `locales_quest` SET `Title_loc2` = 'Pièces de chariot explosif' ,`Details_loc2` = 'Eh ! Avec toutes les courses qui ont lieu ici, il n\'est pas surprenant que quelques incidents surviennent le long du chemin.$B$BSi vous regardez tout autour des Salines, vous verrez des traces d’accidents. On trouve des pièces de chariots explosifs partout !$B$BEt ces pièces valent de l\'argent. Elles intéressent les gnomes et les gobelins. Ils cherchent toujours des pièces à monter sur leurs chariots.$B$BAlors allez-y et trouvez-moi des pièces. Apportez-m\'en un tas et je vous paierai bien.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Whoa, look at all that! &nbsp;There\'s some good stuff in that heap of junk. &nbsp;I\'m sure I can pawn it off for a hefty sum.$B$BAnd... is that an ear I see squashed between those two sheets of metal?? &nbsp;Ouch! &nbsp;Heh, I sure wouldn\'t want to be that pilot!$B$BThanks, &lt;name&gt;. &nbsp;Here\'s your pay. &nbsp;And here\'s some advice, free of charge...$B$BIf you look hard enough, you can always find profit in someone else\'s tragedy.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1110;
UPDATE `locales_quest` SET `Title_loc2` = 'Des cœurs zélés' ,`Details_loc2` = 'Vous pourriez vous demander pourquoi j\'ai besoin d\'un type rare de guano. Eh bien, pour cette expérience, je veux voir quels effets deux substances chimiques diamétralement opposées peuvent avoir l\'une sur l\'autre, et spécialement lorsqu\'elles sont combinées à un agent extrêmement toxique pour assurer les résultats les plus... effrayants.$B$BDans les pics au nord-est des Clairières de Tirisfal, vous trouverez le Monastère écarlate. Les disciples fanatiques de la Croisade s\'y réunissent, à l\'intérieur et à l\'extérieur des salles. Peu importe lesquels vous tuez, ils peuvent tous avoir le type de cœur dont j\'ai besoin.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'My what an abundance of gore you have brought me, &lt;name&gt;! Your efforts have been most excellent!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1113;
UPDATE `locales_quest` SET `Title_loc2` = 'Retourner voir Bullévente' ,`Details_loc2` = 'Cette bière brune du Sot a marché à merveille ! J\'ai parié sur la course suivante… Vous pensez bien que l\'équipe que vous avez éméchée a fait son numéro sur le chariot !$B$BIl n\'y a plus qu\'une chose à faire : apportez cette note à Grincheux Bullévente à Baie-du-Butin. Il voudra connaître les résultats du test.$B$BEt avec un peu de chance, nous aurons plus de cette bière brune du Sot pour les prochaines courses !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'It says here that the fool\'s stout was a success. &nbsp;That\'s good news!$B$BIf you ever want more of the stuff, then let me know and I\'ll have you gather more of Zanzil\'s Mixture.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1122;
UPDATE `locales_quest` SET `Title_loc2` = 'Le venin de Haut-Perchoir' ,`Details_loc2` = 'Comme vous le savez, les wyvernes sont souvent domestiquées par les orcs, qui les utilisent comme montures. Ce que l\'on sait moins, c\'est que, dans certains cercles, le venin de wyverne est plus recherché que la bête elle-même. Si vous n\'avez pas peur de chasser la wyverne, j\'ai un travail pour vous.$B$BIl existe dans la région des Mille pointes, à l\'ouest, un lieu nommé Haut-Perchoir. Les wyvernes qui y nichent produisent un puissant venin. Très puissant et largement monnayable.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1135;
UPDATE `locales_quest` SET `Title_loc2` = 'Des parties de l\'essaim' ,`Details_loc2` = 'Vous inspectez la carapace avec soin. Comme vous la tournez, vous comprenez que le placage est entrecroisé de telle manière qu\'elle lui donne une dureté incroyable. Cela ne ressemble à rien que vous connaissiez. Vous inspectez le reste du corps et vous réalisez que ces créatures sont très puissantes et très évoluées. $B$BEn regardant de nouveau la carapace, vous vous dites que Korran apprécierait sûrement d\'étudier ces créatures d\'un peu plus près. Cela l\'aiderait à découvrir ce qu\'elles sont et d\'où elles viennent.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Ahh, you\'ve seen Moktar recently? He always be lookin\' out for me. I\'m sorry to hear he spends his time watching over his little war band instead of seeing battle, but I\'m sure he be takin\' great joy in killing members of the Explorers\' League.$B$BYou be smart to bring me these specimens, &lt;name&gt;. Along with the eggs we be findin\' in the Field of Giants, these should give me a good start in figuring out what these insects are, and what they be wantin\'.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1148;
UPDATE `locales_quest` SET `Title_loc2` = 'L’épreuve de l’Endurance' ,`Details_loc2` = 'Il me plaît de constater que votre courage reste entier à la lumière de ce que vous avez déjà réalisé. Puisque votre foi est intacte, il est temps de mettre les capacités de votre corps à l\'épreuve.$B$BCommençons par une épreuve d\'endurance. Allez jusqu\'à la tanière des Volplumes, à l\'ouest du poste de Librevent. Plongez au cœur de leur tanière et détruisez leurs réserves de nourriture. Cela devrait provoquer leur colère, et si vous êtes capable de survivre assez longtemps à leur furie, Grenka Hurlesang elle-même s\'attaquera à vous... Tuez-la et apportez-moi sa griffe en accomplissement de votre épreuve.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Grenka was powerful, even by $R standards. You should be proud of your accomplishment this day.$B$BDuring your test of faith you displayed strength of spirit, and now you have shown endurance in battle by taking on the Roguefeather harpies, but the time has come for a new test.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1150;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'épreuve de la Force' ,`Details_loc2` = 'Au temps des Dieux très anciens, les élémentaires peuplaient ce monde. Ils adoraient les Dieux très anciens, et le pouvoir de ces derniers maintenait les élémentaires attachés à ce monde. Lorsque les Dieux très anciens furent bannis, les élémentaires disparurent de notre monde et ils n’y reviennent que s\'ils sont appelés par de puissants mages.$B$BIl y a pourtant des exceptions. Certains élémentaires étaient suffisamment puissants pour rester. L\'un d\'eux est Rok\'Alim.$B$BPour mettre votre force à l\'épreuve, trouvez Rok\'Alim dans les Mille pointes et tuez-le.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'The Test of Strength is done. You have shown strength and bravery, and you are now prepared for the final test before these rites are completed.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1151;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'épreuve de la Connaissance' ,`Details_loc2` = 'Vous avez presque terminé votre apprentissage avec moi, mais l\'Épreuve de la Connaissance n\'est pas de mon ressort. Il y a des leçons que l\'on apprend et des lieux que l\'on visite pour lesquels il faut un autre guide que moi-même.$B$BSi vous vous sentez apte, cherchez Braug Esprit-troublé... C\'est un chaman d\'une grande sagesse. Il vous mettra à l\'épreuve, alors prêtez attention à ses paroles, $N. $B$BLorsque vous vous estimerez apte, trouvez le tunnel qui relie les Serres-Rocheuses et Orneval : Braug habite près de là. Mais soyez $Gprudent:prudente, $N. Les elfes de la nuit pourraient vouloir vous empêcher d\'accomplir votre épreuve.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'The days and nights grow colder throughout the world as goblins rape the land, and the night elves point fingers at the younger races. The Forsaken and orcs are not the only ones to blame for the hardships found throughout Kalimdor and Lordaeron. Many of the intelligent races have contributed to the world\'s pain. Dorn knows this, and he has sent you to me because you have shown the attributes necessary to realize it also.$B$BNow you stand before Braug.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1152;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;NYI&gt; &lt;TXT&gt; speak to hamuul runetotem' ,`Details_loc2` = 'x' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1162;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;NYI&gt; &lt;TXT&gt; speak to tyrande whisperwind' ,`Details_loc2` = 'x' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1163;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;NYI&gt; Ore for the Races' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1165;
UPDATE `locales_quest` SET `Title_loc2` = 'Les espions de Theramore' ,`Details_loc2` = 'La trêve entre la Horde et l\'Alliance n\'est pas toujours respectée. Des escarmouches et des actions secrètes sont menées par chacun des deux camps, et il est de notre devoir de nous assurer que la Horde garde l’avantage ! $B$BNous avons maintenant l\'occasion de démasquer les infiltrateurs de l\'Alliance. $B$BIls sont en position à l\'est du village de Mur-de-Fougères. Ils nous espionnent et rapportent nos mouvements à Theramore, ce que nous ne pouvons permettre. $B$BTraquez-les et tuez-les. Envoyons un message clair à leurs chefs : nous ne supporterons pas leur présence sur notre sol !' ,`Objectives_loc2` = 'Tuer 9 Infiltrateurs de Theramore, puis rendre compte à Nazeer Pique-sanglante à Mur-de-Fougères.' ,`OfferRewardText_loc2` = 'Bon travail ! Leurs infiltrateurs sont habiles, mais pas assez pour échapper aux yeux attentifs de la Horde !'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1201;
UPDATE `locales_quest` SET `Title_loc2` = 'Jarl a besoin d\'une épée' ,`Details_loc2` = 'Je dois découper quelque chose. Je ne peux pas vous dire quel type de découpage je vais faire, mais... j\'ai besoin d\'une lame. Pas n\'importe quelle lame !$B$BIl y en a qui disent que c\'est une épée. Moi je dis lame.$B$BVous allez m\'aider ? Ces hommes-poissons, les murlocs, ou un truc comme ça... Il y en a un qu\'ils appellent Tranchéchine, il porte une grande épée, et il vit au nord d\'ici près de la plage. Voilà l\'épée qu\'il me faut !$B$BMais ne traînez pas. Ce que je dois découper n\'attendra pas des années.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'That\'s a good blade. &nbsp;It has a good swing. &nbsp;It\'s a good swing blade. &nbsp;Thank you. &nbsp;Thank you, thank you, thank you!!! &nbsp;Now I want to try it.... &nbsp;$B$BLater. &nbsp;I\'ll try it later. &nbsp;But like I was saying... thanks.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1203;
UPDATE `locales_quest` SET `Title_loc2` = 'Le rapport perdu' ,`Details_loc2` = 'Au milieu de la poussière, une main d\'orc, seule. Et dans cette main, un tube creusé dans de l\'os. Et dans le tube, un parchemin sale.$B$BCe parchemin est un rapport établi par un orc qui a passé des semaines à espionner la ville de Theramore.$B$BAprès examen du rapport, il est clair qu\'il doit être apporté au maître-espion orc au village de Mur-de-Fougères.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Good work, &lt;name&gt;. &nbsp;Our agent, Marg Nighteye, was stationed outside Theramore. &nbsp;He was charged to spy on the city. &nbsp;$B$BThis is his latest report, and it may be his last -- from your testimony, I fear that Marg met his end in the swamp...'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1238;
UPDATE `locales_quest` SET `Title_loc2` = 'Un diplomate a disparu' ,`Details_loc2` = 'Vous dites que c\'est Elling qui vous envoie ? Eh bien l\'hospitalité que je vous dois est encore plus grande. Quels que soient vos besoins, demandez. Je ferai de mon mieux pour vous aider.$B$BHmm, le Mince, vous dites ? Ça ne me dit rien, mais je garderai un œil ouvert au cas où.$B$BNe vous retournez pas maintenant, $N, mais le gentilhomme là-bas près de la sortie regarde par ici. Il écoute notre conversation un peu trop attentivement, si vous voyez ce que je veux dire. Si j\'étais vous, je l\'intercepterais avant qu\'il ne puisse atteindre la porte et disparaître.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Caught him, huh? Good for you. I\'m surprised that was Slim.$B$BI\'ll be honest, I never thought I\'d miss the excitement of some of the more devious underground activities. I guess old habits die hard. Don\'t mention that to Elling--he\'d probably try and get me to come back to Stormwind if he heard I missed the action.$B$BI\'m glad I could at least be of a little service to you while you were in Menethil. If you need anything else, just ask.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1249;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'auberge en feu &lt;CHANGE TO GOSSIP&gt;' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1263;
UPDATE `locales_quest` SET `Title_loc2` = 'Recherches sur la corruption' ,`Details_loc2` = 'Ces imbéciles d\'elfes de sang peinent avec la magie démoniaque. N\'ont-ils pas vu ce qui est arrivé aux nagas et aux satyres de Kalimdor ?$B$BJe crains que les elfes de sang ne subissent pareille transformation. Azeroth ne supporterait pas la naissance d\'une autre race de monstres abjects.$B$BJe souhaite étudier les troncs cérébraux des satyres et des nagas afin de découvrir ce qui attend les Sin’dorei. Vous trouverez des satyres et des nagas dans les profondeurs de Brassenoire, juste à l’ouest.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You have traveled far and undoubtedly faced great peril.$B$BYour actions have been selfless. \'Tis for the good of all Azeroth that you help me with my research. I thank you, &lt;name&gt;, for all that you have done.$B$BNow if you\'ll excuse me I have much work to do....'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1275;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;nyi&gt; &lt;TXT&gt; The Centaur Hoofprints' ,`Details_loc2` = 'Kagoro a trouvé les propriétaires des empreintes de centaures. Il s\'agit d\'une bande de crétins de Mauradon.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1277;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;nyi&gt; &lt;TXT&gt; The Grim Totem Clan' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1278;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;nyi&gt; &lt;TXT&gt;The Centaur Hoofprints' ,`Details_loc2` = 'Finir l\'un des trois.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1279;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;nyi&gt; &lt;TXT&gt;The Centaur Hoofprints' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1280;
UPDATE `locales_quest` SET `Title_loc2` = 'Le rapport de Vimaire' ,`Details_loc2` = 'Vous avez fait du bon travail, $N, le meilleur que j\'ai vu de la part de quelqu\'un qui n\'est pas de chez nous.$B$BDame Portvaillant a demandé que je la tienne informée de cette situation délicate et je ne lui ai pas parlé depuis longtemps.$B$BJe m\'apprêtais à lui envoyer mon rapport, mais il me semble plus approprié que cela soit vous qui lui parliez. Vous répondrez mieux à ses questions que moi.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1288;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;nyi&gt; Enquête sur Mosarn' ,`Details_loc2` = 'Parler au Garde Komak. Poser des questions est mauvais pour votre santé.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1290;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;nyi&gt; &lt;TXT&gt; Centaur Hoofprints' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1291;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;nyi&gt;&lt;TXT&gt; Centaur Hoofprints' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1292;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;nyi&gt; &lt;TXT&gt; Centaur Hoofprints' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1293;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;nyi&gt; &lt;TXT&gt;Centaur Sympathies' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1294;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;nyi&gt; &lt;TXT&gt; Course of Action' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1295;
UPDATE `locales_quest` SET `Title_loc2` = 'Une étrange alliance' ,`Details_loc2` = 'Nous avons de la chance que les clans de centaures se battent entre eux, car s\'ils unissaient leurs forces, rien ne pourrait les arrêter. Oui, nous sommes chanceux, et nous devrions mettre à profit cet avantage.$B$BL\'un des clans de centaures, le clan Gelkis, mène une guerre acharnée au clan Magram. Si nous attaquons ce second clan, alors nous pourrions peut-être conclure une alliance avec les Gelkis.$B$BAllez parler à Uthek la Sage, aux approches des campements gelkis, au sud-ouest de Désolace. Grâce à elle, vous pourrez peut-être conclure une alliance avec le clan Gelkis.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Your name is whispered among the Gelkis. &nbsp;You have much Magram blood on your hands. &nbsp;This is good.$B$BMaybe it is also good if we speak. &nbsp;Speak of what you can do for the Gelkis, and what we can do for you.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1382;
UPDATE `locales_quest` SET `Title_loc2` = 'La vérité, rien que la vérité' ,`Details_loc2` = 'Je pense avoir trouvé la composition du sérum qu\'il nous faut. Il s\'occupera de la vérité exactement comme il convient. Pour cette préparation, je vais avoir besoin de beaucoup de cœurs de panthères des ombres, en provenance du marais. J\'aurai également besoin d\'un champignon de boueux enchanté, de la réserve personnelle du seigneur du bourbier local. Il est certain que quelqu\'un d\'aussi talentueux que vous, $N, peut le persuader de s\'en séparer.$B$BLa dernière chose dont j’aurai besoin pour le sérum, c’est du sang d’un dragonnet rêveur, comme ceux qui vivent à côté de la caverne d’Itharius dans le marécage.$B$BAllez-y à présent !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Splendid, &lt;name&gt;! And just in time too. From what Zraedus tells me, the Infiltrator has recovered from his wounds and is ready to be questioned by the orcs.$B$BGive me but a moment to prepare the pois-- I mean prepare the truth serum.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1383;
UPDATE `locales_quest` SET `Title_loc2` = 'La prime aux centaures' ,`Details_loc2` = 'Le peuple des centaures est divisé en nombreux clans. Chaque clan a son chef et ses propres buts, mais ils ont tous une « qualité » en commun : la cruauté.$B$BSi nous voulons avoir une chance de construire la paix, alors les centaures doivent être éradiqués. C\'est exactement la tâche que je vous confie.$B$BPendant que vous serez en Désolace, chassez des centaures et prenez leurs oreilles. Apportez-moi de nombreuses, très nombreuses oreilles.$B$BŒuvrez au service de l\'Alliance, $N, et nous nous rapprocherons de la fin de la guerre.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Ah, the ears. &nbsp;Well done.$B$BYour service to the Alliance is unquestioned, &lt;name&gt;. &nbsp;The blood you spilled in Desolace will, no doubt, save countless lives.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1387;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;nyi&gt; Oups, on les a encore tués !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1390;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;nyi&gt; Sauvé !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1397;
UPDATE `locales_quest` SET `Title_loc2` = 'La recherche de Vahlarriel' ,`Details_loc2` = 'Je me nomme Vahlarriel, $N. Je suis ici pour retrouver deux nobles disparus, issus d\'une famille marchande de Darnassus : la famille Malem. Le père craint le pire, et je dois tout tenter pour retrouver ses enfants disparus. J\'ai commencé mes recherches pour retrouver Tyranis ou Dalinda, mais j\'ai bon espoir que je ne serai pas ici longtemps.$B$BAux dernières nouvelles, ils passaient par les Serres-Rocheuses il y a quelques semaines.$B$BSi vous trouvez leur chariot, s\'il vous plaît, veuillez revenir ici pour m\'en informer.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'As you rummage around the remains of the wagon, it becomes obvious that it was attacked by both spells and weapons. The battle seemed one-sided and quick; there\'s no blood to be found, and no remains of any bodies.$B$BWhoever attacked the wagon was efficient and swift.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1437;
UPDATE `locales_quest` SET `Title_loc2` = 'Le retour vers Vahlarriel' ,`Details_loc2` = 'Ainsi, le temps est venu ? J\'apprécie vos efforts, et si la situation ne se résout pas, alors permettez-moi de vous remercier dès à présent pour tout ce que vous avez fait pour moi et ma famille. Si vous êtes $Gprêt:prête, rien ne me retient ici. Nous pouvons partir quand vous estimerez le bon moment arrivé.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Thank you, &lt;name&gt;. Dalinda made it safely back here to Nijel\'s Point, and in much better condition than I could have asked for. The fact that she arrived here under her own power was a good sign. I hope you\'re well?$B$BIt pains me to know that both Tyranis and Dalinda didn\'t return, but the embarrassment of Tyranis\' decisions is hers to endure. It is something she will have to deal with when she returns to Darnassus.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1440;
UPDATE `locales_quest` SET `Title_loc2` = 'À la recherche du temple' ,`Details_loc2` = 'Fut un temps, j\'étais jeune et plein de vigueur comme vous, $C.$B$BMais à présent je suis vieux et incapable d\'explorer le monde comme je le faisais autrefois. Ah, les choses que j\'ai vues !$B$BMais il reste un mystère qui m\'a toujours échappé. Pour ma dernière quête, la Ligue des Explorateurs m\'a envoyé chercher le temple d\'Atal\'Hakkar. La rumeur veut qu\'il se trouve dans le marais des Chagrins.$B$BEn raison de mon état fragile, j\'ai parcouru les cieux à dos de griffon, mais je ne l\'ai jamais trouvé.$B$BPourriez-vous venir en aide à un vieux nain ? Peut-être aurez-vous plus de chance à pied.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'The Temple of Atal\'Hakkar has been sunk beneath the Pool of Tears? And surrounded by the green dragonflight?$B$BAn amazing discovery, &lt;class&gt;. But obviously something has gone terribly wrong.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1448;
UPDATE `locales_quest` SET `Title_loc2` = 'Des réactifs pour les Récupérateurs' ,`Details_loc2` = 'L\'une de mes tâches ici consiste à collecter des réactifs pour Leftwitch, un mage gnome de Kharanos. Roetten semble penser que c\'est de l\'argent facilement gagné puisque nous sommes déjà sur place. Il se soucie peu du fait que nous sommes si démunis.$B$BLes premiers objets de sa liste devraient être assez faciles à trouver, et ce sera de l\'argent gagné pour quiconque m\'aidera.$B$BJ\'ai besoin de cornes et de griffes des satyres Fielfurie qui vivent à l\'est d\'ici. Rapportez-m\'en et nous poursuivrons la liste.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Good good, &lt;name&gt;. Not sure if these are of any good quality, but if Leftwitch wanted a specific type, he should have specified before sending the Reclaimers out here to begin with.$B$BWhat say you to getting on with the next item on his list?'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1458;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'histoire de Rhapsody' ,`Details_loc2` = 'Avant le vol, je me suis bu un plein tonneau de bière brune. Hic !$B$BEt ça a été un de ces vols ! Volé de-ci de-là avec Brohann qui me suivait. Mais pas vu de temple en tout cas. Hic !$B$BJe veux dire, j\'ai pas vu de temple, jusqu\'à ce que je sois trop parti et que je tombe de ma monture. J\'ai plongé directement dans la flotte et j’ai été aspiré. Me suis trouvé entouré par un tas de trolls à la mine patibulaire. Vous pensez bien que j\'ai nagé très vite pour partir ! J\'ai quand même ramassé un souvenir troll. Tenez, apportez ça à Brohann et dites-lui que son temple est là-bas, en bas, de fait. Hic !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Rhapsody Shindigger! That\'s right... how could I forget such a name?$B$BSo he knew about the sinking of the temple all along? Well, that certainly would have saved you some leg work.$B$BBut this Atal\'ai artifact! Why, it\'s quite a find!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1469;
UPDATE `locales_quest` SET `Title_loc2` = 'Le lien' ,`Details_loc2` = 'Vous avez sûrement vu le cercle d\'invocation en dessous. C\'est le seul endroit d’où vous pourrez tirer un marcheur du Vide libre.$B$BEn utilisant ces runes, vous pourrez l\'attirer ici. Vous devrez alors le dominer par la force des armes et de la magie. Lorsqu\'il sera assujetti, il sera lié à vous. Venez alors me voir, et je vous expliquerai le sort d\'invocation, pour que vous puissiez vous en servir, pour quelque objectif que ce soit.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1471;
UPDATE `locales_quest` SET `Title_loc2` = 'Le corrupteur' ,`Details_loc2` = 'La peau déchirée sent le pourri. Malgré son aspect sec et fragile, elle est remarquablement souple.$B$BEn la déroulant, vous voyez clairement une sorte de langage écrit en rouge sur l\'un des côtés. Un symbole est apposé en évidence en bas de la peau ; vous supposez qu’il s’agit d’une signature.$B$BPeut-être Maurin pourrait-il vous en dire plus ?' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Hmm, these runes are indeed a language, &lt;name&gt;, an old language. They seem remotely similar to the Eredun language, but the differences are drastic. If I am not mistaken, this is a contract of some kind.... Here, yes, this symbol is the demon\'s signature: Lord Azrethoc.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1480;
UPDATE `locales_quest` SET `Title_loc2` = 'Le corrupteur' ,`Details_loc2` = 'Un cristal d\'oracle ferait parfaitement l\'affaire. Ils sont conçus par les nagas et utilisés par leurs oracles.$B$BAu nord-ouest d\'ici, le long de la côte, se trouve un vaste récif de corail que les nagas considèrent comme leur demeure, lorsqu\'ils viennent près du rivage. Vous trouverez leurs oracles là-bas. Les Ondulames sont dangereux et ne réagiront pas amicalement à votre intrusion : soyez $Gprudent:prudente et hâtez-vous.$B$BQuand vous reviendrez, nous verrons à localiser le seigneur-démon Azrethoc.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'A precious oracle crystal... flawless... beautiful. Indeed.$B$BWe shall begin immediately. Finding this demon lord should be our primary concern, and its slaying demands our utmost attention.$B$BTake a step back, &lt;class&gt;, and let us see what this demon is, and  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  he may be found.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1482;
UPDATE `locales_quest` SET `Title_loc2` = 'Le réveil de Naralex' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1500;
UPDATE `locales_quest` SET `Title_loc2` = 'Les invocations de Gan\'rul' ,`Details_loc2` = 'Vous là ! $N, c\'est ça ?$B$BJ\'espère que votre programme n\'est pas trop chargé, car Gan\'rul veut vous voir tout de suite. Vous le trouverez avec les démonistes seniors à la Faille de l\'Ombre.$B$BD\'après mes souvenirs de Gan\'rul, le fait qu\'il vous appelle est soit une très bonne chose, soit une très mauvaise.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1506;
UPDATE `locales_quest` SET `Title_loc2` = 'La dévoreuse d\'âmes' ,`Details_loc2` = 'Il est temps pour vous d\'en apprendre plus sur les magies d\'invocation. Vous allez apprendre à invoquer une succube, une créature dotée de pouvoirs mentaux incroyables. Elle peut apaiser les esprits faibles jusqu\'à ce qu\'ils tombent en son pouvoir. Mais ne craignez rien ! Je sais que vous êtes capable de relever le défi.$B$BUne succube peut être invoquée que si elle est appâtée avec un objet qui représente une grande dévotion et un grand amour.$B$BUne suggestion ? Parlez à Cazul... Il semble qu\'il n\'y ait pas grand-chose que le vieux démoniste ignore.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1507;
UPDATE `locales_quest` SET `Title_loc2` = 'Le cadeau de l\'amour' ,`Details_loc2` = 'Zankaja est jeune et belle. Etre enchaînée à un invalide… Je ne permettrai pas qu\'elle souffre de cette injustice.$B$BLaissez-moi, $N. Et merci. Je peux ainsi terminer ma vie dans une glorieuse bataille, et non comme un légume.$B$BEt je vous en prie, je souhaiterais que vous ne disiez rien à ma compagne... Je ne veux pas qu\'elle se sente coupable.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1512;
UPDATE `locales_quest` SET `Title_loc2` = 'La fleur-de-vent corrompue' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You apply two Cenarion plant salves to the windblossom. &nbsp;It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1514;
UPDATE `locales_quest` SET `Title_loc2` = 'La captivité de Dogran' ,`Details_loc2` = 'Vous êtes $Garrivé/arrivée&gt; jusque-là, alors si vous voulez voir la fin, je ne vais pas vous arrêter. Cependant, je ne peux pas vous fournir de forces supplémentaires, vous êtes &lt;seul:seule.$B$BDogran est prisonnier dans un camp de hurans au nord d\'ici. Cherchez une paire de huttes. Il sera surveillé de près... À condition qu’ils ne l\'aient pas encore tué et qu’il n’ait pas succombé à ses blessures.$B$BBonne chance.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1515;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'appel de la terre' ,`Details_loc2` = 'Un sapta est une boisson qui lie notre esprit aux éléments. Imprégnée de magie, cette potion permet aux esprits ouverts de voir les éléments comme personne d’autre ne peut les voir. Votre esprit sera lié à jamais à l\'élément pour lequel le sapta a été créé, que seuls de rares chamans peuvent confectionner.$B$BIl faut toujours boire le sapta dans un endroit sacré, jamais ailleurs.$B$BCherchez l\'autel du chaman à l\'est d\'ici et buvez-y le sapta.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1520;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'appel du feu' ,`Details_loc2` = 'Mais vous n\'êtes pas là pour recevoir un cours d\'histoire, n\'est-ce pas ? Vous êtes $Gvenu:venue pour la flamme éternelle et le totem de feu.$B$BLa première étape consiste à créer un sapta et à vous lier à la flamme.$B$BJe peux m\'en occuper si vous m\'apportez deux choses. La première est du goudron de feu, un objet simple à se procurer et généralement porté par les lanceurs de sorts Tranchecrin dans les Tarides de l\'est.$B$BIl sera plus dangereux de se procurer la seconde : une bourse de réactifs que vous trouverez sur l\'un des sectateurs de la Lame ardente. Ils sont dans une caverne au-dessus des ravins, au nord-est de Tranchecolline.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1525;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'appel de l\'air' ,`Details_loc2` = 'Les vents de Kalimdor se hâtent pour vous rencontrer et vous serez bientôt $Gcompté:comptée parmi nos plus puissants frères et sœurs. La fierté m\'emplit de vous voir arriver à ce stade, et de savoir votre avenir si prometteur.$B$BL\'air est difficile à contrôler, mais j\'ai foi en vos capacités à surmonter tous les obstacles.$B$BPartez pour l\'extrême sud des Tarides et entrez aux Mille pointes. C\'est là, loin à l\'est, que vous trouverez Prate Œil-des-nuages. Elle vous enseignera.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'For the time being, I shall give you what you need to focus your spells and to call upon the spirits of air. Take this totem, and when you are ready, train with me some more.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1532;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;NYI&gt;L\'appel de l\'air' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1533;
UPDATE `locales_quest` SET `Title_loc2` = 'Le Tome dérobé' ,`Details_loc2` = 'Vous ici ! $N, c\'est ça ?$B$BÊtes-vous $Gsûr/sûre&gt; de votre vocation de démoniste, car si vous ne pouvez pas maîtriser ce que vous invoquez, vous finirez &lt;aspiré/aspirée&gt; dans le Néant.$B$BJe dois vous enseigner les arts du démoniste, mais je dois d\'abord m\'assurer que vous êtes suffisamment &lt;fort:forte.$B$BUn novice qui étudiait à l\'abbaye s\'est enfui pour rejoindre la Confrérie défias. Pourquoi est-ce que cela m\'intéresse ? Eh bien, il a volé certains livres importants.$B$BL\'un d\'entre eux se trouve être un livre que je voulais pour mon usage personnel : « la Puissance du Vide ». Volez-le pour moi, il est caché dans sa tente dans les vignes.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1598;
UPDATE `locales_quest` SET `Title_loc2` = 'Le Tome de divinité' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1645;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;INUTILISÉ&gt;' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1659;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;INUTILISÉ&gt;' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1660;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;INUTILISÉ&gt;' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1662;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;INUTILISÉ&gt;' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1663;
UPDATE `locales_quest` SET `Title_loc2` = 'Vejrek' ,`Details_loc2` = 'Si vous voulez monter dans les rangs des guerriers, vous avez intérêt à être robuste. Je peux vous apprendre de nouvelles passes d\'armes, mais avant, prouvez-moi que vous pouvez vous débrouiller $Gseul:seule contre des adversaires réels.$B$BVejrek est le plus coriace des trolls du coin ; c\'est lui que vous devez affronter. Il a une hutte dans les contreforts au sud du repaire des Crins-de-givre.$B$BRapportez-moi sa tête et je saurai que vous avez du courage.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1678;
UPDATE `locales_quest` SET `Title_loc2` = 'Muren Foudrepique' ,`Details_loc2` = 'Bonjour guerrier ! Vous êtes $Gvoué/vouée&gt; à un grand avenir, mais il vous reste quelques petits trucs à apprendre et Muren Foudrepique est le nain qu\'il vous faut pour vous les enseigner.$B$BVous trouverez Muren dans la Salle d\'armes à la Garde militaire de Forgefer. Parlez-lui quand vous serez &lt;prêt:prête à apprendre ce qu\'est un vrai guerrier.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1679;
UPDATE `locales_quest` SET `Title_loc2` = 'Le lien' ,`Details_loc2` = 'En retrouvant mon écharpe et en éliminant Surena, vous avez prouvé que vous étiez au moins aussi $Gtalentueux:talentueuse qu\'elle.$B$BCela dit, vous avez terriblement besoin d\'entraînement. Vous pouvez manier les flux magiques, bien sûr, mais être démoniste, c\'est bien plus que cela.$B$BJe vais vous montrer comment contrôler un marcheur du Vide, mais avant, vous devez en invoquer un dans le cercle d\'invocation et le maîtriser avec les armes – physiques et magiques – dont vous disposez. Utilisez la magie que contient l\'écharpe sur le cercle d\'invocation en bas.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1689;
UPDATE `locales_quest` SET `Title_loc2` = 'Coquilles desséchées au soleil' ,`Details_loc2` = 'Mon collègue, Furen Barbe-longue, a fait une découverte que je suis impatient d\'essayer. Je veux la tester sur une de mes nouvelles armures, car je pense que ses techniques me permettraient d’améliorer la qualité de mes produits.$B$BVoulez-vous m\'aider ? Si vous me rapportez de quoi fabriquer l\'armure, alors j\'en ferai une pour vous.$B$BJ\'ai besoin de coquilles d\'œuf desséchées au soleil des wyvernes ailes-fières qui résident à Haut-Perchoir, dans les Mille pointes. Cherchez les coquilles au pied de leurs nids, et revenez quand vous en aurez assez.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1710;
UPDATE `locales_quest` SET `Title_loc2` = 'Essence d\'exil' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1714;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'île mortelle' ,`Details_loc2` = 'Votre renommée en tant que $Gguerrier:guerrière s\'accroît, $N. Il est temps de vous mesurer à l\'un de vos pairs.$B$BIl existe une île près des côtes des Tarides, au sud de Cabestan : l\'île de la Dispute. Les guerriers s’y retrouvent.$B$BS’y retrouvent et s’affrontent.$B$BParlez au chef des guerriers de l\'île, Klannoc Macleod. S’il pense que vous le méritez, alors il vous guidera plus avant sur la voie du guerrier.$B$BPour atteindre l\'île de la Dispute, cherchez des bancs de sable à faible profondeur le long de la côte des Marchands.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1718;
UPDATE `locales_quest` SET `Title_loc2` = 'La Dispute' ,`Details_loc2` = 'Si vous voulez que je sois votre Maître, vous devez d\'abord passer la Dispute. C\'est une épreuve que nous, sur l\'île de la Dispute, aimons faire passer aux guerriers qui pensent être forts, et si vous réussissez ce test alors nous saurons que vous l\'êtes.$B$BPour commencer, marchez à travers cette foule là-bas, et allez jusqu\'à l\'âtre. Dites à Twiggy Tête-plate que vous êtes $Gprêt:prête, il vous enverra des partenaires de combat.$B$BBattez-les tous, et il appellera le Grand Will. Tuez le Grand Will, et revenez me voir.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1719;
UPDATE `locales_quest` SET `Title_loc2` = 'Le lien' ,`Details_loc2` = 'En utilisant le noyau du cœur du bois, vous serez en mesure de jeter le sort dans le cercle d\'invocation qui se trouve dans la crypte en bas afin d\'ouvrir un portail d\'où la succube arrivera.$B$BAttention, $N, les succubes arrivent à contrôler les esprits les plus forts.$B$BUne fois que vous l\'aurez vaincue et renvoyée dans le néant, revenez me voir pour que je vous apprenne comment l\'appeler et la contrôler.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1739;
UPDATE `locales_quest` SET `Title_loc2` = 'Le lien' ,`Details_loc2` = 'Si vous êtes suffisamment $Gpréparé:préparée, alors nous pouvons commencer. Laissez-moi vous expliquer la procédure.$B$BJe vais commencer par créer un simple cercle d\'invocation. Quand j\'aurai fini, mes acolytes vont utiliser leurs bâtonnets de canalisation pour créer un cercle majeur.$B$BLisez alors l\'incantation du Tome de la Cabale, et le chasseur corrompu apparaîtra. Faites attention, $N. Un chasseur corrompu est un ennemi puissant, et j\'aimerais que vous gardiez la tête sur les épaules.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1795;
UPDATE `locales_quest` SET `Title_loc2` = 'Parler à Coleman' ,`Details_loc2` = 'Vous avez prouvé votre valeur à la Dame noire, à présent vous pourrez peut-être la prouver à l\'un de ses sujets.$B$BColeman Farthing cherche un guerrier pour être son bras vengeur contre les ennemis des Réprouvés.$B$BVous le trouverez à « la Taverne des Pendus » dans l\'est de Brill.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Yes, it is true. &nbsp;I have need for your skills in combat. &nbsp;My means are small, but my task aids both me and the Forsaken\'s cause. &nbsp;Do it, and your reward will be twofold.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1820;
UPDATE `locales_quest` SET `Title_loc2` = 'Mennet Carkad' ,`Details_loc2` = '$N ? Pas vraiment ce à quoi je m\'attendais, compte tenu de l\'intérêt que Carkad lui-même manifeste pour vous ! Comme quoi les apparences sont parfois trompeuses, non ?$B$BPeu importe, vous devez aller le voir dès que possible. Autant que vous ne sachiez pas ce qui est arrivé au dernier qui a ignoré ses sommations.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1885;
UPDATE `locales_quest` SET `Title_loc2` = 'Les nécrotraqueurs' ,`Details_loc2` = 'Si vous voulez rejoindre les nécrotraqueurs, la garde d\'élite du seigneur Varimathras, vous devez prouver votre valeur.$B$BJe ne vais pas vous mentir, $N, cette tâche pourrait bien signifier votre mort.$B$BIl y a certaines choses auxquelles il faut prêter attention. J\'ai besoin des effets personnels d\'Astor Hadren, un messager qui voyage entre les Pins-Argentés et Fossoyeuse. Je vous laisse le choix de la méthode.$B$BMoins vous en saurez, mieux ça vaudra pour vous. Réussissez et je vous en dirai plus.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1886;
UPDATE `locales_quest` SET `Title_loc2` = 'Les nécrotraqueurs' ,`Details_loc2` = 'La lettre de recommandation devrait être suffisante pour convaincre Andron que vous êtes $Gle messager:la messagère qu\'il attendait. J\'ai déjà rencontré Andron, il n\'est pas assez intelligent pour être soupçonneux.$B$BLe seigneur Varimathras suspecte Andron d\'avoir fourni de l\'aide à un autre groupe. Il exige de savoir lequel et en veut la preuve.$B$BJe veux que vous alliez voir Andron pour obtenir cette information. Vous le trouverez à son magasin près de l\'Apothicarium.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1898;
UPDATE `locales_quest` SET `Title_loc2` = 'Les sœurs rieuses' ,`Details_loc2` = 'En remerciement de vos services, j\'ai pris des dispositions pour vous faire confectionner de nouvelles robes, à condition que vous alliez chercher les matériaux nécessaires... $B$BPour ces robes, vous devez vous rendre en Orneval et prélever des boucles de cheveux sur les sœurs rieuses, les dryades qui se réunissent près de la retraite de Raynebois.$B$BUne fois en possession de leurs cheveux, apportez-les à Kil\'hala, un tailleur troll qui se trouve à la Croisée, dans les Tarides. Il vous attend et coudra vos robes.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1945;
UPDATE `locales_quest` SET `Title_loc2` = 'Voyager vers le Marais' ,`Details_loc2` = 'Il est à présent temps pour vous d\'obtenir votre baguette de mage. Tout d\'abord, allez voir Tabetha, l\'ermite humaine du marécage d\'Âprefange. Sa cabane se trouve au fin fond du marais, à l\'ouest de Theramore.$B$BAdressez-vous à elle, car son savoir est immense. Vous trouverez sa cabane à l\'ouest de Theramore, et juste au nord des ruines Cognepierres.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1947;
UPDATE `locales_quest` SET `Title_loc2` = 'Retourner dans le Marais' ,`Details_loc2` = 'Il est temps pour vous de parler à l\'ermite Tabetha, $N. Elle vous donnera des indications sur la fabrication de l\'un des objets les plus importants pour les mages.$B$BLa cabane de Tabetha est loin dans le marécage d\'Âprefange, à l\'ouest de Theramore.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1953;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'énergie céleste' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 1958;
UPDATE `locales_quest` SET `Title_loc2` = 'Terres malades' ,`Details_loc2` = 'Il y a une maladie qui s\'étend à travers tout Sombrivage, contaminant les esprits et les organismes qu\'elle touche.$B$BCe sont les ours des chardons qui ont été les plus touchés. Ces animaux, autrefois si nobles, sont devenus des instruments de destruction, enragés et frénétiques. J\'ai peut-être trouvé un remède contre ce mal.$B$BApportez ce piège dans la forêt et placez-le au sol. N\'importe quel ours des chardons enragé qui traversera cette lumière deviendra docile pour un court moment. Une fois que l\'ours sera docile, il vous suivra. Ramenez-le ici, $N.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Our work has only just begun, &lt;name&gt;. Are you prepared for your next task?'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2118;
UPDATE `locales_quest` SET `Title_loc2` = 'Brouet de trotteur' ,`Details_loc2` = 'Sentez-vous cet arôme délicieux ? C\'est ma recette secrète, $N : le brouet de trotteur frais. Délicieux !$B$BLa recette vous intéresse peut-être ?$B$BAvant de vous l\'apprendre, je veux voir comment vous vous débrouillez avec les bêtes.$B$BJe ne m\'attends pas à ce que vous sortiez en dompter un, mais si vous pouvez en abattre quelques-uns et rapporter leur succulente viande, ça suffira à me convaincre que je peux vous montrer comment cuisiner mon brouet de trotteur.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Fantastic! Try it and tell me what you think, &lt;name&gt;!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2178;
UPDATE `locales_quest` SET `Title_loc2` = 'La chambre secrète' ,`Details_loc2` = 'Bon... Pour la tâche en cours...$B$BIl va falloir explorer une chambre secrète, la Chambre de Khaz\'mul. Le problème, c\'est qu\'elle se trouve derrière une lourde porte fermée ! Il est heureux que je sache comment l\'ouvrir ! $B$BJ\'ai tout écrit dans ce journal, là, sur la table. Pour l\'instant nous sommes coincés ici. Trouvez la Chambre de Khaz\'mul pour nous, $N !$B$BLisez mon journal, explorez la Chambre, puis rendez compte au Prospecteur Foudrepique de la Ligue des Explorateurs à Forgefer.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Vous avez vu la Chambre de Khaz\'mul ? Fascinant ! Baelog affirmait son existence, mais j\'avais des doutes... $B$BMerci, $N. Avec un peu de chance, cette découverte incitera nos chefs à renouveler leurs efforts pour reprendre Uldaman. Et si nous en reprenons le contrôle, les secrets de la Chambre de Khaz\'mul serons nôtres !'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2240;
UPDATE `locales_quest` SET `Title_loc2` = 'Erion Murmombre' ,`Details_loc2` = 'Ne vous inquiétez pas, $N, je ne vous envoie pas voir Syurna. Erion veut que vous le rejoigniez dans l\'Enclave de Cenarion. Faites vite !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2259;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'ordre d\'Erion' ,`Details_loc2` = 'Le Service de Renseignement de Hurlevent (SI:7) a entendu parler de vos exploits, $N, et il veut vous recruter. $B$BLe voyage est coûteux et périlleux, mais on ne rejette pas l\'opportunité de s\'entraîner pour le SI:7 !$B$BUn nouveau monde s\'ouvre à vous, $N. Rendez-vous dans la Vieille ville de Hurlevent et adressez-vous à Renzik "le Kriss" " au QG du SI:7.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2260;
UPDATE `locales_quest` SET `Title_loc2` = 'Réparer le Collier' ,`Details_loc2` = 'Nous avons du travail pour vous, si vous avez du cran...$B$BAu plus profond de l\'excavation d\'Uldaman, dans les Terres ingrates, il paraît qu\'il y a les restes d\'un crétin de paladin qui est mort par là il y a pas longtemps. Ça nous intéresse pas, sauf qu\'il avait un collier de prix avec lui. On dit qu\'il y avait trois Gemmes dessus : un Rubis, un Saphir et une Topaze.$B$BBon alors voilà : on le paiera très cher. Il est peut-être abîmé mais on s\'en moque. On le réparera.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Mmmm salvage! &nbsp;I can smell the necklace from here. &nbsp;Now let\'s look at that beauty...$B$BHey, what do you think you\'re trying to do here? &nbsp;This necklace is missing the gems! &nbsp;You think we wouldn\'t notice? &nbsp;Even Malton could see that the gems are gone.$B$BWell, maybe you didn\'t steal them... but this necklace is worthless without those gems! &nbsp;Your job isn\'t done here yet, not by a long shot!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2283;
UPDATE `locales_quest` SET `Title_loc2` = 'Cap sur Hulfdan !' ,`Details_loc2` = 'Où donc étiez-vous, $N ? Hulfdan vous cherche partout. On craignait que le roi ne vous ait fait mettre aux fers.$B$BAllez à Forgefer dès que possible et parlez à Hulfdan !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2299;
UPDATE `locales_quest` SET `Title_loc2` = 'La traduction du Journal' ,`Details_loc2` = 'Chez Droffers et fils, ils sont trop bêtes pour restaurer la magie du collier. Moi, je peux le faire. Je veux l\'étudier pour apprendre à le confectionner moi-même. Pour ce faire, j\'aurai besoin de votre collier. Laissez-le-moi et en échange je vous traduirai le journal. Le temps que vous trouviez les gemmes, j\'aurai le loisir d\'apprendre ce qui m\'intéresse.$B$BUn marché simple et sans duperie... Alors, qu\'en pensez-vous ?' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You know a good deal when you see one - I knew I could count on you.$B$BIt will be just a moment while I cast a spell to quickly translate the human tongue into a form you will understand. &nbsp;I am a troll of my word; ask anyone in the Horde about Jarkal Mossmeld. &nbsp;Now, let\'s get this started...'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2338;
UPDATE `locales_quest` SET `Title_loc2` = 'Trouver les gemmes et la source d\'énergie' ,`Details_loc2` = 'Voici la traduction du journal, pour que vous puissiez le lire de votre côté. En gros, il dit que ce sont les nains qui ont caché le rubis, les troggs la topaze et que c\'est un trogg nommé Grimelok qui a le saphir.$B$BBon. Ensuite, vous aurez besoin d\'une source d\'énergie pour que le collier fonctionne de nouveau. Et ça, Dran Droffers ne le sait pas. Pour en trouver une, moi, j\'irais au plus profond d\'Uldaman et je détruirais les machines les plus puissantes. Elles disposent sûrement d\'une source d\'énergie !$B$BQuand vous aurez tout ça, revenez me voir.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Very well done, &lt;class&gt;. &nbsp;Now the fruits of our redirection will pay themselves out handsomely. &nbsp;I\'m finished with the necklace itself, and I am confident I can duplicate the construction process consistently.$B$BBut first, I will use a specially prepared incantation to quickly analyze those gems before you take them and the necklace back to Orgrimmar. &nbsp;This will take just a brief moment to do.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2339;
UPDATE `locales_quest` SET `Title_loc2` = 'Réparer le collier, troisième prise' ,`Details_loc2` = 'Pendant que Dran et Malton Droffers se disputent la prise, la moitié de l\'affaire est maintenant close. Il vous reste à retourner dans les Terres ingrates pour finir tout ça. Jarkal Fondemousse, à Kargath, pourrait avoir une récompense spéciale à vous donner.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'The power source you gave me exceeded every expectation I had for it. &nbsp;I was able to make both you and me a necklace, and I have enough left over to make at least three more. &nbsp;They will fetch quite the fee, with Droffers and Son Salvage none the wiser.$B$BHere you go, my friend. &nbsp;I give you my thanks on a splendid job... and something quite special for your trouble.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2341;
UPDATE `locales_quest` SET `Title_loc2` = 'Cornes de Nez\'ra' ,`Details_loc2` = 'Il y a bien des lustres, les Cornes de Nez\'ra ont été volées au manoir de Ravenholdt. Je les ai cherchées par le monde entier, sur terre comme sur mer. Et tout ce temps, c’étaient ces bêtes immondes qui les avaient !$B$BExterminez tous les orcs rochenoires que vous croiserez jusqu\'à ce que vous retrouviez les Cornes de Nez\'ra.$B$BFaites ceci pour moi, $N, et votre renommée atteindra Ravenholdt.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'To deny Ravenholdt the artifact, would be to deny one\'s own existence. Well done, $Gclass&gt;. You may yet walk among the assassins of Ravenholdt.$B<br :>As a young &lt;class&gt; adventuring in Darkshore, I discovered a tea that replenishes energy in combat. It has served me well throughout the years and now I pass on a taste of the thistle tea to you. Drink of it when you tire, &lt;name, and fight with vigor once more!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2358;
UPDATE `locales_quest` SET `Title_loc2` = 'Réparer le Collier' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2361;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'aide d\'Hinott' ,`Details_loc2` = 'Je n\'ai jamais vu d’aussi merveilleux résultats pour un poison ! Zanzil, vous dites ? Jamais entendu parler de lui, mais je m\'incline devant le mélange qu\'il a créé.$B$BBon, vous voulez que je vous guérisse et que je vous apprenne ensuite à utiliser le poison ?$B$BLaissez-moi un peu de temps pour examiner la toxine, $N.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2480;
UPDATE `locales_quest` SET `Title_loc2` = 'Les réactifs des Terres ingrates II' ,`Details_loc2` = 'Vous m\'avez été des plus utiles, $N. Votre récompense sera la recette de la boisson que vous avez goûtée tout à l\'heure... Mais j\'ai une tâche très dangereuse pour vous.$B$BCette boîte fermée contient trois récipients thaumaturgiques vides ; ils sont imprégnés d\'un fluide magique qui drainera le sang d\'un dragon gardien des terres brûlées. Je vous préviens : les bêtes deviennent très agressives quand on les utilise sur elles. Une fois que vous aurez rempli les trois, apportez-les-moi.$B$BFaites cela, et la recette sera à vous !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'From one alchemist to another, I salute you. &nbsp;Here, take this recipe and commit it to your knowledge. &nbsp;May it bring you the success and rewards that it has brought me over the years!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2501;
UPDATE `locales_quest` SET `Title_loc2` = 'Le temple de la Lune' ,`Details_loc2` = 'Une créature de la forêt, autrefois l\'une des plus grandes, a besoin de votre aide, $N.$B$BC\'est une tâche difficile, mais qui doit être accomplie.$B$BS\'il vous plaît, allez voir la prêtresse A\'moora, elle vous expliquera de quoi il s\'agit. Elle se trouve au sud-est d\'ici, dans le temple de la Lune.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'So, Sister Aquinne sent you?'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2519;
UPDATE `locales_quest` SET `Title_loc2` = 'Les héros des Anciens' ,`Details_loc2` = 'Votre noblesse et votre héroïsme m\'ont honoré, $N.$B$BVous les avez honorés aussi…' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'En vérité, vous nous avez tous honorés. Nos remerciements ne sont pas suffisants.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2702;
UPDATE `locales_quest` SET `Title_loc2` = 'Kirith' ,`Details_loc2` = 'Un chapitre de vos aventures se termine, un autre commence, $N.$B$BL\'un de mes hommes manque toujours à l\'appel, il est perdu quelque part dans ces terres. Je parle du lieutenant Kirith, bien sûr.$B$BComme je l\'ai déjà dit, nous avons été tous deux capturés et torturés par Allistarj mais, pauvre Kirith, sa force a vacillé. Depuis ma cage, je pouvais entendre ce qu’il subissait, ses cris résonnaient dans toute la caverne. Je ne l’ai plus vu depuis.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Trebor? Trebor sent you?'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2721;
UPDATE `locales_quest` SET `Title_loc2` = 'Le Super-Oeuf-O-Matic' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You place the hippogryph egg into the machine. A series of beeps follows along with some strange mechanical sounds.$B$BIt appears that the Eggometer has weighed, measured, and packaged your egg. Now that\'s speedy service!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2741;
UPDATE `locales_quest` SET `Title_loc2` = 'Des objets d’une certaine importance' ,`Details_loc2` = 'Trias veut accélérer les choses, hein ? Bien, nous pouvons le faire, mais je ne suis pas prêt. Je vais faire la partie difficile du boulot, mais c\'est vous qui allez vous salir les mains, d’accord ? Bon.$B$BIl faut que vous m\'obteniez certaines choses pour mettre mon plan à exécution. J\'ai le déguisement parfait pour que l\'Espion passe ces gardes.$B$BApportez-moi de la soie et deux pommes. Pour la soie, débrouillez-vous, mais vous trouverez des pommes parfaites à la petite ferme de Clara à l\'extérieur de la ville.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'These will do nicely, &lt;name&gt;. Great job.$B$BI have the rest of the gear I\'ll need in my pack... thank the Light I just got a tailor friend of mine to make me a silk bag. I was running out of room carrying all my gear around.$B$BBefore Spybot heads in, let\'s get the details set so you know what you have to do.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2746;
UPDATE `locales_quest` SET `Title_loc2` = 'Les vieilles voies' ,`Details_loc2` = 'Aux temps légendaires, Aturk aurait écrasé les crânes de ses ennemis et réduit leurs os en poudre comme combustible de lampe. Ces jours sont loin, $N. Le chef de guerre appelle à la paix parmi les clans. Les vieux ennemis ne sont plus observés que par des yeux fatigués ! $B$BC\'est incroyable, je sais, mais telle est la volonté de Thrall.$B$BAssez ! Vous êtes ici pour apprendre et vous allez apprendre ! Apportez-moi quatre cuirasses en acier et quatre heaumes en plaques d\'acier et vous aurez un avant-goût de la guerre !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'It seems you are but another complacent lemming of the Warchief, &lt;name&gt;. How disappointing.$B$BHow I long for a glorious death upon the battlefield. Instead, I am stuck here, by order of Thrall, to teach infants how to craft! Watch and learn, child!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2756;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'ordre du Mithril' ,`Details_loc2` = 'La plupart de nos recrues ne vont jamais jusqu\'à Baie-du-Butin, $N. L\'éclat de l\'insigne est suffisant pour déclencher la convoitise, même chez le plus loyal des forgerons. Les imbéciles ! Cet insigne est une goutte d\'eau en comparaison de l\'océan des richesses que vous verrez !$B$BAllez trouver Galvan l\'Ancien et montrez-lui cette épingle. Il habite loin au nord, entre Zul Gurub et le tertre des ogres mosh\'Ogg.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Ah, many years it has been since one has been worthy of the Mithril Order\'s initiation. We will start now!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2760;
UPDATE `locales_quest` SET `Title_loc2` = 'Querelles mesquines' ,`Details_loc2` = 'Nous avons combattu le mauvais ennemi, $N.$B$BLa vraie puissance de cette région continue de croître alors que nous nous chamaillons avec les orcs sur cette terre aride.$B$BDevons-nous continuer à nous battre contre la Horde, pour finir écrasés un jour par les armées de la Légion ardente ? C\'est futile.$B$BMettez vos différences de côté, $N, parlez au Héros de la Horde tombé au combat. Il sait ce qui se passe dans les terres Foudroyées.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'The Alliance wishes to assist?$B$BI thought to never see this day again. A day in which the Alliance fights side by side with the Horde against a common enemy.$B$BIt shall be... we will unite.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2783;
UPDATE `locales_quest` SET `Title_loc2` = 'Le garde géant' ,`Details_loc2` = 'Je suis vraiment bon à rien, $N. J\'étais supposé surveiller mon amie Shay, et je l’ai perdue. Je ne suis pas sûr de ce qui s\'est passé, mais la dernière fois que je l\'ai vue, nous étions en train de marcher dans les ruines au nord d\'ici.$B$BTout allait bien, mais lorsque je me suis retourné vers elle... elle avait disparu.$B$BMaintenant je ne sais plus quoi faire. Vous avez une idée, $N ? Je suis affreusement inquiet.$B$BEt si vous alliez la chercher ? Vos jambes vous permettent d\'aller plus vite que moi.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Oh, I am so glad you found me! I must admit, I am a bit lost... But isn\'t this a beautiful place?'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2844;
UPDATE `locales_quest` SET `Title_loc2` = 'La tiare des abysses' ,`Details_loc2` = 'Il y a longtemps, je possédais une splendide pièce de joaillerie, la tiare des abysses. Elle n\'était pas seulement belle, elle détenait un grand pouvoir, pour ceux qui savaient l\'utiliser.$B$BQuand l\'hydromancienne Velratha a appris son existence, elle l’a convoitée. Elle a envoyé des agents chez moi pour la voler pendant mon absence. Voleurs !$B$BJe veux ma tiare ! Allez à Zul\'Farrak, la cité des trolls à Tanaris, trouvez Velratha et reprenez-lui la tiare. Rapportez-la et vous gagnerez mes faveurs.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Wonderful! &nbsp;You found it! &nbsp;And what\'s just as important--Velratha no longer has it! &nbsp;Thank you, &lt;name&gt;. &nbsp;I am forever in your debt!$B$BAnd if I sounded a little... mean before, pay it no mind. &nbsp;You\'ll find me a much nicer person to those who haven\'t stolen from me.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2846;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'armure en cuir fauve' ,`Details_loc2` = '$N, vous avez acquis une bonne connaissance du travail du cuir ! Mais je sais des choses qui pourraient vous intéresser… L\'armure en cuir fauve ! L’intérêt du cuir fauve tient aux propriétés chaotiques des sauvageonnes et de quelques autres créatures d\'ici. L\'armure que vous réaliserez sera ce qu’un artisan de votre compétence peut fabriquer de mieux.$B$BJe vous apprendrai à la réaliser si vous acceptez de travailler pour moi pendant un moment. Le coût initial est juste de dix pièces de cuir épais. Après nous parlerons technique.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2847;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'armure en cuir fauve' ,`Details_loc2` = '$N, la renommée de vos talents vous précède. Je connais quelque chose qui pourrait vous intéresser... l\'armure en cuir fauve ! Le cuir fauve tire ses propriétés des sauvageonnes que l\'on trouve dans la végétation luxuriante et sur quelques créatures. Cela ne devrait pas être compliqué pour quelqu\'un de votre niveau.$B$BJe vous enseignerai, mais en échange vous travaillerez pour moi pendant un moment. La première chose consiste à me rapporter dix pièces de cuir épais. Après nous discuterons technique.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Well &lt;name&gt;, I\'m glad to have you on board as my apprentice. &nbsp;You\'ve made a wise decision as a leatherworker; those who make use of leather armor will be clamoring to you once you learn how to master the art of Wild Leather.$B$BLet\'s get to work -  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  do you want to start?'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2854;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;TXT&gt; The Shriveled Heart' ,`Details_loc2` = 'Faites de ce mauvais garçon une tombe trolle dans le Temple englouti et récupérez quelque chose de sympathique !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2868;
UPDATE `locales_quest` SET `Title_loc2` = 'La fleur-de-chant corrompue' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You apply some Cenarion plant salve to the songflower. &nbsp;It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2878;
UPDATE `locales_quest` SET `Title_loc2` = 'Chasse au collier troll' ,`Details_loc2` = 'Comprenez bien ceci, $R. Nous ne faisons PAS partie de l\'Alliance ! Les Marteaux-hardis sont indépendants, ils prouvent leur valeur par l\'action et leurs actes et non en bavardant, contrairement à ceux qui parlent afin de camoufler leur passivité !$B$BSi vous cherchez des amis par ici, commencez par prouver votre valeur ! Ces idiots de trolls qui remplissent les Hinterlands portent parfois des colliers tribaux. Rapportez-en cinq et nous considérerons que vous avez fait un pas dans la bonne direction.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Now that\'s what I am talking about! &nbsp;See - these necklaces prove that you\'re willing to get your hands dirty. &nbsp;We Wildhammers value that in those we associate with.$B$BYou keep this up, and you\'ll be in our good graces yet.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2880;
UPDATE `locales_quest` SET `Title_loc2` = 'Chasse aux colliers trolls' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2881;
UPDATE `locales_quest` SET `Title_loc2` = 'Les cerveaux mécaniques' ,`Details_loc2` = 'Un composant pratiquement universel, le cerveau mécanique, est d\'une grande utilité pour les artisans et les forgerons de Gnomeregan. Mais durant notre exode, personne n\'a pensé à en emporter ! Et j\'en ai besoin d\'un grand nombre pour le travail que je vais bientôt accomplir.$B$BS\'il vous plaît, allez à Gnomeregan et rapportez-en. Vous les trouverez dans les profondeurs de notre cité, dans des conteneurs appelés des extrapolateurs artificiels.$B$BMerci, $N et dépêchez-vous !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'You got them! &nbsp;Now I can begin my new experiments!$B$BI can\'t thank you enough, &lt;name&gt;! &nbsp;Your bravery has advanced gnomish research by a leap and a bound!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2924;
UPDATE `locales_quest` SET `Title_loc2` = 'Du travail pour Fontuyau' ,`Details_loc2` = 'Le maître-mécanicien Fontuyau à Forgefer a besoin d\'aventuriers ! Il organise des groupes pour pénétrer à nouveau dans Gnomeregan afin d\'effectuer une mission de récupération de première importance.$B$BIl se peut que Fontuyau ne soit pas membre des grandes Assemblées d\'arcanologie, d\'alchimie ou de sciences d\'ingénieries comme je le suis, mais il reste un gnome intelligent. S\'il considère que c\'est important, alors mes calculs indiquent qu\'il y a quatre-vingts pour cent de chances que ce soit véritablement important !$B$BIl s\'agit là des probabilités les plus optimistes en ce qui nous concerne !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'I\'m organizing data extraction missions into Gnomeregan. &nbsp;If you\'re here for that, then listen up!'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2931;
UPDATE `locales_quest` SET `Title_loc2` = 'Sinistre message' ,`Details_loc2` = 'Les trolls fanécorces étaient jadis des alliés de la Horde, mais ils ont trahi Thrall... ce qui ne peut être toléré !$B$BVoici, prenez ma pique et allez dans les Hinterlands, loin au nord. Vous trouverez l\'endroit en question au nord-est du Moulin-de-Tarren, un village tenu par nos alliés, les Réprouvés.$B$BQuand vous entrerez sur les terres des Hinterlands, chassez les trolls fanécorces. Collectez leurs crânes, puis plantez ma pique dans le sol de l\'un de leurs petits villages, Hiri\'watha ou Zun\'watha.$B$BMontrez-leur que tout se paie en ce bas monde !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Word reached me of your deed. &nbsp;A grim deed, but one of honor. &nbsp;Let us hope the Witherbarks heed your message.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2932;
UPDATE `locales_quest` SET `Title_loc2` = 'En savoir plus' ,`Details_loc2` = 'Bien que les ruines de Féralas puissent être très dangereuses, elles ont beaucoup à raconter sur ce qui s\'est passé ici.$B$BIl y a quelques jours, tandis que je fouillais les décombres, j\'ai découvert ce qui semble être une douve classique. Je ne peux cependant pas me défaire de l\'idée que cela cache quelque chose... Angelas et moi avons fait des recherches dans nos livres, mais nous n’avons rien découvert la concernant.$B$BJe connais une collègue à Darnassus qui pourrait peut-être nous dire de quoi il s\'agit, $N. Pourquoi n\'iriez-vous pas lui parler et voir si elle peut nous aider ?' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Ainsi, c\'est Troyas Lunebise qui vous envoie ?$B$B&lt;Marmonnements&gt;… n\'arrête pas de me harceler, comme quoi il aurait découvert quelque chose d\'important…$B$BÉcoutez, $N, je vais être franche. Troyas croit toujours avoir découvert quelque chose d\'important dans ces ruines. Mais tout ce qui avait une véritable valeur a été ravagé depuis longtemps par les ogres et les harpies qui y vivent à présent.$B$BVous pouvez faire toutes les recherches que vous voulez ici, mais je doute que vous trouverez un livre pouvant vous en apprendre davantage.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2939;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;INUTILISÉ&gt;' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2971;
UPDATE `locales_quest` SET `Title_loc2` = 'Les ogres de Féralas' ,`Details_loc2` = 'La tribu des ogres gordunni ne peut pas passer inaperçue, dans cette région. Regardez autour de vous : le pays est comme blessé par leur simple présence, $N. Nous devons les repousser avant qu\'ils ne se répandent plus avant en Féralas.$B$BSuivez la rivière au nord, vous tomberez sur leur avant-poste. Portez la mort et la désolation parmi eux, $N. Nous devons leur montrer à quel point nous les trouvons indésirables.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Excellent, &lt;name&gt;. In time, the land will heal. We must not give up.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2975;
UPDATE `locales_quest` SET `Title_loc2` = 'Le médaillon de Nekrum' ,`Details_loc2` = 'Si vous voulez que je localise votre griffon, vous devrez faire quelque chose pour moi. Il y a un troll dans Zul\'Farrak, Nekrum Mâchentrailles, qui possède un médaillon que j\'aimerais beaucoup étudier. Apportez-le-moi et j\'utiliserai mes pouvoirs pour retrouver Bec-tranchant.$B$BZul\'Farrak est de l\'autre côté de l\'océan, à l\'est, dans le désert de Tanaris.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Ah, the medallion of Nekrum. &nbsp;It is rumored to have given the troll an unnatural long life, but at a cost. &nbsp;I hope to discover a way to reap the medallion\'s power while avoiding its... unpleasant side effects.$B$BYou have done me a service, &lt;name&gt;. &nbsp;I shall now uphold my end of our bargain.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2991;
UPDATE `locales_quest` SET `Title_loc2` = 'À la recherche de Strahad' ,`Details_loc2` = 'Je vous ai beaucoup appris et maintenant je dois passer la main à un autre professeur. Il s\'appelle Strahad et habite à Cabestan. C\'est un humain, mais que cela ne vous incline pas à croire qu\'il est faible. Il est de petite taille, c\'est vrai, mais très puissant et pourra vous apprendre des invocations beaucoup plus importantes. Allez le voir maintenant. Vous le trouverez à Cabestan... à côté de la tour qui surplombe le port.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2996;
UPDATE `locales_quest` SET `Title_loc2` = 'Le Tome de divinité' ,`Details_loc2` = 'Bonjour, $N. Je sais que vous êtes très $Goccupé:occupée à aider les gens de Dun Morogh, mais si vous avez le temps, allez jusqu\'à Forgefer pour parler à la vénérable Tiza Forgeguerre. Les plus jeunes de nos paladins vont la voir lorsqu\'ils veulent en apprendre plus sur la Lumière et sur leurs obligations.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 2999;
UPDATE `locales_quest` SET `Title_loc2` = 'Le Tome de divinité' ,`Details_loc2` = 'Bonjour, $N. Je sais que vous êtes très $Goccupé:occupée à aider les gens d\'Azeroth, mais si vous avez le temps, retournez à Forgefer et parlez à l\'une de nos paladins les plus respectés, Tiza Forgeguerre. Nos jeunes paladins nains vont la consulter quand ils veulent en apprendre plus au sujet de la Lumière et sur ce que l\'on attend d\'eux.$B$BVous la trouverez à l\'étage du Hall des Mystères qui se trouve dans la Garde mystique.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3000;
UPDATE `locales_quest` SET `Title_loc2` = 'À manipuler avec soin' ,`Details_loc2` = '$N, je vois que vous aspirez à embrasser ma louable cause. Bien entendu, vous pouvez faire quelque chose pour moi !$B$BApportez cette caisse d\'œufs à Erelas Ambreciel. Ce druide les met en couveuse puis entraîne les jeunes hippogriffes. Vous le trouverez dans Teldrassil, dans un petit village appelé Rut\'theran.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Thank you for your help, $Gname&gt;. The hippogryph population simply wouldn\'t flourish if it weren\'t for us. Ah, but I am sure you understand all too well, having traveled to Feralas yourself, and seen the devastation of the Gordunni there.$B$BWell, as you can see, I am hard at work here, so you\'d best be on your way.$B<br :>Thanks again, &lt;name.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3022;
UPDATE `locales_quest` SET `Title_loc2` = 'Se venger des Ruissenord' ,`Details_loc2` = 'Jadis, j\'étais un chasseur de renom et ne craignais aucune espèce de créature. Jusqu\'à ce que...$B$BUn jour que j\'aiguisais mon épée dans la forêt, après une rude bataille, des harpies m\'ont encerclé et attaqué. Je ne sais comment je réussis à en sortir vivant.$B$BEncore maintenant, je peux entendre leurs affreux cris de crécelle...$B$BDepuis cette embuscade, je ne suis plus le même tauren ; j\'ai peur et mon âme est emplie de haine.$B$BLes harpies ruissenord vivent à l\'ouest. $N, soyez le bras armé de ma colère, vengez-moi !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Thank you, &lt;name&gt;. You have put an old tauren\'s mind at peace.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3063;
UPDATE `locales_quest` SET `Title_loc2` = 'Muisek d\'hippogriffe' ,`Details_loc2` = 'Puisque vous avez montré que vous êtes à la fois digne de confiance et $Gdoué:douée pour utiliser le récipient à muisek, je souhaite à présent que vous capturiez une autre créature.$B$BLes hippogriffes ont été les compagnons loyaux des elfes de la nuit durant plusieurs années. Leur esprit est résolu et ferme.$B$BVotre tâche consiste à tuer 10 hippogriffes Aigreplume et de rapidement les faire rétrécir et les capturer avec le récipient, afin que leur muisek soit préservé. Vous trouverez les hippogriffes dans les plateaux Sauvages au sud d\'ici.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'After killing an animal, if we allowed the muisek to escape, it may take the form of an even greater creature. We would never be safe from its vengeance.$B$BThis is a dangerous job I have given you, &lt;name&gt;. Again, you have completed it well.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3124;
UPDATE `locales_quest` SET `Title_loc2` = 'Muisek de dragon féerique' ,`Details_loc2` = 'À présent, $N, je souhaite que vous capturiez une créature fragile en apparence, mais dont l\'esprit puissant nous intéresse. Je veux parler des dragons féeriques. Ils ont été les alliés des elfes de la nuit. Nous devons en capturer afin que la Horde puisse également bénéficier de cette force spirituelle.$B$BVoici le récipient, $N. Tuez 8 Fées fléchetteuses ou Petits dragons féeriques, et capturez leur muisek. On peut en trouver à l\'ouest d\'ici.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Thank you, &lt;name&gt;. Now the spiritual powers of the faerie dragon will be available to us.$B$BI hope you willing to perform another task for me.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3125;
UPDATE `locales_quest` SET `Title_loc2` = 'Armes de l\'Esprit' ,`Details_loc2` = 'Imprégner les armes de muisek est un processus difficile. Maintenant que nous avons les matériaux nécessaires et le muisek des créatures que vous avez capturées, je peux commencer. Attendez s\'il vous plaît ; je reviens dans un moment.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'As promised, you have your choice, &lt;name&gt;. The muisek of the creatures has been forever sealed into the power of these weapons.$B$BYou may now wield their energy as your own. Use it well.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3129;
UPDATE `locales_quest` SET `Title_loc2` = 'La gahz\'ridienne' ,`Details_loc2` = '$N, je suis un scientifique mais au fond de moi, je suis aussi un chasseur de trésor !$B$BIl y a longtemps, quand les trolls occupaient ces terres, ils ont créé de nombreux ornements à l\'image de Gahz\'rilla, une hydre qu\'ils adorent comme divinité. Ces ornements sont sculptés à partir d\'une matière que j\'appelle la « gahz\'ridienne », d\'après le nom du dieu hydre.$B$BJ\'en ai déjà trouvé un peu moi-même, mais j\'aimerais en avoir plus à étudier ; peut-être pourrez-vous m\'en trouver ?$B$BJ\'ai même inventé un détecteur qui fait de la recherche de gahz\'ridienne un jeu d\'enfant ! Vous n\'avez qu\'à le porter et la recherche peut commencer !' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Perfect; these ornaments are perfect for my studies. Thanks so much for the help, &lt;name&gt;.$B$BOh, can I get my helmet back?'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3161;
UPDATE `locales_quest` SET `Title_loc2` = 'Enfin !' ,`Details_loc2` = 'Je n\'arrive pas à croire que Minigravillon ait pu douter de l\'authenticité de la corne. Entre vous et moi, je pense qu\'elle est seulement jalouse. Je sais qu\'elle tenait vraiment à tuer elle-même cette bête, à cause de « l\'incident » et tout.$B$BDonc vous avez besoin d\'un document comme preuve ? Après la contribution que vous venez de faire au musée, ce ne sera pas un problème.$B$B&lt;Le conservateur en chef Thorius commence à remplir un grand document&gt;$B$BEt voilà ! Vous pouvez présenter ceci à Minigravillon.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'I don\'t know how someone as weak as you overcame Margol, but a deal is a deal. Here is the key.$B$BIf anything happens to you after this, it will be your own fault.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3201;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;NYI&gt; &lt;TXT&gt;&lt;redux&gt; Dreadmist Peak' ,`Details_loc2` = 'Récupérez les totems du Pic de Brume-funeste, car il est plutôt poisseux en ce moment et ce n\'est pas un endroit convenable pour des totems tout propres.' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3241;
UPDATE `locales_quest` SET `Title_loc2` = 'La vallée des Chardonniers' ,`Details_loc2` = 'Avez-vous déjà vu la vallée des Chardonniers ? C’est un endroit dangereux, $N.$B$BIl y a d’étranges bêtes là-bas, imprévisibles ! Je crois qu’on les appelle les chardonniers noueux et les chardonniers taille-racine...$B$BJe veux accéder à la vallée, mais avec tous ces chardonniers dans les parages c’est bien trop risqué. Si vous vouliez bien aller en tuer quelques-uns, je pourrais envisager d’y retourner. Qu’en pensez-vous, $N ? M’aiderez-vous ?' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'They\'re that big? I might have to rethink my plan...$B$BBut you did hold up your end of the bargain, so here\'s what I promised.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3362;
UPDATE `locales_quest` SET `Title_loc2` = 'L\'Éclat luminescent' ,`Details_loc2` = 'L\'éclat est d\'un vert brillant, et bien que compact, ce qui ressemble à de la fumée tourbillonne juste au-dessous de sa surface. Vous ne pouvez vous empêcher de contempler les profondeurs du cristal, bien qu\'en même temps, vous sentiez qu\'il y réside quelque chose de très inquiétant.$B$BPeut-être devriez-vous voir si quelqu\'un à Cabestan pourrait vous en apprendre plus à propos de cet étrange éclat...' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3366;
UPDATE `locales_quest` SET `Title_loc2` = 'La justice des nains' ,`Details_loc2` = 'Il y a, à présent, un creux vide de sens, là où se trouvait autrefois mon cœur, $N. Je ne me remettrai pas de la perte de Dorius. Je dois faire en sorte que sa dernière volonté soit accomplie. Justice doit être rendue contre ceux qui ont commis ce meurtre de sang-froid.$B$BRetournez à la Gorge des Vents brûlants et respectez sa dernière volonté : libérez ses hommes. Les nains sombrefers forcent leurs esclaves à travailler dans une mine étouffante appelée la Fosse aux scories. Allez chercher les hommes de Dorius là-bas.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>' ,`OfferRewardText_loc2` = 'Forget about me, help the others.'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3371;
UPDATE `locales_quest` SET `Title_loc2` = 'Une rencontre avec le maître' ,`Details_loc2` = 'Ah ! &lt;Un/Une&gt; jeune vient parler à Sanath. Quelle journée merveilleuse ! Quel plaisir que vous m\'honoriez de la grâce de votre présence. Merci, $R !$B$BS\'il vous plaît, n\'ayez pas l\'arrogance de considérer mon sarcasme comme un compliment. Que les choses soient claires : je ne vous apprécie pas ! Mais cette petite conversation n\'a strictement rien à voir avec nous. Elle a à voir avec mon maître.$B$BApportez-moi une paire de plumes d\'hippogriffe pour mes flèches et je vous octroierai l’autorisation d\'aller le voir. Il a expressément demandé que je ne lui envoie &lt;d\'aventuriers que s\'ils/d\'aventurières que si elles s\'en montrent suffisamment dignes.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3381;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;INUTILISÉ&gt;' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3383;
