UPDATE `locales_quest` SET `Title_loc2` = '&lt;INUTILISÉ&gt;' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3422;
UPDATE `locales_quest` SET `Title_loc2` = '&lt;INUTILISÉ&gt;' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 3423;
