UPDATE `locales_quest` SET `Title_loc2` = 'Une surprise vous attend...' ,`Details_loc2` = 'Une surprise vous attend à l\'intérieur des Mortemines.$B$BLa prédiction que Sayge vous a donnée vous semble un peu chaude au toucher. Alors que vous la serrez dans votre main, vous avez une vision des Mortemines, situées dans le hameau en ruine de Ruisselune, dans la marche de l\'Ouest. En vous concentrant sur cette image, vous vous voyez emprunter des passages tortueux jusqu\'à ce que vous atteigniez un portail étincelant... qui conduit au cœur même des mines. Lorsque vous le traversez, un étrange coffre apparaît de nulle part.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 7938;
UPDATE `locales_quest` SET `Title_loc2` = 'Gwedins de lapins !' ,`Details_loc2` = 'Ces lapins se reproduisent comme… comme des lapins ! Il faut faire quelque chose !$B$BAu secours $N, vous êtes notre seul espoir.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 7961;
