UPDATE `locales_quest` SET `Title_loc2` = 'Le charme du chasseur' ,`Details_loc2` = 'Ogtinc, d\'Azshara, recherche des chasseurs aguerris comme vous. C\'est un chasseur furbolg qui a quitté sa tribu pour aider le Cercle cénarien. Il enseigne sa sagesse à ceux qui partagent son art, et les guide dans les voies de l\'équilibre. Vous devriez lui parler.$B$BOgtinc réside au sommet des falaises d\'Azshara, au nord-est des ruines d’Eldarath.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 8151;
