UPDATE `locales_quest` SET `Title_loc2` = 'La maîtrise des éléments' ,`Details_loc2` = 'Bath’rah la Vigie des vents a demandé qu’un chaman aussi sage que vous vienne le voir. Il demande que vous lui apportiez un échantillon de chacun des éléments, pour lui montrer que vous êtes $Gprêt:prête.$B$BPour trouver Bath’rah, allez jusqu’à la rivière à l’est de Moulin-de-Tarren. Suivez la rivière vers le nord, jusqu’à ce que vous arriviez aux ruines où vit Bath’rah.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 8410;
UPDATE `locales_quest` SET `Title_loc2` = 'Pierres du Fléau inertes' ,`Details_loc2` = 'Le mal a été purgé des pierres, $N.$B$BJe connais les plans du commandant Ashlam, et il n’a besoin que d’une seule pierre inerte. Apportez-la-lui pour que nous puissions plonger le Fléau dans le feu justicier.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 8416;
UPDATE `locales_quest` SET `Title_loc2` = 'Forger la pierre de pouvoir' ,`Details_loc2` = 'Nous devons à présent combiner les pierres du Fléau inertes et créer une arme capable de briser les rangs des morts-vivants. Ce ne sera guère une tâche facile, $N. Seuls les trolls du temple englouti détiennent la magie rare qui permet de retourner le mal contenu en une chose contre le mal lui-même.$B$BÀ l\'intérieur du temple, les trolls sont connus sous les noms de Balafreur, Mijan, Zolo, Hukku, Zul\'lor et Loro. N\'éprouvez pas de remords envers leurs âmes corrompues... Ce sont des êtres malfaisants.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 8418;
UPDATE `locales_quest` SET `Title_loc2` = 'La demande d\'un diablotin' ,`Details_loc2` = '$N, un diablotin de Gangrebois nommé Démonet a exigé votre présence. Sa requête est assez spéciale, car il a demandé que vous lui apportiez un morceau de gangrétoffe en gage de « bonne volonté ». Vous êtes un démoniste déjà très puissant et je suppose que vous savez que les satyres de Gangrebois portent de cette étoffe.$B$BVous pourrez trouver Démonet juste au nord des chutes de la Vénéneuse qui, d’après ce que j’ai entendu dire, sont assez belles en cette période de l’année.' ,`Objectives_loc2` = '<b style="color: red">Cette quête est marquée comme obsolète par Blizzard et ne peut être obtenue ou accomplie.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 8419;
UPDATE `locales_quest` SET `Title_loc2` = 'La bataille du goulet des Chanteguerres' ,`Details_loc2` = 'La bataille dans le goulet des Chanteguerres contre les Sentinelles d’Aile-argent est d’une grande importance. Sous le prétexte de protéger une forêt qui ne lui appartient pas, l’Alliance cherche à refuser à la Horde l’une de ses plus grandes ressources en bois.$B$BNe la laissez pas faire, $N ! Revenez me voir quand vous aurez la preuve que vous avez bien servi la Horde !' ,`Objectives_loc2` = '<b style="color: red">Cette quête n\'est plus disponible dans le jeu.</b><div class="pad"></div>'  WHERE (`title_loc2` ='' or ISNULL(`title_loc2`))  and  `entry` = 8427;
